css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("atmarkit.co.jp") {
#masterBodyOut {
    width: 100%;
}

#masterMain {
    min-width: 637px;
    width: calc(100% - 350px);
    /* width: -moz-calc(100% - 400px); */
}

#masterMainRight {
    min-width: 420px;
    width: calc(100% - 220px);
    /* width: -moz-calc(100% - 220px); */
}

#masterBody {
    /* width: 990px;
        background: #FFF;*/
    margin-right: 0px;
    margin-left: 90px;
    /* print option */
    width: calc(100% - 90px);
    min-width: 1000px;
}
html#masterChannel-ait div.GTLeftBox div.colBoxIndex.GTLeft{
    min-width: 320px;
    width: calc(50% - 20px);
}
html#masterChannel-ait div.GTRightBox{
    min-width: 280px;
    width: calc(50% - 20px);
}
html#masterChannel-mn div#masterContents{
    min-width: 990px;
    width: 100%;
}
html#masterChannel-mn div.colBoxInner.colBoxTopStoriesLeft{
    min-width: 320px;
    width: calc(50% - 20px);
}
html#masterChannel-mn div.colBoxTopStoriesTitleBox div.colBoxTitle{
    clear: both;
}
html#masterChannel-mn div#masterMain div#colBoxTopPickUp.colBox.colBoxTopPickUp div.colBoxOuter div.colBoxInner div.colBoxIndex{
    min-width: 220px;
    width: calc(33% - 20px);
}
html#masterChannel-mn div.colBox.colBoxTopCategory{
    min-width: 320px;
    width: calc(50% - 20px);
}
html#masterChannel-mn div.colBox.colBoxTopCategory div.colBoxIndex.colBoxTopCategoryItem{
    min-width: 140px;
    width: calc(50% - 20px);
}
html#masterChannel-mn div.colBox.colBoxTopCategory div.colBoxIndex.colBoxTopCategoryItem div.colBoxTitle{
    clear: both;
}
}
