css:
@-moz-document domain("fogbugz.com") {
:root {
	--unused: #303e4c;
	--dark-text-colour: #a09e9e;
	--light-text-colour: #aab0b7;
	--main-body-color: #3e4b58;
    --divider-colour: rgba(64, 88, 111, 0.36);
	--divider: 1px solid rgba(64, 88, 111, 0.36);
	--text-selection-dropdown-border: 1px solid rgba(64, 88, 111, 1);
	--dark-highlight-color: #2b3a48;
    --remove-shadow: 0 0px 0 rgba(255,255,255,0);;
    --remove-border: 0px solid;
    --link-color-1: #2b041c;
    --link-color-2: #39668e;
    --light-link-colour: #588ebf;
    --selected-case: #48535f;
    --grid-background-color: #364552;
}


/* General */

#global-drop-target {
	background-color: var(--grid-background-color)
}

a.visited {
    color: var(--light-link-colour);
}

a:visited {
    color: var(--light-link-colour);
}

a {
    color: var(--light-link-colour); 
}

#stacking-context-main>#main {
	/*This is the main text in the page*/
    color: var(--light-text-colour);

    /*Main BG colour*/
    background-color: var(--grid-background-color);
}


/* Top navigation bar */

#header.thunder-header .main-header-bar {
    background-color: var(--dark-highlight-color);
    border-bottom: 1px solid var(--dark-highlight-color);
    border-bottom-color: var(--dark-highlight-color);
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.1);
    color: var(--dark-highlight-color);
}
#header.thunder-header .search-guide .search-box {
    background-color: rgba(43, 58, 72, 0.91);
}
#header.thunder-header .button {
    background: var(--main-body-color);
    border-color: var(--dark-highlight-color);
    background-color: var(--main-body-color);
}
#header.thunder-header a {
    color: var(--light-text-colour);
}

#header.thunder-header .cases-button .link-list-cases {
    border-left-color: var(--dark-highlight-color);
}

#header.thunder-header .planner-button .link-list-planner {
    border-left-color: var(--dark-highlight-color);
    box-shadow: none;
}

#header.thunder-header .dropdown-menu {
    background: var(--dark-highlight-color);
    border: 1px solid var(--dark-highlight-color);
}

#header.thunder-header .dropdown-menu {
    border-color: var(--dark-highlight-color);
}

#header.thunder-header .dropdown-menu a {
    color: var(--light-text-colour);
}

#header.thunder-header .dropdown.active .section-link {
    background: var(--main-body-color);
    border-left-color: var(--dark-highlight-color);
    border-right-color: var(--dark-highlight-color);
    -moz-transition: none;
    -webkit-transition: none;
    -o-transition: none;
    transition: none;
    z-index: 2; /* or -1 */
}

#header.thunder-header .cases-button .link-list-cases .section-link {
    border-top-right-radius: 5px;
    height: 30px;
    left: -1px;
    padding: 5px 15px;
    z-index: 2;
}

#header.thunder-header .dropdown.active:not(.no-menu-content):after {
    content: "";
    display: block;
    height: 15px;
    position: absolute;
    z-index: 2;
    bottom: -1px;
    right: -1px;
    left: 1px;
    opacity: 1;
    background-color: rgba(54,69,82,0.2);
    background-image: -webkit-linear-gradient(top,rgba(255,255,255,0),var(--dark-highlight-color));
    background-image: -moz-linear-gradient(top,rgba(255,255,255,0),var(--dark-highlight-color));
    background-image: -ms-linear-gradient(top,rgba(255,255,255,0),var(--dark-highlight-color));
    background-image: -o-linear-gradient(top,rgba(255,255,255,0),var(--dark-highlight-color));
    background-image: linear-gradient(top,rgba(255,255,255,0),var(--dark-highlight-color));
}

#header.thunder-header .search-guide #search-guide-button {
    background-color: var(--main-body-color);
    border: var(--divider);
}

#header.thunder-header .search input {
    border: var(--divider);
    border-radius: 0px;
}

#header.thunder-header .search input:hover {
    border-color: rgba(64, 88, 111, 0.36);
}

#header.thunder-header .secondary-nav .menus {
    background-color: var(--dark-highlight-color);
    border-bottom: var(--divider);
    border-bottom-color: var(--divider-colour);
}

#header #below-banner #below-nav {
    background-color: var(--dark-highlight-color);
    border-bottom-left-radius: 0px;
    background-color: var(--dark-highlight-color);
    border: var(--divider);
    border-bottom-left-radius: 0px;
}

#header.thunder-header .avatar img {
    border-left: none;
    border-right: none;
}
#header.thunder-header .avatar.section-link {
    border-right: var(--divider);
}


#header.thunder-header .cases-button .cases-link:hover {
    box-shadow: none;
    background-color: var(--main-body-color);
}

#header.thunder-header .planner-button .planner-link:hover {
    box-shadow: none;
    background-color: var(--main-body-color);
}

#header.thunder-header .cases-button .cases-link {
    border-top-left-radius: 0px;
    border-bottom-left-radius: 0px;
}

#header.thunder-header .planner-button .planner-link {
    border-top-left-radius: 0px;
    border-bottom-left-radius: 0px;
}

/* Main buglist */
#filter-bar {
    border-color: var(--dark-highlight-color);
    background: var(--dark-highlight-color);
}

#list-group-body {
	background-color: var(--dark-highlight-color);
}

.case-list {
    background-color: var(--dark-highlight-color);
}
.case-list tr.bug-grid-row {
    border-color: var(--grid-background-color);
    background-color: var(--grid-background-color);
}

.case-list tr.bug-grid-row.alt {
    border-color: var(--grid-background-color);
    background-color: var(--grid-background-color);
}

.case-list th {
	background: var(--dark-highlight-color);
    border: 1px solid var(--dark-highlight-color);
}

.case-list .bug-grid {
    border-bottom-color: var(--dark-highlight-color);
}

.case-list th .header-sort-toggle, .case-list .grid-column-header-drag-helper .header-sort-toggle {
    color: var(--light-link-colour);
}

#filter-bar #filter-description .filter-description-sort-element .filter-sort-clickable {
    color: var(--light-link-colour);
}

#filter-bar #filter-description .filter-axis-clickable {
    color: var(--light-link-colour);
}

.case-list .list-group-footer>.list-add-case {
    color: var(--light-link-colour);
} 

.action-link {
    color: var(--light-link-colour);
}


table.mini-report td {
    border-bottom-color: var(--divider-colour);
}
table.mini-report th, table.mini-report td {
    border-bottom: var(--divider);
}

table.mini-report {
    border-color: var(--divider-colour);
    color: var(--light-text-colour);
    background-color: var(--dark-highlight-color);
}

tbody {
    border-color: var(--divider-colour);
    background-color: var(--dark-highlight-color);
}
/*Inherited from table#case-list-mini-report.mini-report*/

table.mini-report th {
    border-bottom: var(--divider-colour);
    background-color: var(--dark-highlight-color);
}
table.mini-report tr:first-child {
    background: var(--dark-highlight-color);
}

.action-bar>.action-button {
    border: var(--divider);
    background: var(--dark-highlight-color);
    color: var(--light-text-colour);
}

.case-list tr.bug-grid-row.selected {
    background-color: var(--selected-case); 
}
.case-list tr.bug-grid-row.alt.selected {
    background-color: var(--selected-case);
}

.action-bar>.action-button:last-child {
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
    border: var(--divider); 
}

.popup {
    background-color: var(--main-body-color);
    border: var(--divider);
}
.filterbar-choices-root .list-choices-item, .filterbar-refine-further-popup .list-choices-item, .filterbar-more-popup .list-choices-item, .filterbar-view-popup .list-choices-item {
    color: var(--light-text-colour);
}


.list-choices-item:hover {
    background: var(--selected-case);
}

.list-choices-section:before {
    border-top: var(--divider);
}



/* Case view */
.event .body {
    padding: 10px;
    border-top: var(--divider);
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    font-size: 14px;
    line-height: 1.4;
    background-color: var(--main-body-color);
    color: var(--light-text-colour);
}


section.case>article {
    background-color: var(--dark-highlight-color);
    border-color: var(--dark-highlight-color);
}
section.case>article {
    border: 1px solid var(--dark-highlight-color);
    background-color: var(--dark-highlight-color);
}

section.case .left label {
    color: var(--light-text-colour);
}

section.case .top {
    background-color: var(--dark-highlight-color);
    border-bottom: 1px solid var(--dark-highlight-color);
    border-radius: 15px;
}

section.case nav {
    background: var(--dark-highlight-color);
}

section.case nav:first-child, section.case nav:last-child {
    border-color: var(--dark-highlight-color);
}

.event {
    border: var(--divider);
    background-color: rgba(0,0,0,0);
}

.event header {
	color: var(--light-text-colour);
	background-color: var(--dark-highlight-color);
    border: var(--divider);
}

section.case .top>header {
    border-left: var(--divider);
}

section.case .top>header .case-header-info {
    border-top: var(--divider);
}

.event .body {
    border: 0px solid rgba(64, 88, 111, 0.36);
}

section.case .corner a.case {
    color: var(--light-link-colour);
}

section.case .top>header>h1 {
    color: var(--light-link-colour);
}

section.case nav .controls>.control {
    color: var(--light-text-colour);
}

section.case .top>header>.case-header-info {
    color: var(--light-text-colour);
}

.bodycontent p {
    background-color: var(--main-body-color) !important;
    color: var(--light-text-colour) !important;
}
.bodycontent div {
    background-color: var(--main-body-color) !important;
    color: var(--light-text-colour) !important;
}

.bodycontent ul {
    background-color: var(--main-body-color) !important;
    color: var(--light-text-colour) !important;
}
.bodycontent ol {
    background-color: var(--main-body-color) !important;
    color: var(--light-text-colour) !important;
}
.bodycontent li {
    background-color: var(--main-body-color) !important;
    color: var(--light-text-colour) !important;
}
.bodycontent span {
    background-color: var(--main-body-color) !important;
    color: var(--light-text-colour) !important;
}



/* Editing Cases */
.event .editor textarea, .event .editor .cke_contents {
    border: var(--divider);
    border-top: none;
    border-bottom: none;
    background-color: var(--main-body-color);
    color: var(--light-text-colour);
}

.event .buttons {
    border-top: var(--divider);
}

.event .editor-toggle {
    border-bottom: var(--divider);
    border-top: var(--divider);
}

.chunky-droplist .droplist-chunks {
    border: var(--divider);
    border-radius: 0px;
    box-shadow: inset 0 0px 0px rgba(0,0,0,0.15);
    font-size: 13px;
    line-height: 13px;
    padding: 2px 5px;
    background-color: var(--main-body-color);
    color: var(--light-text-colour)
}

.chunky-droplist .droplist-input {
    display: inline-block;
    border: var(--text-selection-dropdown-border);
    outline: none;
    padding: 2px 0px 2px 1px;
    margin: 1px;
    font-size: 13px;
    background-color: var(--main-body-color);
    color: var(--light-text-colour);
}

section.case input, section.case #sTitle {
    border-radius: 2px;
    border: var(--divider);
    box-shadow: inset 0 0px 0px rgba(0,0,0,0.15);
    color: var(--light-text-colour);
    font-size: 13px;
    height: auto;
    padding: 5px;
    background-color: var(--main-body-color);
}

.select-droplist .droplist-input {
    background: var(--main-body-color);
    border: var(--divider);
    box-shadow: inset 0 0px 0px rgba(0,0,0,0.15);
    color: var(--light-text-colour);
}

.event .buttons>button {
    background: var(--main-body-color);
    border: var(--divider);
    color: var(--light-text-colour);
    box-shadow: var(--remove-shadow);
    border: var(--remove-border);
}


section.case #formEditCase .top>header {
    border-left: var(--divider);
}


.select-droplist .select-droplist-button {
    background-color: var(--dark-highlight-color);
    border: var(--divider);
}

.event .buttons>button:hover {
    color: var(--link-color-2);
    border-color: var(--divider);
}


#header.thunder-header .search-guide .panel .suggestions {
    padding: 5px 0px;
    background-color: var(--main-body-color);
    border-color: var(--divider);
    color: var(--light-text-colour);
}


/* Wiki stuff */

.wiki-page-list .wiki-grid {
    border: var(--divider);
}

.wiki-page-list .wiki-page-grid-row:nth-child(even) {
    background-color: var(--grid-background-color);
}

.wiki-page-list .wiki-page-grid-row {
    background-color: var(--grid-background-color);
}

a.not-visited {
    color: var(--light-link-colour);
}

.wiki-page-list .wiki-page-grid-row.wiki-page-header-row {
    border-bottom: var(--divider);
}

.wiki-page-list .wiki-page-grid-row.wiki-page-header-row .wiki-grid-header {
    border-right: var(--divider);
    background-color: var(--dark-highlight-color);
}

.wiki-page-list .wiki-grid-header {
    color: var(--light-text-colour);
}



/* SETTINGS */

.userPrefs .form-item .input-wrap {
    background-color: var(--grid-background-color);
}

.userPrefs label {
    color: var(--light-text-colour);
}

.userPrefs input:not([type=submit]):not([type=radio]):not([type=checkbox]) {
    border: var(--divider);
    color: var(--light-text-colour);
    background-color: var(--main-body-color);
    box-shadow: var(--remove-shadow);
}

.userPrefs .form-item {
    border-bottom: var(--divider);
}

.userPrefs button {
    background: var(--main-body-color);
    background: linear-gradient(var(--main-body-color),var(--main-body-color));
    border: var(--divider);
    border-radius: 3px;
    color: var(--light-text-colour);
}

.userPrefs .form-item .input-wrap .selected {
    background-color: var(--main-body-color);
    border-radius: 3px;
    position: relative;
    transition: all 0.125s ease;
}

}
