css:
/********************************************************/
/* Change "demo.rocket.chat" to your Rocket.Chat domain */
/********************************************************/

@-moz-document domain("demo.rocket.chat") {


/********************************************************/
/********************************************************/


/* Get rid of footer on bottom-left */

.side-nav footer {
    display:none;
}
.side-nav .rooms-list, .flex-nav .content, .account-box .options {
    height:100%;
}

.side-nav .unread-rooms.bottom-unread-rooms {
    bottom:0;
}

.rooms-list {
    padding-bottom:5em;
}




/* Make messages more compact */
.message .body {
 	font-size:.9em !important;   
}

.message {
     padding: 6px 20px 4px 70px;  
     line-height:18px;
}

.message.sequential {
    padding-top:1px;
}


/* Make sidebar more compact */


.side-nav h3 {
 	margin-top:15px;   
}


.side-nav .more {
    font-size:11px !important;
}

.side-nav ul a {
 	padding-top:2px;
    padding-bottom:2px;
}


/* Font size */

li {
    font-size:.93em !important;
}

.rooms-list li span {
    font-size:.85em !important;
}

.attachment-title {
    font-size:1em !important;
}

pre {
    font-size:1.2em !important;
}

code {
    font-size:1em !important;
}



/* Optional font face */

* {
    font-family:"Open Sans";
}

pre * {
 	font-family:Monospace !important;   
}



/* Sidebar doesn't push content */


.flex-tab {
 	max-width:300px;   
}

.main-content.flex-opened {
    right:0px;
}

/* Sidebar user photo not cropped */

.user-view .thumb {
    height: 350px;
    cursor: default !important;
}

}
