@-moz-document domain("corazondemelon.es") {
/*** Creado por Calista 7/11/16
Puedes modificar el tema a tu gusto — NO borres esta parte.
***/

body#container { background-color: Black!important; }

#container.connected { background: url(https://68.media.tumblr.com/c687d6d4025b168f2285c391b99bc1ef/tumblr_odzxn6sDru1vq7wkwo4_r2_500.png)fixed left bottom no-repeat, url(https://68.media.tumblr.com/04a86b620ab9f1f4594a8aa54e099de0/tumblr_odzxn6sDru1vq7wkwo5_r2_500.png)fixed right bottom no-repeat, url(https://68.media.tumblr.com/77c7d8362d816bb909974b8ab44ef586/tumblr_odzxn6sDru1vq7wkwo1_r3_1280.jpg)fixed center top!important; background-size: 19%, 19%, cover!important; }

#header h1 a { background: url(https://68.media.tumblr.com/b24283f2d1ce5df1a4200836a6af4e7b/tumblr_odzxn6sDru1vq7wkwo7_r1_250.png)left bottom no-repeat, url(https://68.media.tumblr.com/4e78c7decca0bf0a187cbc42a17f44eb/tumblr_odzxn6sDru1vq7wkwo8_r1_400.png)right bottom no-repeat; background-size: 15%, 20%!important }

#cadre { background: rgba(255, 255, 255, 0.9); border: 1px solid Black; border-radius: 5px; box-shadow: 2px 8px 15px 3px Black; min-width: 740px }

#body:before { border-left: transparent }

#copy1 { background: transparent; color: White; font-weight: bold; margin-top: 50px; margin-left: 250px; }

#copy1 a { color: White; font-weight: bold; }

#copy2 { background: transparent; margin-left: -10px; }

#copy2 a { color: White; }

#avatarGirl { padding-left: 30px }

.soundController { background: White; border: 1px solid Black; border-right: transparent; box-shadow: -2px 5px 15px 1px Black; height: 35px; width: 35px }

.soundController img { height: 30px; margin-top: -3px; width: 30px }

.sliderContainer { background: White; border: 1px solid Black; border-right: transparent; box-shadow: -5px 5px 15px 1px Black; height: 35px; left: -68.5px }

.sliderContainer label { color: Black; font-family: Calibri, sans-serif; height: 20px; margin-top: -3.5px }

.ui-slider-horizontal { height: 4px }

.ui-slider .ui-slider-handle { border: 1px solid DimGray; height: 10px; }

#faq { background: transparent }

#faq p#frequency-name { background: linear-gradient(to top, Black 0%, DarkRed 60%, Black 100%); border: 2px solid Black; border-radius: 10px; color: White; letter-spacing: 1px; line-height: 28px }

#faq div#frequency { background: linear-gradient(to top, DimGray 20%, LightGray 60%, Gray 100%); border: 2px Black solid }

#faq div#left a { color: White; font-family: century gothic, sans-serif; font-weight: bold }

#faq div#right a.category { color: DimGray; font-family: Cambria, serif; font-size: 23px; letter-spacing: 1px }

#faq div#right a.question { color: DarkRed; font-weight: bold; text-shadow: 0 3px 4px Silver }

#container.connected #header { background: url(https://68.media.tumblr.com/7d19f0ee87cae6cff1a1279a5fc99b89/tumblr_odzxn6sDru1vq7wkwo2_r3_1280.png) center top no-repeat; height: 145px; }

#logged-deco { background: url(https://66.media.tumblr.com/391a0dc6928b31f58995f46c0a83d025/tumblr_inline_ocebpyl4RY1u5w4b5_540.png); -webkit-filter: grayscale(100%) }

#logged-pseudo { left: 10px; width: 280px }

#logged-pseudo span, #logged-pseudo a { color: Ghostwhite; font-size: 14px; letter-spacing: 1px; text-shadow: 3px 2px 4px Black }

#logged-episode { color: Gainsboro; left: 10px; text-shadow: 3px 4px 5px Black; top: 33px }

#logged-episodenumber { background: DimGray; border-radius: 15px; box-shadow: 1px 2px 3px Black; height: 18px; left: 101px; text-shadow: 1.5px 2px 2px Black; top: 33.5px; width: 18px }

#logged-money, #logged-pa, #logged-pa .actionpoints, #logged-moral { color: White!important; font-family: century gothic, sans-serif; letter-spacing: 1px; left: 147px; text-decoration: none; text-transform: none; width: 165px }

#logged-pa { font-size: 10px!important }

#logged-money:before { content: url(https://68.media.tumblr.com/285e33a16cca2208252d95d216df4526/tumblr_inline_oe1a6fvA3o1u5w4b5_540.png); font-size: 20px; margin-right: 5px }

#logged-pa .actionpoints:before { content: url(https://68.media.tumblr.com/fbfc58b3b177fc211e755d0944bfa2e7/tumblr_inline_ocebnvaS9o1u5w4b5_540.png); font-size: 17px; margin-right: 5px }

#logged-moral:before { content: url(https://68.media.tumblr.com/c696a3e05f0e36fbadfd042944168643/tumblr_inline_og4x5sqGol1u5w4b5_540.png); font-size: 15px; margin-right: 5px; }

#logged-replays a { color: White; font-size: 10px; font-family: century gothic, sans-serif; letter-spacing: 1px; text-decoration: none; text-transform: none }

#logged-messages, #logged-replays { left: 10px; width: 180px }

#logged-messages a, #logged-replays a { color: White!important; font-size: 10.5px!important; text-decoration: none; text-transform: none }

#logged-messages:before { color: White; content: url(https://68.media.tumblr.com/44f15a5066d2454cf86973f5a288bdbd/tumblr_inline_oe1a6gupaZ1u5w4b5_540.png); font-size: 18px; margin-right: 17px }

#logged-replays:before { color: White; content: url(https://68.media.tumblr.com/5b5b75aa90847d7cb0f711105201fc93/tumblr_inline_oe1a6gQU1P1u5w4b5_540.png); font-size: 15px; margin-right: 14px }

#logged-messagescount { background: linear-gradient(to bottom, Black 20%, DarkSlateGray 60%, DimGray 100%); border-radius: 15px; color: White; font-size: 11px; left: 25px; top: 59px }

#logged-replayscount { background: linear-gradient(to bottom, Black 20%, DarkSlateGray 60%, DimGray 100%); border-radius: 15px; color: White; font-size: 11px; left: 25px; top: 89px }

#astrological-sign { left: 45px; top: 125px }

#astrological-sign a { color: LightGray; font-family: Calibri, sans-serif; font-size: 16px; text-shadow: 2px 0 3px Black }

h1, h2, h3, h4, h5 { color: Black; font-family: century gothic, sans-serif }

h2 .firstword, .integrated-title2 .firstword { background: -webkit-linear-gradient(OrangeRed, DarkRed); font-family: century gothic, sans-serif; letter-spacing: 1.5px; -webkit-background-clip: text; -webkit-text-fill-color: transparent }

h3, h4, h5 { background: -webkit-linear-gradient(DarkRed, Red); letter-spacing: 2px; -webkit-background-clip: text; -webkit-text-fill-color: transparent }

h2 .firstword, .integrated-title2 .firstword { background: -webkit-linear-gradient(LightGray, DimGray); letter-spacing: 1px; -webkit-background-clip: text; -webkit-text-fill-color: transparent }

h2, .integrated-title2 { color: Red }

#right-informations, #counter, .number, .firstword { color: Black!important; font-family: century gothic, sans-serif; font-weight: bold; letter-spacing: 2px }

a { color: Gray; font-weight: bold; text-decoration: none }

#sous-menu { background: url(https://68.media.tumblr.com/53726ebfb2a0c8bcdf02fe3a97cad5c3/tumblr_odzxn6sDru1vq7wkwo6_r1_1280.png) no-repeat scroll top center transparent; margin-top: -5px }

#sous-menu a:hover, #sous-menu a.actif { opacity: 0 }

#discussion table { background: #505050; color: White }

#discussion table td { border: 1px solid Black; border-right: none }

#discussion table .date { border: 1px solid Black; border-left: none }

#discussion table tr:hover { background: White; transition-duration: 0.5s }

#discussion table tr:hover .author_avatar { background: Black; transition-duration: 0.5s }

#discussion table .author_avatar { background: IndianRed; border: 1px solid Black; border-right: none }

#discussion table tr { border-bottom: 1px solid Black; border-right: 1px solid Black; border-top: 1px solid Black; }

#discussion table tr.read_1 { background: White; transition-duration: 0.5s }

#discussion .warning { background: LightGray; color: White }

#discussion .message_bloc .type-0 { background: linear-gradient(to top, Black 10%, DimGray 100%); border-radius: 7px; box-shadow: 3px 5px 6px 2px DarkGray; color: White; font-family: century gothic, sans-serif; margin-bottom: 15px }

#discussion .message_bloc .type-1 { background: linear-gradient(to top, Black 40%, DarkRed 60%, Gray 100%); border-radius: 7px; box-shadow: 3px 5px 6px 2px DarkGray; color: White; font-family: century gothic, sans-serif; margin-bottom: 15px }

div.avatarfull { background: rgba(255, 255, 255, 0.5); border: 2px Black solid }

.cupboard .tab-header a img, .cupboard .tab-header .tab-active img { border-radius: 10px }

.cupboard .tab-header a:hover img.hover, .cupboard .tab-header .tab-active img.hover { border-radius: 10px }

.cupboard .tab-header a:hover img.normal, .cupboard .tab-header .tab-active img.normal, .cupboard .tab-header a img.hover { border-radius: 10px }

.cupboard .tab-body ul li, #zIndexTools>#current, #zIndexTools #backward, #zIndexTools #forward { border-radius: 10px }

.cupboard .tab-body ul li { border-radius: 10px }

#storeclothes-header a:hover img.normal, #storeclothes-header .selected img.normal, #storeclothes-header a img.hover { border-radius: 10px }

#storeclothes-header a img.normal { border-radius: 10px }

ul#store-items-list li { background: transparent url(https://68.media.tumblr.com/d23ca222418714c2a8de0cbb990487e5/tumblr_inline_og8z1v7Bte1u5w4b5_540.png)no-repeat left top; background-color: rgba(255, 255, 255, 0.7); border-radius: 5px }

#store-items-list li { border: 1px solid transparent; }

#profile .player-name { background: transparent; left: 55px }

#profile .player-name .summary { background: linear-gradient(to top, Black 10%, DarkRed 50%, Black 100%); border: 1px solid DarkGray; border-radius: 5px; color: White; font-family: century gothic, sans-serif; left: -10px; letter-spacing: 2px; font-size: 16px; width: 165px }

#profile div#avatarProfil { background: rgba(255, 255, 255, 0.5); background-size: 100%; border: 2px solid Black; border-bottom: transparent; border-radius: 15px 15px 0 0 }

#profile div#right { background-color: transparent }

#profile div#actions { background: Black; background-size: 260px 120px; border: 2px solid transparent; border-radius: 0 0 15px 15px; height: 138px; left: 0px; top: 0px; width: 260px; }

#profile div#actions li .action-description { color: White; font-size: 11px }

.idcard { border-radius: 15px; padding-left: 7.5px; padding-top: 40px }

.idcard-member { background: url(https://68.media.tumblr.com/ab4f5589d6e2f166ee51550297a07eed/tumblr_odzxn6sDru1vq7wkwo3_r2_400.jpg) }

.idcard-member span { color: White; font-size: 11px; font-weight: bold; text-shadow: 2px -1px 1px Silver }

.idcard .avatar { background: rgba(255, 255, 255, 0.4); background-size: cover; border: 1px solid transparent; border-radius: 10px; margin-left: 5px }

.idcard .value { background: rgba(255, 255, 255, 0.4); border-radius: 4px }

.idcard .gender .value { background: White; width: 18px; }

.idcard .signature .value { width: 285px }

#profile blockquote.spoil h4.punmob_spoil { background-color: DimGray; border-radius: 10px }

#profile blockquote { background: linear-gradient(to bottom, Gainsboro 10%, LightGray 50%, DimGray 100%); border-radius: 10px; }

#episode-list .clear { background: transparent; height: 10px }

.scene .sentence { background: linear-gradient(to left, Black 10%, DarkRed 60%, FireBrick 100%); border-left: 4px solid transparent; border-top: 4px solid transparent }

.scene .responses-wrapper { background: transparent url(https://65.media.tumblr.com/9f700f7c838d7ff5dc5a6e3a5c1b94fb/tumblr_inline_ocxz8pOYNV1u5w4b5_540.png) right top no-repeat }

.scene .responses { border-left: 4px solid transparent }

.scene .responses .response { color: DimGray; font-family: Cambria, serif; font-size: 15px; transition-duration: 0.2s }

.scene .response:hover { background: DarkGray; color: White; transition-duration: 0.3s }

.scene .responses .response span { font-family: Cambria, serif; font-size: 14px }

.episode-progression .progress-left { background: linear-gradient(to top, Black 10%, Red 60%, DarkRed 100%); border-radius: 5px 0 0 5px }

.episode-progression .progress-inner { background: linear-gradient(to top, Black 10%, Red 60%, DarkRed 100%) }

.episode-progression .progress-right { background: linear-gradient(to top, Black 10%, Red 60%, DarkRed 100%); border-radius: 0 5px 5px 0 }

.episode-progression .playcount { background: radial-gradient(DimGray 20%, Black 100%); border-radius: 15px; box-shadow: 2px 4px 15px 5px Black }

#current-episode-image { -webkit-filter: grayscale(100%) }

#current-episode-image #current-episode-number { background: radial-gradient(DimGray 20%, Black 100%); border-radius: 15px; box-shadow: 3px 4px 5px 2px Black }

.boyfriend { margin-bottom: 10px }

.boyfriend, .boyfriend.crush { background: White; border: 1px solid DarkGray; border-radius: 25px 0 25px 0; color: White; text-decoration: none; text-transform: none; }

.boyfriend .description { font-family: Helvetica, sans-serif; font-size: 12px; font-weight: bold }

.boyfriend .lom, .boyfriend .lom span { background-color: White; border-radius: 25px; text-transform: uppercase; color: Black; font-size: 10px; height: 90px; padding: 4px; width: 90px }

.pagination a.page { background-color: Black; color: White; text-decoration: none }

.pagination a.selected { background: FireBrick }

.friend .player-name a { color: FireBrick; text-decoration: none }

.player-name .player-name-tools { background: linear-gradient(to left, Black 10%, DarkRed 60%, FireBrick 100%); border-top: 1px solid DimGray; }

.player-name .player-name-tools li { background: linear-gradient(to left, Black 10%, DarkRed 60%, FireBrick 100%); border: 1px solid DimGray; }

.player-name .player-name-tools li a { background: linear-gradient(to left, Black 10%, DarkRed 60%, FireBrick 100%); color: White; font-size: 11px; font-family: century gothic, sans-serif; text-align: center; transition-duration: 0.3s }

.player-name .player-name-tools li a:hover { background: linear-gradient(to left, DimGray 10%, Gainsboro 40%, White 100%)!important; color: Black; font-size: 11px; font-family: century gothic, sans-serif; letter-spacing: 0.5px; text-align: center; transition-duration: 0.4s }

#bank-container #bank-other-countries { background-color: #808080; }

#bank-container #bank-payment div[class^=payment-method] { background: White; border-left: 2px solid DarkRed!important; border: 2px solid DarkRed }

#bank-container #bank-selection li { background: transparent; border: solid 2px transparent; border-right: solid 2px transparent!important; margin-bottom: 5px }

#bank-container #bank-selection li.active { background: White; border: solid 2px DarkRed; border-right: none!important; margin-bottom: 5px }
}
