css:
@-moz-document domain("www.nordlys.no") {
body.am-page.am-page--fixed.am-skin--page.am-model-section, .am-siteFooter2, .am-siteHeader2-body, .am-article-mainTitle.am-txt--head, .am-gridComp--article, .am-articleEntry-mainTitle, .am-article, .am-skin--gray, .am-skin--blueLight {
    background: #2d2d2d !important;
    color: #fff !important;
}
  
.am-articleByline, .am-articleTag {
    color: #666 !important;
}

.am-articleByline-item, .am-articleDateBar-published:before {
    color: #fff !important;
}

.am-siteHeader2-aID, .am-is-alwaysOpen {
    background: #2d2d2d !important;
    color: #fff !important;
}

.am-figure-caption {
	background: #484544 !important;
    color: #fff !important;
}

.am-commentsTitle {
    border: none !important;
}

.am-skin--page, #am-legacy, #am-legacy .am-adContainer.am-skin--white, #am-legacy .am-page-content .apiPage, #am-legacy #apiStickyAd, #am-legacy #apiHangover {
    background: #2d2d2d !important;
}

.df-article-content {
    color: #fff !important;
}



div.df-container, .df-f3-34, .am-figure {
    background: #222 !important;
    color: #fff !important;
    box-shadow: 3px 3px 10px #181818;
}

.df-article.df-skin-box--finance {
    background: #222 !important;
}

.am-siteHeader2-aID {
    border-left: 1px solid #ccc !important; 
}

span.df-blk {
    color: #fff !important;
}


span.df-article-header-inner, .df-skin-breakingnews--red, .drfront .df-container.df-skin-box--red, .drfront .df-article.df-skin-box--red, .df-skin-box--purple, .df-skin-box--yellow {
    background: #9c0000 !important;
}

.am-article-body a {
    color: #d80000 !important;
}

.am-article-header {
    border: none !important;
}

.am-articleTagList--prominent .am-articleTagList-item:first-child .am-articleTag {
    color: #fff !important;
}


div.am-color--brandCombo {
    background: #2d2d2d !important;
}

amedia-weather-widget ul.locations {
    background: #222 !important;
}

amedia-weather-widget ul li a {
    color: #999 !important;
}

li {
    border-color: #000 !important;
}

#skyscraper-ads-container, bazaar-ad-container, .am-siteHeader2-content, .am-siteHeader2-menuSubscription, #apiBackgroundAd, .am-page-ad, .drfront.lp_row9, .am-adContainer, .bb-iawr, .am-gridComp--1.am-gridComp:nth-of-type(4) > .am-gridComp-body, .lp_challengedeck, .lp_jobcarousel, div.ad, .lp_servicebar, .amc-1881, .personalia_frontpage_v2, #df-111-g264a01d  {
    display: none !important;
}

.am-siteHeader2-dateAndMenuTrigger.lp-menuopen {
    background: #555 !important;
}

.am-siteHeader2-menu, .am-siteHeader2-dateAndMenuTrigger, .am-siteHeader2-services, .am-siteHeader2-item.am-siteHeader2-panel {
    background: #333 !important;
    color: #fff !important;
}

.am-siteHeader2-menu, .am-siteHeader2-services, amedia-weather-widget {
    box-shadow: 0px 3px 10px #181818;
}

.am-siteHeader2-menuTrigger {
    border-color: #666;
}

.df-skin-annonse {
    display: none !important;
}

.am-page-body {
    margin-left: auto !important;
    margin-right: auto !important;
    display: block !important;
}
}
