css:
@-moz-document domain(devs.forumvi.com) {
body{
    background: rgb(43, 43, 43) !important;
    color: #A9A9A9;
}
.borderwrap .box-content, .borderwrap .maincontent, .borderwrap .maintitle~fieldset, .borderwrap>.ipbform, .borderwrap>fieldset {
    border: 1px solid #232323;
    background: #232323;
    box-shadow: 0 1px 1px rgb(25, 25, 25);
}
.olList li:after {
    border-color: #232323 transparent #232323 #ebebeb;
}
.borderwrap .box-content:before, .borderwrap .maincontent:before, .borderwrap .maintitle~fieldset:before, .borderwrap>.ipbform:before, .borderwrap>fieldset:before {
    border-bottom-color: #232323;
}
.borderwrap .box-content:after, .borderwrap .box-content:before, .borderwrap .maincontent:after, .borderwrap .maincontent:before, .borderwrap .maintitle~fieldset:after, .borderwrap .maintitle~fieldset:before, .borderwrap>.ipbform:after, .borderwrap>.ipbform:before, .borderwrap>fieldset:after, .borderwrap>fieldset:before {
    border-color: transparent transparent #232323;
}
.olList li a {
    color: #a9a9a9;
}
table th {
    border-bottom: 2px solid #2b2b2b;
}
.olList li {
    border-bottom: 1px dashed #2b2b2b;
}
.stat4r>strong {
    color: #d8d8d8;
}
.index-box tr {
    border-top: 1px solid #2b2b2b;
}
.module.inner {
    background: #232323;
}
#fo_stat th {
    border-bottom: 1px solid #2b2b2b;
}
select{
    color: #A9A9A9;
}
.olList li a:focus, .olList li a:hover {
    color: #f57c00;
}
input[type=password], input[type=text], input[type=search], textarea {
    border: 1px solid #212121;
    background-color: #2b2b2b;
    color: #A9A9A9;
    outline: none;
}
#tooltip {
    border: 1px solid #000000;
    background: #232323;
    box-shadow: 0 1px 3px rgba(0,0,0,.24);
}
#message {
    border: 1px solid #2b2b2b;
    color: #A9A9A9!important;
    background: #2b2b2b;
    border-bottom-color: #000;
}
#chatbox,#chatbox_header,#message:focus,.resize{
    background: #2b2b2b;
}
.last-post-icon, .lastaction .stat4r>a:last-child {
    border: 1px solid #232323;
    background-color: #1b1b1b;
}
.has-sub>ul {
    border: 1px solid #111;
    background: #212121;
}
.has-sub>ul .line{
    border-top: 1px solid #2b2b2b;
}
.current-user-link {
    border-bottom: 1px solid #2b2b2b;
}
#navstrip>li {
    background: #212121;
}
#navstrip>li:after, #navstrip>li:before {
    border-color: transparent transparent transparent #212121;
}
.post-footer {
    border: 1px solid #191919;
    background: #2b2b2b;
}
.author-stat {
    border: 1px solid #101010;
    background: #2b2b2b;
}
.field-label {
    border-bottom: 1px dashed #101010;
}
.module {
    background: #232323;
}
#qpost .qreply {
    border: 1px solid #232323;
    background: #232323;
    box-shadow: 0 1px 1px rgba(0,0,0,.1);
}
#qpost .qreply:after, #qpost .qreply:before {
    border-color: transparent transparent #232323;
}
#find_username .right, #quick_reply>.center, .formbuttonrow {
    background: #1d1d1d;
}
div.sceditor-toolbar {
    background: #1d1d1d;
    border-bottom: 1px solid #1d1d1d;
}
.sceditor-container {
    background: #2b2b2b;
    border: 1px solid #232323;
    color: #A9A9A9;
}
.sceditor-container iframe, .sceditor-container textarea {
    background: #2b2b2b;
    color: #A9A9A9!important;
}
#plus_menu {
    border: 1px solid #000000;
    background: #212121;
}
#plus_menu>.left-overview a:focus, #plus_menu>.left-overview a:hover {
    background: #000000;
    color: #A9A9A9;
}
hr {
    border-color: #000000;
}
#plus_menu:after, #plus_menu:before, .maintitle .popupmenu:after, .maintitle .popupmenu:before {
    border-color: transparent transparent #000;
}
.post-type-icons span {
    background-color: #fff;
}
.subtitle {
    border: 1px solid #2b2b2b;
    background: #2b2b2b;
}
#tagsSelect .select2-selection {
    border-color: #000000;
    background-color: #2b2b2b;
}
.select2-container--default .select2-selection--multiple .select2-selection__choice {
    background-color: #000000;
    border: 1px solid #656565;
    color: #A9A9A9;
}
.select2-container--default .select2-results>.select2-results__options {
    background-color: #2b2b2b;
}
*::-webkit-scrollbar {
  width: 10px;
}
 
*::-webkit-scrollbar-thumb {
  background: #666;
  border-radius: 20px;
}

*::-webkit-scrollbar-track {
  background: #ddd;
  border-radius: 20px;
}
.has-sub>ul:after, .has-sub>ul:before {
    border-color: transparent transparent #000;
}
.member-status, .popupmember {
    background: #2b2b2b;
}
.group-users tr, .member, .topic-list-box tr {
    border-bottom: 1px solid #2b2b2b;
}
.member:hover {
    border-color: #000000;
    background: #171717;
}
.post-btn:focus, .post-btn:hover {
    border: 1px solid #E64A19;
    color: #E64A19;
}
.cb-bubcloud-options-list {
    border: 1px solid #000000;
    background: #232323;
}
list:after, .cb-bubcloud-options-list:before {
    border-color: transparent transparent #000;
}
.msg-day span {
    background: #2b2b2b;
}
#profile-advanced-details .message-text {
    border: 1px solid #2b2b2b;
    background: #2b2b2b;
}
#profile-advanced-details .message-text:after, #profile-advanced-details .message-text:before {
    border-color: transparent transparent #2b2b2b;
}
.friend-block~.mainmenu {
    background: #2b2b2b;
}
#new-message .privmsg li .tabitem, #new-message .privmsg li .tabitem a, .privmsg-menu li .tabitem, .privmsg-menu li .tabitem a {
    box-shadow: 0 0 0 1px #232323;
}
#chatbox_members {
    border-right: 1px solid #000000;
    background: #2b2b2b;
}
.member-title {
    background: #232323;
}
}
