css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
    domain("iiio.ml") , 
    domain("dayigu.github.io"), 
    domain("victor72.github.io"), 
    domain("ii.3w.dkys.org"), 
    url-prefix("http://orgmode.org/manual/"), 
    domain("chinaxing.org"),
    url-prefix("http://orgmode.org/worg/")
    {
        
/* 阻止 */
#org-info-js_console-container,
#menu
    {
    display: none;
    }    
    
body {
    font-family: Helvetica, Arial, Vernada, Tahoma, STFangsong, STXihei, "Songti SC", "Microsoft YaHei", Heiti, sans-serif;
    font-size: 18px;
    margin: 5% 10% 5% 10%;
    padding: 2% 5% 5% 5%;
    width: 70%;
    line-height: 150%;
    border: 1px solid LightGrey;
    box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    border-radius:10px;
}

H1 {
    font-family: "verdana", "Helvetica", Palatino Linotype, Book Antiqua, Palatino, STKaiti, SimSun, serif;
}

H2 {
    font-family: "verdana", "Helvetica", Palatino Linotype, Book Antiqua, Palatino, STKaiti, SimSun, serif;
    margin-bottom: 60px;
    margin-bottom: 40px;
    padding: 5px;
    border-bottom: 2px LightGrey solid;
    line-height: 150%;
    color: DarkGreen;
}


H3 {
    font-family: "verdana", "Helvetica", Palatino Linotype, Book Antiqua, Palatino, STKaiti, SimSun, serif;
    margin-top: 40px;
    margin-bottom: 30px;
    border-bottom: 1px LightGrey solid;
    width: 80%;
    line-height: 150%;
    color: DarkBlue;
}


H4 {
    font-family: "verdana", "Helvetica", Palatino Linotype, Book Antiqua, Palatino, STKaiti, SimSun, serif;
    margin-top: 40px;
    margin-bottom: 30px;
    border-bottom: 1px LightGrey solid;
    width: 80%;
    line-height: 150%;
    color: DarkBlue;
}


li {
    margin-left: 10px;
}


blockquote {
    border-left: 4px lightgrey solid;
    padding-left: 5px;
    margin-left: 20px;
}


pre {
    font-family: Inconsolata, Consolas, "DEJA VU SANS MONO", "DROID SANS MONO", Proggy, monospace;
    font-size: 75%;
    border: solid 1px lightgrey;
    background-color: Ivory;
    padding: 5px;
    line-height: 130%;
    margin-left: 10px;
    width: 95%;
}


pre.src {
    position: inherit;
    overflow: visible;
    padding-top: 1.2em;
    border-radius: 5px;
}


code {
    font-family: Inconsolata, Consolas, "DEJA VU SANS MONO", "DROID SANS MONO", Proggy, monospace;
    font-size: 90%;
}


a {
    text-decoration: none;
    cursor: crosshair;
    border-bottom: 1px dashed Red;
    padding: 1px;
    color: black;
}


a:hover {
        background-color: LightGrey;
}


img {
    margin-left: 10px;
    display: block;
    margin-left: auto;
    margin-right: auto;
    margin-top: 30px;
    margin-bottom: 30px;
}


hr {
    color: LightGrey;
}

#cc-license {
    text-align: center;
    font-family: "verdana";
}

/* 目录的 CSS: table-of-contents
   -------------------------------------------------- */

#table-of-contents {
    position: fixed;
    right: 1.5em;
    background: #FFF;
    border: 1px solid  #e1e1e8;
    border-radius:10px;
    -webkit-box-shadow: 0 0 1em #777777;
    text-align: left;
    /* ensure doesn't flow off the screen when expanded */
    max-height: 100%;
    overflow: auto;
}
#table-of-contents h2 {
    padding-top: 0.05em;
    padding-bottom: 0.05em;
    text-align: center;
    border-bottom:none;
    margin-bottom:0.5em;
}
#table-of-contents ul {
    margin-left: 10pt;
    margin-bottom: 2pt;
    padding: 0;
}
#table-of-contents li {
    padding: 0;
    margin: 2px;
}
#table-of-contents #text-table-of-contents {
    display: none;
    text-align: left;
}
#table-of-contents:hover #text-table-of-contents {
    display: block;
    padding: 0.5em;
    margin-top: -2em;;
}

/* 标签设置 */

#postamble{
    position: fixed;
    top: 0%;
    text-align: left;
    left: 0%;
    line-height: 90%;
    font-size: xx-small;
    display: none;
}


/* UP | HOME */
#org-div-home-and-up{
    position: fixed;
    right: 46%;
    bottom: 3%;
    font-size: 100%;
    display: none;
}
        
}
