css:
@-moz-document domain("roblox.com") {
#header {
        background-color: #ED750A;
    }
    
.navigation .notification-icon {
        background: #ED750A;
    }

.forum-table-header, body .forum-table-header th, body .forum-table-footer {
        background: #ED750A !important;
    }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more {
  		background-color: #ED750A !important;
    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover {
        background:#212020!important;
    }

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer {
  		display: none;
  	}

#MasterContainer, .nav-content-inner, .container-main, .gutter-ads {
        background: url(http://www.cityrider.com/fixed/43aspect.png) top center repeat-x #ED750A;
    }

.content {
background-color: #212020;
}
  
.games-list:hover .scroller, .scroller:hover {
background-color: rgba(100, 100, 100, 0);
}
      
.scroller {
background-color: rgba(100, 100, 100, 0);
}

.btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited {
border: solid 1px rgba(127, 127, 127, 0);
background-color: #ED750A;
background-image: url(http://www.cityrider.com/fixed/43aspect.png);
color: white;
}

div#friend_dock_thumb_container {
box-shadow: 0px 1px 5px #000000;
}

.redesign .tab_white_31h_container .ajax__tab_inner, .tab, .tab-container>div {
border: solid 1px rgba(127, 127, 127, 0);
}

div.chat-header-blink-off {
background-color: #ED750A;
background-image: linear-gradient(to bottom,#ED750A,#ED750A);
}

.party-window .title {
background-color: #ED750A;
background-image: linear-gradient(to bottom,#ED750A,#ED750A);
}

.chat-sender-self {
font-weight: bold;
color: #ED750A;
}

.party-window .main {
border: 1px solid rgba(166, 166, 166, 0);
}
    
#chat_input textarea {
border: 1px solid rgba(170, 170, 170, 0);
}
    
#chat_messages {
border: 1px solid rgba(170, 170, 170, 0);
}
    
div.friend_dock_chatbox {
border: solid 1px rgba(127, 127, 127, 0);
}
    
div.friend_dock_chatbox_chat {
border: solid 1px transparent;
}
    
textarea {
border-color: rgb(169, 169, 169, 0);
}

div#friend_dock_thumb_container {
border: solid 1px rgba(127, 127, 127, 0);
}

.btn-primary {
color: #000000;
background-color: #ED750A;
border-color: #ED750A;
}

.btn-primary, .btn-primary:link, .btn-primary:active, .btn-primary:visited {
background-image: url(http://www.cityrider.com/fixed/43aspect.png)!important;
}
    
.btn-control, .btn-control:active, .btn-control:link, .btn-control:visited, .btn-control:hover {
border: 1px solid rgba(119, 119, 119, 0);
color: #333333;
background-color: #ED750A;
}
        
.btn-control.btn-control-medium {
background-image: url(http://www.cityrider.com/fixed/43aspect.png);
}

.btn-control.disabled {
border: 1px solid rgba(204, 204, 204, 0);
color: rgba(255, 255, 255, 0.62);
}
                
#MasterContainer, .nav-content-inner, .container-main, .gutter-ads {
background: url(http://www.cityrider.com/fixed/43aspect.png) top center repeat-x #000000;
}

.btn-control.btn-control-large {
height: 25px;
line-height: 24px;
font-size: 12px;
padding: 0 7px;
background-image: url(http://www.cityrider.com/fixed/43aspect.png);
}

.GrayDropdown .Button {
background: url(http://i.imgur.com/Z7U9uDM.png) no-repeat right top;
border: 1px solid rgba(119, 119, 119, 0);
color: #000000;
}

.pager.next {
background: url('http://i.imgur.com/fZ3KJvh.png') no-repeat top left;
}
    
.pager.previous {
background: url('http://i.imgur.com/kA1nEgo.png') no-repeat top left;
}

.roblox-message-large-btn {
background-image: url(http://www.cityrider.com/fixed/43aspect.png);
}

.filter select {
background: #ED750A;
border: 1px solid rgba(165, 165, 165, 0);
color: white;
}

#searchbox {
padding: 1px 22px 1px 7px;
border: 1px solid #bbb;
color: #ED750A;
height: 20px;
background-color: #ED750A;
}
}
/* i really want this to be global */
