css:
@-moz-document domain("0nyan.ml"), domain("nullnyan.net"), domain("www.nullnyan.net"), domain("0nyan.space"), domain("www.0nyan.space") {
html, body {
  color: #333333;
  font-family: "Roboto","Open Sans","Trebuchet MS",Trebuchet,sans-serif;
  font-size: 15px;
}
body, .wrapper {
  background: #eee;
  color: #333333;
}
.post, .bubble, .help > div {
  color: #333333;
}
.post {
  background: #F8F8F8;
  padding: 0;
  padding-right: 2px;
  border-radius: 0px;
  border: 1px #CCC solid;
}
.popupPost, .highlight {
	border:1px #CCC solid;
	border-radius:none;
	-webkit-box-shadow:0 0 20px #ccc;
	box-shadow:0 0 20px #ccc;
	border-radius:0;
}
.fileType, [class^="icon"], [class*=" icon"], .topbar label, .postcounter, .bubble label, #postform #lock::before {
  color: dimgrey;
}
a, .logo, #postform .iconHelp::before {
  color: rgb(97,138,182);
}
#counter {
  border-color: rgb(97,138,182);
}
a {
  text-decoration: underline;
}
a:visited {
	color: rgb(97,138,182);
}
a:hover, #submit:hover, #submit:focus, .topbar label:hover, .bottombar label:hover, .topbar label:hover i, .file:hover .icon::before {
	color: rgb(23,53,85);
}
.boardlink a, .file a {
  color: dimgrey;
}
aside .icon:hover::before {
  color: rgb(23,53,85) !important;
}
aside .icon::before {
  color: dimgrey !important;
}
aside a:hover, aside a.active {
  color: rgb(23,53,85) !important;
}
.reflink a {
  font-size: .8em;
  font-weight: bold;
  text-decoration: none;
  color: #696969;
}
.reflink a:hover {
	color:#000;
}
hr {
  border-style: solid none none none;
  border-width: 1px;
  border-color: #BBBBBB;
}
.filetitle {
  font-size: 1.1em;
  color: #3F5264;
  font-weight: bold;
}
aside a, aside label, aside a:visited {
  background: #F8F8F8;
  border: 1px #CCC solid;
  text-decoration: none;
}
.active, aside a:hover, aside label:hover {
  background: #FFFFFF !important;
  -webkit-box-shadow: 0 4px 8px #CCCCCC;
  box-shadow: 0 4px 8px #CCCCCC;
}
.bubble {
  padding: 3px;
  border-radius: 0;
  border: 1px #CCC solid;
  white-space: nowrap;
  z-index: 9999;
  opacity: 0.85;
  -webkit-transition: opacity 0.2s;
  transition: opacity 0.2s;
  background: #eee;
  box-shadow: 0 0 20px #ccc;
}
.bubble label:hover {
  color: rgb(23,53,85);
}
.bubble:hover {
  opacity: 1;
}
.message {
  padding-bottom: 5px;
}
.refmap {
  margin-top: -1px;
  padding-bottom: 5px;
}
.help .rules, .help .helpMark, .help .lang {
  background: #F8F8F8;
  border: 1px #CCC solid;
  box-shadow: 0 0 20px #ccc;
}
#deletepostpassword {
  border-bottom: 1px #CCC solid;
  border-left: 1px #CCC solid;
  border-top: 1px #CCC solid;
  box-shadow: 0 0 20px #ccc;
}
#delform button {
  border: 1px #CCC solid;
  box-shadow: 0 0 20px #ccc;
}
#postform {
  border: 1px #CCC solid;
  box-shadow: 0 0 20px #ccc;
}
#postform #dragCell {
  background: #FFFFFF;
}
textarea, input, .post-mark, .captchaWrapper {
  background-color: #eee;
  color: #333333;
}
button {
  background: #fff;
  color: #333333;
}
button:hover {
  color: rgb(23,53,85);
}
.post-mark a {
  text-decoration: none;
}
.pagenavigator, .pagenavigator button, .pagenavigator a:first-of-type {
  color: rgb(97,138,182);
  background: transparent;
}
.pagenavigator a:hover {
  background: #fff;
}
.unkfunc {
  color: #26AF4C;
}
.file .postFileLink {
  margin-top: -33px;
}
.spoiler:hover {
  background: transparent;
  color: #333333;
}
}
