css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("moodle3.unistra.fr") {
  html, body {
    background-color: #1C1C1C;
  }
  
  /* HEADER */
  
  .navbar-inner, #page-footer {
    background-color: #252526;
    color:#7F8C8D;
    text-shadow: none;
  }
  
  .navbar .nav > li > a {
    color:#7F8C8D;
  }
  
  .usermenu .moodle-actionmenu .toggle-display .userbutton .usertext {
   color: #7F8C8D;
  }
  
  /* PAGE */
  
  p, a {
    color:#E0DDF0;
  }
  
  .pagelayout-mydashboard #region-main, .block .header .title h2, .coursebox, .coursebox a, div.content, .input-block-level,
  .course-content, .course-content p, .course-content h6, div.summary ul#tetras li, h1, select,
  .pagelayout-incourse #region-main, .generaltable tbody tr > th, .generaltable tbody tr > td, .generaltable tbody > tr:nth-child(2n+1) > td,
  .path-mod-assign td.submissionnotgraded, .path-mod-assign td.earlysubmission, .jstree-default .jstree-hovered, ul.jstree-children a,
  label, div#fitem_id_categorylist, .userprofile .profile_tree section, table.flexible tbody > tr:nth-child(2n+1) > td, .generaltable tbody > tr:nth-child(2n+1) > td,
  .que .formulation, .que .info, pre{
    background-color: #2D2D30;
    color:#E0DDF0;
    text-shadow: none;
  }
  
  div#jstree_categorylist div.coursebox {
    background-color: #3F3F46;
  }
  
  h1 {
    text-align: center;
  }
  
  #coc-courselist .coursebox {
    border-bottom: 2px solid #1C1C1C;
  }
  
  select {
    border: 1px solid #1C1C1C;
  }
  
  .pagelayout-mydashboard #region-main,
  .course-content .section-summary,
  div.summary ul#tetras li,
  h6{
    border: 0;
  }
  
  input[type="submit"], input#searchform_search,input#searchform_button, input#id_submitbutton,div.loginpanel div:first-child a, button {
    background-color: #3F3F46;
  }
  
  .course-content ul li.section.main {
    border-bottom: 2px solid #252526;
  }
  
  h2, div.summary #tetras a {
    color: #007ACC;
  }
  
  .breadcrumb > li {
    text-shadow: 0 1px 0 #3F3F46;
  }
  
  .alert-danger, .alert-error {
   color:#E0DDF0;
   background-color: #812B28;
    text-shadow: 0 0px 0 rgba(255,255,255,0.5);
  }
}
