css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("hattrick.org") {
body {
 margin:0 0 !important;
 width:auto !important;
 text-align:left !important;
 color: #fff !important;
 background-image: none !important;
 background-color: #000 !important;
}
#footer {
  display: none !important;
}
div[class*="ad_"]{display:none!important}
a[id*="logo"]{display:none!important}
#page {
  background-color:#000 !important;
  border: 1px solid #333333 !important;
}
#hattrickNoSupporter {
  width:1024px !important ;
}
h2.info, h3 {
  color:#fff !important;
}
.subMenuBox ul li {
  color:#fff !important;
}
#sidebar ul li {
  color:#fff !important;
}
.draw {
  color:#fff !important;
}
#ticker div a {
  color:#fff !important;
}
.subMenuBox a, .subMenuBox a, .sidebarBox a.active {
  color:#fff !important;
}
.viewActive {
  color:#fff !important;
}
#menu {
  background-color:#282828 !important;
}
.main .boxHead h2, .sidebarBox h2, .subMenuBox h2 {
  background-color:#282828 !important;
}
#menu a:hover {
  background-color:#333333 !important;
}
h1 {
  color: #FFFFFF !important;
}
.main .mainBox h2 {
background-color:#282828 !important;
border-bottom:2px solid #333333 !important;
}
.main table th {
border-bottom:2px solid #333333 !important;
background-color: #282828 !important;
color: #FFFFFF !important;
}
h2 {
  /*background-color:#282828 !important;*/
  background-color:#000000 !important;
  border-bottom:2px solid #282828 !important;
  color:#FFFFFF !important ;
}
/* MAINSITE */
#menu {
  background-image: none !important;
  background-color: #282828 !important;
}
.sidebarBox .boxBody, .subMenuBox .boxBody, .main .boxBody {
background:#000000 none repeat scroll 0 0 !important;
}
#header {
background-image: none !important;
background-color: #000000 !important;
}
#top {
background-image: none !important;
background-color: #000000 !important;
}
#content {
  background-image: none !important;
  background-color: #000000 !important;
}
.sidebarBox .boxFooter .boxLeft, .subMenuBox .boxFooter .boxLeft, .main .boxFooter .boxLeft {
  background-image: none !important;
  background-color: #000000 !important;
}  
#bottom {
  background-image: none !important;
  background-color: #000000 !important;
}
#page {
padding: 0 0 !important;
}
/* LINKS */
a {
  color: #ffffff;
}
table tr.even {
border-top: 1px #333333 solid !important;
}
table tr.odd {
border-top: 1px #333333 solid !important;
}
table tr.topBorder td {
border-top: 2px #333333 solid !important;
}
.alert, .error, .ok {
background-color:#282828 !important;
border:1px solid #333333 !important;
}
#shortcuts {
top: 0 !important;
}
/* HATRICK */
.main table, #sidebar table, .subMenuBox table {
background-color: #000000 !important;
}
/* LINE UP */
.name {
color: #000000 !important;
}
.ok {
  background-color: #282828 !important;
  border: 1px solid #333333 !important;
}
.user, .national{
  background-color: #282828 !important;
}
/* MAIN PAGE BORDERS */
.boxBody, .main .boxBody {
  border-bottom: 0px solid #333333 !important;
  border-left: 0px solid #333333 !important;
  border-right: 0px solid #333333 !important; 
} 
}
