css:
@-moz-document domain(daynhauhoc.com) {
    body {
        background-color: #232323;
        color: #A9A9A9;
    }
    body,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    html {
        color: #A9A9A9;
    }
    .d-header {
        background-color: #232323;
        box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.25);
    }
    .list-controls .category-breadcrumb li.bullet>.badge-category,
    .list-controls .btn {
        background: #191919 !important;
        color: #A9A9A9 !important;
    }
    .discourse-tag.box {
        background-color: #191919;
        color: #bdbdbd;
    }
    .topic-list>tbody>tr {
        border-bottom: 1px solid #2b2b2b;
    }
    .topic-list a.title {
        color: #efefef;
    }
    .topic-list>tbody>tr:first-of-type {
        border-top: 3px solid #5d5d5d;
    }
    .topic-list .sortable:hover {
        background-color: #5d5d5d;
    }
    ._2Xv6F {
        color: #fff;
    }
    .nav-pills>li>a {
        color: #A9A9A9;
        background-color: #191919;
    }
    .d-header .icons .icon:hover,
    .btn {
        color: #A9A9A9;
        background-color: #191919;
    }
    .btn:hover {
        background: #404040;
        color: #c7c7c7;
    }
    .user-menu .notifications .read,
    .stats-section li,
    .badge-card {
        background-color: #191919;
    }
    .badge-card .badge-contents .badge-icon {
        background-color: #171717;
    }
    .user-menu .notifications li span,
    .badge-card .badge-contents .badge-info,
    #topic-progress,
    #user-card,
    body input[type="text"],
    body input[type="password"],
    body input[type="datetime"],
    body input[type="datetime-local"],
    body input[type="date"],
    body input[type="month"],
    body input[type="time"],
    body input[type="week"],
    body input[type="number"],
    body input[type="email"],
    body input[type="url"],
    body input[type="search"],
    body input[type="tel"],
    body input[type="color"],
    .category-combobox .category-desc,
    .select2-drop .category-desc,
    #reply-control .contents .d-editor-input,
    #reply-control .contents .d-editor-preview,
    #reply-control.edit-title .contents input#reply-title {
        color: #A9A9A9;
    }
    .user-menu .notifications li:hover,
    .menu-panel ul.menu-links li a:hover,
    .menu-panel ul li.heading a:hover,
    .user-menu .notifications li,
    div.menu-links-header a:hover {
        background-color: #232323;
    }
    .drop-down-visible .d-header .icons .active .icon:after {
        border-top: 1px solid #191919;
    }
    #topic-title h1 a,
    .extra-info-wrapper .topic-link {
        color: #f7f7f7;
    }
    .topic-body,
    .topic-avatar {
        border-top: 1px solid #525252;
    }
    .drop-down-visible .d-header .icons .active .icon {
        color: #A9A9A9;
        background-color: #191919;
        border-top: 1px solid #191919;
        border-left: 1px solid #191919;
        border-right: 1px solid #191919;
    }
    .menu-panel {
        border: 1px solid #191919;
        background-color: #191919;
    }
    .badge-notification {
        background-color: #424242;
    }
    hr {
        border-top: 1px solid #5d5d5d;
    }
    .badge-wrapper.bullet span.badge-category {
        color: #A9A9A9!important;
    }
    *::-webkit-scrollbar {
        width: 10px;
    }
    *::-webkit-scrollbar-thumb {
        background: #666;
        border-radius: 20px;
    }
    *::-webkit-scrollbar-track {
        background: #ddd;
        border-radius: 20px;
    }
    .user-main .about.collapsed-info .details {
        background: rgb(25, 25, 25);
        border-bottom: 1px solid #000000;
    }
    .user-main .about .controls {
        padding: 0;
    }
    .hljs-comment,
    .hljs-quote {
        color: #809980
    }
    .hljs-variable,
    .hljs-template-variable,
    .hljs-attribute,
    .hljs-tag,
    .hljs-name,
    .hljs-regexp,
    .hljs-link,
    .hljs-name,
    .hljs-selector-id,
    .hljs-selector-class {
        color: #e6193c
    }
    .hljs-number,
    .hljs-meta,
    .hljs-built_in,
    .hljs-builtin-name,
    .hljs-literal,
    .hljs-type,
    .hljs-params {
        color: #87711d
    }
    .hljs-string,
    .hljs-symbol,
    .hljs-bullet {
        color: #29a329
    }
    .hljs-title,
    .hljs-section {
        color: #3d62f5
    }
    .hljs-keyword,
    .hljs-selector-tag {
        color: #ad2bee
    }
    .hljs {
        display: block;
        overflow-x: auto;
        background: #131513!important;
        color: #8ca68c;
        padding: 0.5em
    }
    .hljs-emphasis {
        font-style: italic
    }
    .hljs-strong {
        font-weight: bold
    }
    blockquote,
    aside.quote .title {
        border-left: 5px solid #131513;
        background-color: #131513;
    }
    code {
        background: #131513 !important;
    }
    p>code,
    li>code,
    pre>code {
        color: #ff9f23;
    }
    #topic-progress {
        background-color: #000;
        border: 1px solid #232323;
    }
    #topic-progress .bg {
        border-right: 1px solid #333333;
        background-color: #333333;
    }
    .topic-map,
    .topic-map .buttons .btn {
        background: #000000;
        border: 1px solid #000000;
    }
    .topic-map section {
        border-top: 1px solid #232323;
    }
    #user-card .card-content {
        background: rgb(0, 0, 0);
    }
    #user-card h1 a {
        color: #adadad;
    }
    .btn[href] {
        color: #A9A9A9;
    }
    #user-card.show-badges .badge-section .user-badge,
    #user-card.show-badges .badge-section .more-user-badges {
        background: #232323;
        border: 1px solid #232323;
        color: #A9A9A9;
    }
    body input[type="text"],
    body input[type="password"],
    body input[type="datetime"],
    body input[type="datetime-local"],
    body input[type="date"],
    body input[type="month"],
    body input[type="time"],
    body input[type="week"],
    body input[type="number"],
    body input[type="email"],
    body input[type="url"],
    body input[type="search"],
    body input[type="tel"],
    body input[type="color"] {
        background-color: #232323;
        border: 1px solid #000000;
    }
    body input[type="text"]:focus,
    body input[type="password"]:focus,
    body input[type="datetime"]:focus,
    body input[type="datetime-local"]:focus,
    body input[type="date"]:focus,
    body input[type="month"]:focus,
    body input[type="time"]:focus,
    body input[type="week"]:focus,
    body input[type="number"]:focus,
    body input[type="email"]:focus,
    body input[type="url"]:focus,
    body input[type="search"]:focus,
    body input[type="tel"]:focus,
    body input[type="color"]:focus {
        border-color: #656565;
        box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.3), 0 0 8px #656565;
    }
    div.ac-wrap {
        background-color: #232323;
        border: 1px solid #000000;
    }
    body input[type="text"]:focus,
    body input[type="password"]:focus,
    body input[type="datetime"]:focus,
    body input[type="datetime-local"]:focus,
    body input[type="date"]:focus,
    body input[type="month"]:focus,
    body input[type="time"]:focus,
    body input[type="week"]:focus,
    body input[type="number"]:focus,
    body input[type="email"]:focus,
    body input[type="url"]:focus,
    body input[type="search"]:focus,
    body input[type="tel"]:focus,
    body input[type="color"]:focus {
        border-color: #232323;
        box-shadow: inset 0 1px 1px rgb(35, 35, 35), 0 0 8px #353535;
    }
    .select2-drop {
        background: #191919;
    }
    .select2-results .select2-highlighted {
        background: #4c4c4c;
    }
    .category-combobox .topic-count,
    .select2-drop .topic-count {
        color: #ffffff;
    }
    .select2-container a.select2-choice {
        background: #232323;
        border-radius: 3px;
        border-color: #191919;
        color: #A9A9A9;
    }
    .select2-container {
        border: 1px solid #191919;
    }
    .select2-results .select2-no-results,
    .select2-results .select2-searching,
    .select2-results .select2-selection-limit {
        background: #191919;
        color: #A9A9A9;
    }
    .select2-container-multi .select2-choices {
        background-color: #232323;
        border-color: #191919;
    }
    .btn-primary {
        color: #fff!important;
        background: #191919!important;
    }
    a:visited {
        color: #616161;
    }
    h2 a {
        color: #A9A9A9;
    }
    .DiFw- {
        background-color: #131513;
        color: #fdfdfd!important;
        border: 2px solid #131513!important;
    }
    #reply-control {
        background-color: #000000;
    }
    #reply-control .wmd-controls .d-editor-input,
    #reply-control .wmd-controls .d-editor-preview {
        background-color: #232323;
    }
    #reply-control .d-editor-button-bar {
        color: #A9A9A9;
        background-color: #151515;
    }
    #reply-control .d-editor-button-bar {
        border-bottom: 2px solid #151515;
    }
    .d-editor {
        border: 1px solid #000000;
    }
    #reply-control .contents .d-editor-preview {
        border: 1px dashed #000000;
    }
    #reply-control.draft {
        border-top: 1px solid #5d5d5d;
    }
    .d-editor .d-editor-modal {
        background-color: #000;
        border: 1px solid #464646;
    }
    .modal-inner-container {
        background-color: #232323;
        border: 1px solid #000000;
    }
    .modal .modal-body label,
    .upload-selector .radios .inputs input[type="file"] {
        color: #A9A9A9;
    }
    .popup-menu {
        background-color: #232323;
        border: 1px solid #383838;
    }
    div.poll li[data-poll-option-id],
    .user-main .user-stream .excerpt {
        color: #A9A9A9;
    }
    .user-main .user-stream .item {
        background-color: #232323;
        border-bottom: 1px solid #424242;
    }
    .user-main .about .details {
        background: rgb(35, 35, 35);
    }
    .user-main .about .secondary {
        background: #191919;
        border-top: 1px solid #4a4a4a;
        border-bottom: 1px solid #4a4a4a;
    }
    .user-main .about .secondary dd {
        color: #ffffff;
    }
}
