css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lestoilesheroiques.fr") {
  #nav{
    width: 100%;
    margin: auto;
    background-color: #2980b9;
    position: fixed;
    height: 50px;
    top: 0;
    z-index: 500010;
  }
  
  #nav-content{
    width: 60%;
    margin: auto;
  }
  
  ul#menu-menu > li:nth-child(1){
    background-color: #FFF!important;
  }
  
  ul#menu-menu > li > a{
    line-height: 50px;
    padding: 0px 8px!important;
  }
  
  ul#menu-menu > li:nth-child(1) > a{
    visibility: hidden;
    text-transform: uppercase;
    color: #2980b9;
    text-indent: -75px;
    font-size: 18px;
  }
  
  ul#menu-menu > li:nth-child(1) > a:hover{
    color: #2980b9;
    background-color: #ecf0f1;
  }
  
  ul#menu-menu > li:nth-child(1)> a::after{
    visibility: visible;
    content: "les toiles héroiques";
  }
  
  #wrapper{
    background: none!important;
    background-color: #FFF!important;
    margin-top: 50px;
  }
  
  #main{
   width: 60%;
   padding: 0;
   border: 0;
   background-color: #FFF;
  }
  
  ul.hfeed > li{
    padding: 0x!important;
  }
  
  .posts-quick .entry-thumbnails{
    border: none;
    padding: 0px!important;
    margin: 0 10px 0 0!important;
  }
  
  .posts-quick .entry-thumbnails div a img{
    height: 170px;
    width: 170px;
  }
  
  ul.hfeed > li:nth-child(2n){
    background-color: #ecf0f1;
  }
  
  .home-title{
    display: none;
  }
  
  #top-menu,
  #header,
  #main > a:nth-child(1),
  .featured,
  .quick-nav,
  #primary{
    display: none!important;
  }
  
  #content,
  #container{
    width: 100%;
  }
  
  .posts-quick .entry-summary{
    font-size: 16px;
    padding-right: 15px;
  }
  
  #nav .sf-menu{
    max-width: none;
  }
  
  #nav .sf-menu a{
    text-transform: uppercase;
  }
  
  .entry-content > strong,
  .my-related-posts-box{
    display: none;
  }
  
  .single .post, #main .single-post{
    border: none;
  }
  
  .entry-content > p > iframe{
    display: block;
    margin: auto;
  }
  
  .posts-quick .entry-thumbnails img{
    width: 170px;
    height: 170px;
  }
  
  #bottom-content-1,
  #bottom-content-2{
    float: right;
  }
  
  #commentlist .reply a, .comments-navigation a, .read-more, .navigation a, .wp-pagenavi a{
    background: none;
    background-color: #2980b9 !important;
    border: none;
    border-radius: 2px;
    color: #fff !important;
    display: inline-block;
    margin: 0;
    padding: 15px!important;
  }
}
