.ipsNavBar_primary {
    position: relative;
    padding: 5px 5px 40px 5px;
    z-index: 2000;
    background: #25252b;
    border-radius: 2px 2px 0px 0px;
}
.ipsNavBar_secondary {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 40px;
    z-index: 1000;
    display: none;
    background: #1E1E20;
}
.ipsNavBar_secondary > li.ipsNavBar_active a {
    font-weight: bold;
    border-color: rgba(51, 51, 51, 0.3);
    color: #AAAAA8;
}
#ipsLayout_contentWrapper {
    width: 100%;
    background-color: #1B1B1D;
}
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
    background-color: #222225;
}
.ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.5;
    color: #989898;
}
.ipsLikeRep {
    background: #1B1B1D;
    border-radius: 2px;
    padding: 3px;
    line-height: 16px;
    display: inline-block;
    position: relative;
}
.ipsType_pageTitle {
    font-size: 26px;
    font-weight: 400;
    line-height: 1.2;
    margin: 0;
    color: #e4e4e4;
}
.ipsAreaBackground_light {
    background: #151515;
}
a.ipsType_blendLinks,
.ipsType_blendLinks a {
    color: #e4e4e4;
}
.ipsReputation_count {
    background: #313131;
}
.ipsQuote_citation {
    background: #1d1d1d;
    margin: 0px -15px;
    padding: 5px 15px;
    color: #c7c7c7;
    font-weight: bold;
    font-size: 13px;
    display: block;
}
.ipsQuote .ipsQuote_citation a {
    color: #677892;
    text-decoration: none;
}
.ipsQuote {
    margin: 0;
    padding: 0px 15px;
    border-width: 1px 1px 1px 2px;
    border-color: #2b2b2b #383838 #1b1b1b #2b2b2b;
    border-style: solid;
    position: relative;
    background: #252525;
    clear: both;
}
.ipsAreaBackground {
    background: #252525;
}
.ipsComposeArea_editor {
    background: #3e3e3e;
    position: relative;
    padding: 1px;
    border-collapse: collapse;
}
html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor:before {
    right: 100%;
    border-color: transparent #3e3e3e transparent transparent;
}
.ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    font-weight: 400;
    margin-bottom: 10px;
    background: #131313;
    padding: 10px;
}
.ipsAreaBackground_reset {
    background: #191919;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title {
    color: #dcdcdc;
    background: #101010;
}
.ipsNavBar_primary:not( .ipsNavBar_noSubBars) > ul:before {
    background: #1b1b1d;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even) {
    background: #131313;
}
.ipsWidget .ipsTabs_small .ipsTabs_item:not( .ipsTabs_activeItem) {
    color: rgba(175, 175, 175, 0.6);
    border-bottom: 1px solid transparent;
}
.ipsType_sectionTitle {
    background: #252525;
    color: #ffffff;
}
.ipsCommentCount {
    padding: 5px 10px;
    border-radius: 3px;
    background: #191919;
    display: inline-block;
    font-size: 12px;
    text-transform: uppercase;
    position: relative;
    text-align: center;
    line-height: 15px;
    color: #c3c3c3;
    margin-bottom: 3px;
}
html[dir="ltr"] .ipsCommentCount:after {
    right: 100%;
    border-color: transparent #191919 transparent transparent;
}
.ipsNavBar_secondary > li > a {
    color: rgba(158, 158, 158, 0.6);
    font-size: 13px;
    padding: 12px 18px 8px;
    border-bottom: 2px solid transparent;
}
.ipsNavBar_primary {
    position: relative;
    padding: 5px 5px 40px 5px;
    z-index: 2000;
    background: #171717;
    border-radius: 2px 2px 0px 0px;
}
.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #2d2d2d;
    color: #7b7b7b;
}
.ipsTabs_panel {
    height: 100%;
    background: #131313;
}
.ipsTabs_activeItem {
    background: #2b2b2b;
    color: #b9b9b9;
    font-weight: bold;
}
a {
    color: #737b84;
    text-decoration: none;
}
.ipsFollow .ipsCommentCount {
    background: #222225;
    color: #e4e4e4;
}
html[dir="ltr"] .ipsFollow .ipsCommentCount:after {
    border-color: transparent #222225 transparent transparent;
}
html[dir="ltr"] .ipsCommentCount:after {
    right: 100%;
    border-color: transparent #191919 transparent transparent;
}
.ipsApp .ipsButton_light {
    background: #333333;
    border-color: rgba(0, 0, 0, 0.03);
    color: #333333;
}
.cChatRow .cChatRow_user,
.cChatRow .cChatRow_content,
.cChatRow .cChatRow_date {
    display: table-cell;
    padding: 10px;
    border-bottom: 1px solid #252525;
    vertical-align: top;
}
.ipsApp .ipsField_autocomplete,
.ipsApp textarea,
.ipsApp input[type="text"],
.ipsApp input[type="password"],
.ipsApp input[type="datetime"],
.ipsApp input[type="datetime-local"],
.ipsApp input[type="date"],
.ipsApp input[type="month"],
.ipsApp input[type="time"],
.ipsApp input[type="week"],
.ipsApp input[type="number"],
.ipsApp input[type="email"],
.ipsApp input[type="url"],
.ipsApp input[type="search"],
.ipsApp input[type="tel"] {
    background-color: #3a3a3a;
    border-width: 1px;
    border-style: solid;
    border-color: #2d2d2d #2d2d2d #2d2d2d #2d2d2d;
    border-radius: 3px;
    width: 100%;
    max-width: 350px;
    padding: 7px;
}
#elChatTabs {
    background: #171717;
}
.ipsMenu_headerBar {
    border-bottom: 1px solid #4e4e4e;
    box-shadow: 0px 1px 6px rgba(0, 0, 0, 0.07);
    border-radius: 3px 3px 0px 0px;
}
.ipsMenu_headerBar,
.ipsMenu_footerBar {
    background: linear-gradient(to bottom, rgb(19, 19, 19) 0%, rgb(0, 0, 0) 100%);
}
.ipsType_sectionHead {
    font-size: 20px;
    color: #b9b9b9;
    line-height: 24px;
    font-weight: 400;
    display: inline-block;
    margin: 0;
}
.ipsMenu {
    background: #1d1d1d;
}
.ipsEmoticons_item[data-emoticon]:hover {
    cursor: pointer;
    background: #101010;
}
.ipsMenu {
    background: #1d1d1d;
    box-shadow: 0px 7px 20px rgba(0, 0, 0, 0.2);
    border: 1px solid #292929;
    border-radius: 3px;
    z-index: 10000;
    position: absolute;
}
.ipsMenu_footerBar {
    border-top: 1px solid #353535;
    box-shadow: 0px -1px 6px rgba(0, 0, 0, 0.07);
    border-radius: 0px 0px 3px 3px;
}
ul:not( .ipsMenu_keyNav) .ipsMenu_item:not( .ipsMenu_itemClicked):not( .ipsMenu_itemDisabled) a:hover,
.ipsMenu_item[data-selected] a,
.ipsMenu_item[data-selected] span,
.ipsMenu_item.ipsMenu_hover {
    background: #191919;
}
.ipsMenu_item > a,
.ipsMenu_item > span {
    display: block;
    padding: 8px 15px;
    color: #949494;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.ipsMenu_item:not( .ipsMenu_itemClicked) a:hover .ipsMenu_itemCount,
.ipsMenu_item:not( .ipsMenu_itemClicked) button:hover .ipsMenu_itemCount {
    background: #040404;
}
.ipsMenu.ipsMenu_bottomCenter:before {
    border-color: transparent transparent #292929 transparent;
    left: 50%;
    margin-left: -16px;
    margin-top: -1px;
    bottom: 100%;
}
.ipsMenu.ipsMenu_bottomCenter:after {
    border-color: transparent transparent #1d1d1d transparent;
    left: 50%;
    margin-left: -15px;
    bottom: 100%;
}
.ipsApp .ipsButton_veryLight {
    background: #1f1f1f;
    border-color: #313131;
    color: #989898;
}
.ipsApp .ipsButton_primary {
    background: #1b1b1b;
    color: #dedede;
}
.ipsApp .ipsButton_light {
    background: #333333;
    border-color: rgba(0, 0, 0, 0.03);
    color: #989898;
}
.ipsMenu_title {
    background: #272727;
    padding: 7px 10px;
    margin: 5px;
    font-size: 12px;
    font-weight: bold;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_inner {
    color: #909090;
}
.ipsDataItem {
    width: 100%;
    position: relative;
    border-width: 0 0 1px 0;
    border-style: solid;
    border-color: #3e3e3e;
}
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread):not( .ipsDataItem_selected):not( .ipsModerated) {
    background: #0a0a0a;
}
.ipsHovercard {
    background: #191919;
    box-shadow: 0px 7px 20px rgba(0, 0, 0, 0.2), 0px 5px 20px rgba(0, 0, 0, 0.1);
    border: 1px solid #ebebeb;
    border-radius: 4px;
    position: relative;
}
#ipsLayout_sidebar {
