css:
@-moz-document domain("extratorrent.cc"), domain("extra.to"), domain("etmirror.com"), domain("etproxy.com"), domain("extratorrentonline.com"), domain("extratorrentlive.com"), domain("extratorrent.works"), domain("extratorrent.life"), domain("extratorrent.one"), domain("extratorrent.site"), domain("extratorrent.ag") {
/* Site Background */
body {
    background: black!important;
    color: grey!important;}

/* Search Logo */
html body table tbody tr td form table tbody tr td.h_search_btn input {
    background: url(https://extraimage.net/images/2016/11/11/7b504edd537db7a56373ae0c8c0d2f92.png) no-repeat!important;
    position: relative !important;
    top: 5px!important;
    height: 0px !important;
    width: 45px !important;
    padding-left: 65px !important;
    padding-top: 45px !important;}

/* First Border */
td.td_menu {
   background:black!important;
   border-radius: 0px;height: 8px;width: 98%!important;
   border: 2px solid grey!important;}

a.menu {color: white!important;}

/* DropDown Menu */
table.tablemenu td {
    border-radius: 0px!important;
    border: 2px solid grey!important;
    background-color: black!important;}

td.tabledata1 {
    border-radius: 8px!important;
    background-color: black!important;
    border: 2px solid white!important;}

/* Second Border */
table.tl th {
    border: 2px solid grey!important;
    border-radius: 8px!important;}

/* Table Data */
td.tabledata1 {
    border: 1px solid grey!important;
    border-radius: 8px!important;
    background-color: black!important;}

table.tl th table tr,table.tl th table tr:hover {background-color: #000000!important;}

a:hover {color: #0cf906!important;}

a {color: #04619c!important;}

div.usr {
   background-color: white;
   border-radius: 4px;
   padding: 1px;}

tr.tlr {color: #beb8a3!important;}

tr.tlz {color: #beb8a3!important;}

tr.tlr {background-color: #303030!important;}

tr.tlz {background-color: #000000!important;}

.leech,.ly {color: red!important;}

.seeds,.sy {color: green!important;}

/* Forum stuff */
table.fm  {border: 0px solid #000000!important;}
table.fm th {background: #1b4e67!important;border-radius: 6px;}
table.fm td {background-color: #131e23!important;}
table.fm td.fme {background-color: #2E383D!important;}
table.fm td.fmo {background-color: #131314!important;}
table.fm td.stp{background:none!important;}
table.fm td div.top, table.fm td.top {background: #021927!important;}

/* Hot Searched Stuff */
div.top_pic img {border: 1px solid #178505!important;}
div.top_torr {background-color: #2E383D;margin: 2px 6px 15px 1px!important;}
div.top_cat {width: 200px;background: none repeat scroll 0 0 white;}
div.menul {background: none!important;width: 0!important;}
div.blog_content {border: 1px solid grey!important;}
div.blog_top {border-radius: 6px 6px 0 0;border: 1px solid grey;padding: 7px 8px 9px 20px!important;background: #101010!important;}

div.chat_msg,td.tabledata0,td.tabledata_num {
   border-radius: 7px!important;
   background-color: #32323b!important;
   border: 1px solid #9d9886!important;}

#MarketGidComposite536,#FFN_IM_Chat_Container,#FFN_IM_Chat,#a18db347,#a4g-floating-ad,
span .grey div a img,
.ads2,
.blog_left,
.blog_right,
.menur,
.top_icon,
img[src="/images/menu_right.gif"],
img[src="/images/menu_left.gif"],
img[src="http://static.extratorrent.cc/images/menu_right.gif"],
img[src="http://static.extratorrent.cc/images/menu_left.gif"],
img[alt="BetExpert.me sport tips"]
{display: none!important;}

#hot_torrents {background: none repeat scroll 0 0 transparent!important;}


a.pager_link {border: 1px solid rgb(1, 1, 1)!important; background-color: #202020!important;}

b.pager_no_link, a.pager_link {border-radius: 6px;}

a img,table {border-radius: 10px 10px 10px 10px;}
}
