css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(medium.com) {

p.graf--p, li.graf--li {
    text-align: justify;
  }

.postArticle--full .sectionLayout--insetColumn {
    max-width: 1200px;
    padding-left: 20px;
    padding-right: 20px;
}

.u-marginLeftNegative100 {
    margin-left: -320px!important;
}

.postArticle-content .graf--p {
    font-family: Georgia,Cambria,"Times New Roman",Times,serif;
    letter-spacing: .01rem;
    font-weight: 400;
    font-style: normal;
    font-size: 16px;
    line-height: 1.58;
}

.graf--li {
    font-family: Georgia,Cambria,"Times New Roman",Times,serif;
    letter-spacing: .01rem;
    font-weight: 400;
    font-style: normal;
    font-size: 16px;
    line-height: 1.58;
    letter-spacing: -.003em;
}

}
