css:
@-moz-document domain(www.tumblr.com) {

body {
font-family: 'Courier New', sans-serif;
background:#272727 url(http://orig04.deviantart.net/7efe/f/2016/123/8/5/vapourwave_by_xwoolfie-da159po.jpg) repeat center fixed! important;
}

div.l-content.l-content.l-content {
background: rgba(255, 255, 255, 0.7) !important;
margin: auto;
padding: 20px 20px;

  }


.l-header-container--refresh {
    box-shadow: 0 0 0 1px hsla(0,0%,100%,.13);
    -webkit-transform: translateZ(0) translateY(0);
    transform: translateZ(0) translateY(0);
    z-index: 90;
    background-color: #e9e6f2;
    position: fixed;
    width: 100%;
    opacity: .3%
}

.post_full .post_wrapper, div.post_footer.clearfix, .post_full .post_body, .post.post_full, .post_full .post_tags:after, .right_column, .controls_section li, .identity .controls_section.user_list li .follow_list_item_blog:before, .follow.plus-follow-button:hover, .post.post_full .post_content, .post_full.is_note .post_body .note_item {
background: #b1bff6!important;
border: 1px !important;
border-radius: 5px;
}
.note_item{
background: black
}
  
#return_to_top .return_to_top_icon{
background: url('http://i64.tinypic.com/10yf7s4.png') no-repeat transparent!important}

.icon_post_text:before,
.icon_post_photo:before,
.icon_post_quote:before,
.icon_post_link:before,
.icon_post_chat:before,
.icon_post_audio:before,
.icon_post_video:before {
color: white;    
}

.post_avatar {
  background-color: transparent !important;
}
.post_avatar_link, .post_sub_avatar {
  -webkit-border-radius:50% !important;
  -moz-border-radius:50% !important;
  border-radius: 50% !important;
}

  
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

#left_column, #right_column {
  background-color: transparent;
}
DIV.post_avatar {background-color: transparent}
}
