.anime-vignette {
    width: 100%;
    height: 100%;
    position: relative;
    overflow: hidden
}

.anime-vignette img {
    width: 100%;
    height: 100%
}

.anime-vignette .label {
    position: absolute;
    left: 10px;
    bottom: 20px;
    z-index: 100;
    font-size: 14px;
    color: #fff
}

.anime-vignette .titre {
    display: inline-block;
    max-width: 125px;
    padding: 7px 10px;
    overflow: hidden;
    border-radius: 4px;
    background: #E74C3C;
    font-size: 14px;
    white-space: nowrap;
    text-overflow: ellipsis
}

.anime-vignette .statut .finish,.anime-vignette .statut .licencie,.anime-vignette .statut .new {
    position: absolute;
    padding: 1px 0;
    font-size: 14px;
    color: #fff;
    z-index: 5;
    text-align: center
}

.anime-vignette .titre.episode {
    background: #E74C3C
}

.anime-vignette .titre.oav {
    background: #4AA1DA
}

.anime-vignette .titre.film {
    background: #64B22E
}

.anime-vignette .statut .finish {
    top: 20%;
    left: 35%;
    width: 100%;
    background: #E74C3C;
    transform: rotate(45deg);
    -webkit-transform: rotate(45deg)
}

.anime-vignette .statut .new {
    top: 15%;
    left: 40%;
    width: 100%;
    background: #64B22E;
    transform: rotate(45deg);
    -webkit-transform: rotate(45deg)
}

.anime-vignette .statut .licencie {
    bottom: -23%;
    right: -23%;
    width: 50%;
    height: 50%;
    background: #e67e22;
    transform: rotate(-45deg);
    -webkit-transform: rotate(-45deg)
}

#slideshow {
    width: 100%;
    height: 280px;
    position: relative;
    overflow: hidden;

        filter: brightness(70%);
        -webkit-filter: brightness(70%);
        -moz-filter: brightness(70%);
        -o-filter: brightness(70%);
        -ms-filter: brightness(70%);
}

#slideshow .slide {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-size: cover
}

#slideshow .slide>* {
    position: absolute;
    opacity: 0
}

.last-episodes {
    position: relative;
    width: 100%;
    overflow: hidden;
	Background-color: rgba(0, 0, 0, 0.5) 
}

.last-episodes-nav {
    position: relative;
    width: 100%;
    height: 48px;
    background: #202020;
    color: #D0D0D0;
    text-align: center;
    font-size: 24px
}

.last-episodes-nav .display-mode i.active,.last-episodes-nav .display-mode i:hover,.last-episodes-nav .navigation i:hover {
    background: #000000;
	color: #e67e22
}

.last-episodes-nav i {
    display: inline-block;
    padding: 12px;
    cursor: pointer
}

.last-episodes-nav .display-mode,.last-episodes-nav .display-mode i {
    float: left
}

.last-episodes-nav .navigation {
    display: inline-block;
    position: absolute;
    left: calc(50% - 170px)
}

.last-episodes-nav .navigation i {
    padding: 12px;
    float: left
}

.last-episodes-nav .navigation i.off:hover {
    background: 0 0
}
.last-episodes-nav .navigation i.on:hover {
    color: #e67e22
}

.last-episodes-nav .navigation .nav-date {
    display: inline-block;
    width: 250px;
    float: left
}

.last-episodes-nav .navigation .nav-day {
    display: none;
    font-weight: 700;
    line-height: 48px
}

.last-episodes-nav .navigation .nav-day.active {
    display: block
}

.last-episodes-nav .navigation i.on {
    color: #D0D0D0
}

.last-episodes-nav .navigation i.off {
    color: #202020;
    cursor: default
}

.last-episodes-nav .historique {
    display: inline-block;
    float: right;
    color: #D0D0D0;
    font-size: 20px;
    line-height: 48px;
    padding-right: 10px
}

.last-episodes-nav .historique:hover {
