    color: #888888;
    font-size: 14px;
    padding-bottom: 12px
}

.fiche-anime .infos-item label {
    display: inline-block;
    width: 60px;
    float: left;
    color: #D0D0D0
}

.fiche-anime .infos-item.licencie {
    display: inline-block;
    float: left;
    background-color: #E74C3C;
    line-height: 20px;
    padding: 5px 10px;
    color: #fff
}

.fiche-anime .list-genre {
    min-width: 170px;
    border: none;
    color: #D0D0D0
}

.fiche-anime .resume {
    margin: 15px;
    font-family: Scada;
    font-size: 15px;
    font-weight: 400;
    text-align: justify;
    clear: both;
    padding-top: 30px;
	color: #D0D0D0
}

.fiche-stream-content {
    margin: 15px 0 15px 15px;
    padding: 0
}

.fiche-player-content {
    min-height: 100%;
    Background: rgba(0, 0, 0, 0.5)
}

.fiche-navigation {
    margin-bottom: 10px
}

.fiche-navigation a {
    display: inline-block;
    width: 150px;
    height: 35px;
    line-height: 35px;
    background: #0D70A1;
    color: #fff;
    text-align: center;
    cursor: pointer
}

.fiche-navigation .prev {
    float: left
}

.fiche-navigation .next {
    float: right
}

.fiche-streaming {
    width: 100%;
    margin: 15px 0 15px 5px;
    float: right;
    background: transparent
}

.fiche-streaming .streaming-list {
    max-height: 500px;
    overflow: hidden
}

.fiche-streaming .streaming-title {
    height: 35px;
    padding-left: 10px;
    text-transform: uppercase;
    font-size: 14px;
    background-color: #000000;
    color: #D0D0D0;
    line-height: 35px
}

.fiche-streaming .ddl .streaming-title {
    background-color: #000000!important;
}

.fiche-streaming .active .streaming-title {
    background-color: #000000;
	color: #e67e22
	
}

.fiche-streaming .streaming-title:hover {
    cursor: pointer;
	color: #e67e22;
	background-color: #000000
}

.fiche-streaming .streaming-item {
    display: none
}

.fiche-streaming .active .streaming-item {
    display: block;
    border-bottom: 1px solid #eee
}

.fiche-streaming .streaming-item a {
    display: block;
    height: 35px;
    padding-left: 20px;
    color: #D0D0D0;
    line-height: 35px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis
}

.fiche-streaming .streaming-item a:hover {
    background-color: #000000;
    color: #E67E22
}

.fiche-streaming .streaming-item a.active {
    color: #E67E22
}

.fiche-player {
    padding-bottom: 10px
}

.fiche-player-block {
    position: relative;
    margin-bottom: 10px
}

.fiche-player-block .iframe-ratio {
    display: block;
    width: 100%;
    height: auto
}

.fiche-player-block iframe {
    position: absolute;
    top: 10px;
    left: 10px;
    width: calc(100% - 20px);
    height: 100%
}

#season {
    padding: 25px
}

#season a.origin {
    margin-bottom: 40px;
    color: #2C3E50;
    display: block;
    text-align: center;
    font-size: 26px
}

#season h2 {
    font-size: 16px;
    margin-bottom: 4px;
    background-color: #2C3E50;
    padding: 8px;
    color: #fff;
    text-align: center
}

#season table {
    float: left;
    font-size: 14px;
    margin: 10px 5px;
    width: calc(33% - 10px);
    background: #F2f2f2
}

#season th {
    background: #F2F2F2;
    border-bottom: 2px solid #FFF;
    height: 30px;
    padding-left: 3px
}

#season th.titre {
    background-color: #0D70A1;
    border: 0;
    text-align: left;
    font-family: Scada
}

#season th.titre a {
    color: #fff;
    font-weight: 400
}

#season a {
    color: #0D70A1;
    font-weight: 700
}

#season td {
    background: #DCDCDC;
    padding-left: 5px;
    text-align: justify
}

#season span.date,#season span.editeur {
    height: 15px;
    padding-bottom: 2px;
    padding-right: 5px;
    text-align: right;
    font-weight: 700;
    font-family: Scada
}

#season td.affiche {
    padding-top: 5px
}

#season td.histoire {
    padding-right: 5px;
    padding-top: 5px;
    vertical-align: top;
    font-family: Scada
}

#season span.origine {
    color: #83A558;
    font-weight: 700;
    height: 15px;
    font-family: Scada
}

#season span.editeur {
    color: #58537C;
    float: right
}

#season span.studio {
    color: #D53453;
    float: left;
    font-weight: 700;
    height: 15px;
    padding-bottom: 2px;
    font-family: Scada
}

#season span.date {
    color: #58537C;
    float: right
}

.partenaires {
    padding: 25px
}

.partenaires h1 {
    color: #e67e22;
	padding: 0 15px;
	background: #000000;
	height: 40px;
	line-height: 40px;
    margin: 30px 0;
    font-size: 24px
}

.partenaires .msg-partenaires {
    margin: 30px 0;
    padding: 15px;
    font-size: 13px;
    background: rgba(0,0,0,.5);
	color: #D0D0D0
}

.magazine {
    padding: 10px 5px
}

.magazine h2 {
    margin: 20px;
    font-size: 34px;
    color: #0D70A1;
    font-weight: 700
}

.magazine .mag-item {
        filter: brightness(70%);
        -webkit-filter: brightness(70%);
        -moz-filter: brightness(70%);
        -o-filter: brightness(70%);
        -ms-filter: brightness(70%);
    margin: 10px 3%;
    float: left
}

.magazine .mag-item h3 {
    width: 100%;
    height: 35px;
    background: #fff;
    text-align: center;
    color: #0D70A1;
    font-weight: 400;
    line-height: 35px
}

.book img {
    width: 100%
}

#commentaires {
    background: transparent;
    border-top: 1px solid #DDD;
    padding: 10px 10px 50px;
    margin-bottom: -30px
}

#commentaires .nb_com {
    font-size: 20px;
    margin-bottom: 20px;
    color: #e67e22;
    font-variant: small-caps;
    padding: 10px;
    font-weight: 700
}

#commentaires form {
    margin: 0 25px 25px
}

#commentaires form p {
    display: inline-block;
    width: calc(50% - 10px);
    margin: 5px 0
}

#commentaires form p.com,#commentaires form p.valid {
    width: 100%
}

#commentaires form p.valid input {
    float: right
}

#commentaires form input {
    width: calc(100% - 40px);
    padding: 5px 10px
}

#commentaires form input[type=submit] {
    width: 150px;
    height: 45px;
    color: #D0D0D0;
    background: #000000;
    cursor: pointer
}

#commentaires form input[type=submit]:hover {
    color: #e67e22;
    background: #000000;
}

#commentaires form textarea {
    width: calc(100% - 40px);
    border: none;
    border: 1px solid #000000;
    height: 150px;
    padding: 5px 10px;
	background: rgb(32,32,32);
	border-radius: 5px;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
}

#commentaires form span.error {
    margin-left: 10px;
    display: inline-block;
    color: red;
    font-size: 12px;
    font-style: italic
}

#commentaires .comment {
    margin: 0;
    padding: 15px 10px;
    border-top: 1px solid #eee
}

#commentaires .comment .avatar {
    float: left;
    margin-right: 30px
}

#commentaires .comment .message {
    width: calc(100% - 150px);
    float: left;
    padding: 5px 10px;
	background: rgb(32,32,32);
	border-radius: 5px;
	border:1px solid #000000;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
}

#commentaires .comment .author {
    margin-bottom: 10px
}

#commentaires .comment .author .pseudo {
    font-weight: 600;
    font-size: 16px;
	color: #e67e22
}

#commentaires .comment .author .date {
    font-size: 13px;
    font-weight: 300;
    font-style: italic;
	color: #888888
}

#commentaires .comment .content {
    font-weight: 300;
    font-family: Scada;
    text-align: justify;
	color: #D0D0D0
}

#commentaires .comment .reponse {
    width: calc(100% - 150px);
    float: left;
    padding: 5px 10px;
	background: rgb(32,32,32);
	border-radius: 5px;
	border:1px solid #000000;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
}

#commentaires p.reponse.error,#commentaires p.reponse.valid {
    width: 600px;
    border-radius: 4px;
    color: #fff;
    cursor: pointer;
    padding: 20px;
    margin: auto;
    text-align: center
}

#commentaires p.reponse.valid {
    background: #71B21F
}

#commentaires p.reponse.error {
    background: #E9322C
}

@media screen and (max-width: 1150px) {
    .list-type-img .anime-vignette .statut .licencie {
        font-size:12px
    }
}

@media screen and (max-width: 979px) {
    .pub-trailer {
        height:300px
    }

    .list-type-img {
        width: calc(100% - 10px)
    }

    .list-type-img .titre {
        max-width: 100px
    }

    .list-type-img .last-episode {
        width: calc(33% - 10px)
    }

    .list-type-img .last-episode .ep {
        max-width: 40px
    }

    .list-news .actu:last-child {
        display: inline-block
    }

    .anime-item .anime-vignette {
        display: none
    }

    .anime-item .anime-informations {
        width: 100%
    }
}

@media screen and (max-width: 759px) {
    .last-episodes-nav .display-mode,.slideshow {
        display:none
    }

    .last-episodes-nav .navigation .nav-date {
        width: 170px
    }

    .list-type-text .label {
        width: calc(100% - 200px)!important;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis
    }

    .list-type-text .statut {
        width: 100px!important
    }

    .list-type-text .statut p {
        width: 10px!important;
        padding: 0 10px!important
    }

    .list-type-text .statut p span {
        display: block;
        visibility: hidden
    }

    .list-type-text .statut p span:first-letter {
        visibility: visible
    }

    .list-type-text .action {
        width: 100px
    }

    .anime-filtre .more-filters span {
        display: none
    }

    .anime-filtre .advanced-filtre p.checkbox:last-child {
        display: block;
        float: none
    }
}

@media screen and (max-width: 559px) {
    .anime-filtre .auto,.last-episodes-nav .historique span {
        display:none
    }

    .anime-item,.anime-item .anime-informations {
        height: auto
    }

    .list-type-text {
        position: relative
    }

    .list-type-text .label {
        width: calc(100% - 200px)!important
    }

    .anime-filtre .valid {
        display: inline-block!important
    }

    .anime-item .anime-titre {
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis
    }

    .anime-item .anime-informations .anime-genre,.anime-item .anime-informations .anime-infos {
        height: auto!important;
        margin-bottom: 5px
    }

    .anime-item .anime-informations .anime-episodes {
        clear: both;
        height: 30px;
        padding-top: 2px;
        border-top: 1px solid #ddd
    }

    .anime-item .anime-informations .anime-episodes p {
        display: inline-block;
        width: 30%;
        float: left
    }

    .anime-item .anime-informations .anime-episodes p:first-child {
        width: 40%
    }

    .anime-filtre .advanced-filtre p:not(.checkbox) {
        display: block;
        float: none;
        clear: both
    }

    .anime-filtre .advanced-filtre p.checkbox:last-child {
        display: block;
        float: none
    }
}

@media screen and (max-width: 449px) {
    .list-type-text .label {
        width:100%!important
    }

    .list-type-text .titre {
        font-size: 16px
    }

    .list-type-text .last-episode:hover .statut {
        position: absolute;
        display: block;
        width: 50%!important;
        text-align: center;
        background: rgba(0,0,0,.7);
        top: 0
    }

    .list-type-text .last-episode:hover .action {
        position: absolute;
        display: block;
        left: 50%;
        width: 50%;
        text-align: center;
        background: rgba(0,0,0,.7);
        top: 0
    }

    .list-type-text .action i {
        color: #fff!important
    }

    .list-type-text .action i.fa-file-text-o {
        display: inline-block
    }

    .panel-anime.anime-saison {
        margin: 20px 0!important
    }

    .pub-trailer {
        height: 200px
    }

    .anime-top,.last-anime {
        display: none
    }
}

}
