css:
@-moz-document domain("orf.at") {
/* Theme: orf.at

firstaltcolor: #3B3B3B
secondaltcolor: #2A2A2A
thirdaltcolor: #212121 (ie: Forum categories)
bordercolor: #464646
text: #DFDFDF

  */

.ss.news .content.front,
.ss.news .networkNavigation,
.ss.news .content,
.ss .storybox,
.ss #content,
.ss .frontHeader,
.ss #networknavigation,
.ss .content,
.griditem.linegrid,
.ss.ooe .header, .ss.tir .header, .ss.vbg .header, .ss.ktn .header, .ss.sbg .header,
.ss.stm .header,
.ss.noe .header,
.ss.wie .header,
.ss.bgl .header,
.ss .spacerDiv,
.ss .sliderNavigation .whiteSpacer,
.ss .lastUpdateMessage,
.ss.wetter .storyText,
.ss .storyWrapper,
.ss .storyList.site .storyListItem.expanded,
.ss .storyList.site .storyListItem,
.ss #faq,
.ss.wetter .networkNavigation,
.ss.sport .networkNavigation {
    background: #3B3B3B !important;
}
.ss.news,
.ss #activeItem,
body.ss,
.ss.sport,
.ss #header,
.ss .header {
    background: #2A2A2A !important;
}
.ss.news .pageFooter,
.ss.news .sitemap .footer,
.ss #footer,
.ss h1.storylistSeparator,
.griditem.textimage,
.ss .storyText table tr.even,
.ss .forecast table .legendCol,
.ss .forecast table .dayCol,
.ss .forecast .temperatureRow, .ss .forecast .iconRow.windRow span,
.ss .storyText .forecast td, .ss .forecast td,
.ss.night .regionNavigation,
.ss.night .sublevelList, .ss.night .sublevelList li,
.ss.night .mapNavigation li div,
.ss .storyList.site .storyBox hr+h2,
.ss.news .sitemapMenu.active,
.ss.news .sitemap .background {
    background-color: #212121 !important;
}
.ss #activeItem.tickerBox {
    border: 2px solid #3B3B3B;
}
.ss,
.ss .ticker #activeItem h2 a,
.ss.news .networkNavigation a,
.ss .ticker .topic,
.ss.news .pageFooter a,
.ss .storyText h1, .ss .storyText h1 p,
body.ss,
.ss .ss-nickname,
.ss a:link,
.griditem.linegrid .storyBox,
.griditem.textimage .storyBox p,
.ss.wetter,
.ss .mapNavigation li div.submenue>div a, .ss.night .mapNavigation li div.submenue>div a,
.ss .mapNavigation li.subvisible a, .ss .mapNavigation li.active a {
    color: #DFDFDF;
}
.ss .banner.ticker {
    display: none !important;
}
.ss .storyWrapper.teaser .subgrid .griditem img {
    border-bottom: 7px solid #3b3b3b;
}

.griditem.linegrid .storyBox,
.ss.news .networkNavigation,
.ss .storyList.site .storyListItem,
.ss #networknavigation {
    border-bottom: 5px solid #2A2A2A;
}
.ss.night .mapNavigation li {
    background: #212121;
    border-color: #2A2A2A;
}

.ss .storyListItem .storyBox,
.ss.news.oes .storyList.site .storyListItem:first-child .storyBox,
.ss #networknavigation {
    border-top: 5px solid #2A2A2A;
}

.ss .wrapper {
    background: none !important;
}
}
