css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.forocoches.com") {

  
  /* 
  gris fondo : rgb(36,40,46)
  gris elemt : rgb(26,29,34)
  sin notif :  (17,20,26)
  notificaci : rgb(253,192,47)
  hipervinc : rgb(252,88,48)
  */
  
  
  /* GENERAL */
  div, li, span, button, a
  {
    background : transparent!important;
    border-color : #444!important;
    border : none!important;
  }
      

  ul 
  {
    background : #333!important;
    
    border-color : #444!important;
    border : none!important;
    color : grey!important;
  }
  
  /* Titulo foro/hilo */
  div.title-forum-display-destacado
  {
    background : #333!important;
  }
  
  
  
  
  
  /* fondo de comentarios y barra de notifiaciones */
  .page-margin ol, .page-margin li.link
  {
    border: none!important;

  }
  
  /* notificaciones */
  div[style="text-align:left"]>
  div[style="padding:0px 6px 0px 4px"]>ul.ul2
  {
    background : #111!important;
    /*position : fixed!important;
    bottom: 0!important;
    left: 0!important;
    margin : 0!important;*/
    border-radius : 0!important;
    text-align: center!important;
  }

  ul.ul2[style="background-color:#ffffcc;"] *
  {
    color : black!important;
    background : #dbe206!important;
    background: rgb(253,192,47)!important;/**/
  }
  
  
    /* caja de búsqueda */
  div.mobiletitle input[type="text"],
  div.mobiletitle input[type="password"] 
  {
   padding: 5px!important;
   border: none!important;
  }
  
  /* nombre hilo*/
  ul img+a 
  {
    color : #ccc!important;
  }
 
  
  /* cabecera fija */  

  div.mobiletitle
  {
    background : #000!important;
  }
  
  span.title-forum
  {
    color : #dbe206!important;
    
    color : rgb(253,192,47)!important;/**/
  }
 
  table, fieldset, textarea
  {
    background : #333!important;
    color : #eee!important;
  }
  
  td, tr {
    background : transparent!important;
  }
  
  input[type=submit] 
  {
    background : #222!important;
    
    color : white!important;
   
   
  }
  
  ul
  {
    border-radius: 0;
  }
  
  ul li
  {
    border-bottom : 1px solid #222!important;
  }
  
  ul a
  {
    color : #aaa!important;
  }
  ul div.asmvinfo a
  {
    color : #888!important;
  }
  
  body 
  {
    background : #222!important;
    
  }
  
  /* botones */
  a.mobile_button, a.mobilebuttonbottom 
  {
    background: #111!important;
    text-transform: uppercase!important;
    font-size: 11px!important;
  }
  
    a.mobile_button:hover
  {
    color: white!important;
    background: #000!important;
  }
  
  /* COMENTARIOS */
  
  /* cabecera comentario */
  div.posthead 
  {
   background : #444!important;  
    
  }
  
  
  li.link 
  {
   /*background : #333!important;  */
    color : #eee!important;
  }
  
  /* citas */
  div.pre-quote
  {
    background : #444!important;
  }
  div.post-quote
  {
    background : #555!important;
    
  }
  
  a:hover 
  {
    color : #00876c!important;
    color : rgb(252,88,48)!important;/**/
  }
  
  /* comentario destacado*/
  /*op*/
  ul[style="border-left: 3px solid #ca3415; border-right: 3px solid #ca3415;"]
  {
   background : #333!important;
   border-left : 2px solid #a50000!important;
  }
  /*usuario*/
  ul[style="border-left: 3px solid #5591cc; border-right: 3px solid #5591cc;"]
  {
   background : #333!important;
   border-left : 2px solid #3d99b5!important;
  }
  
  /* spoiler */
  div.spoiler
  {
    background: #999!important;
    color: #999;
  }
  
  span[style="color:#afafaf; font-weight:normal;"]
  {
    color : #3d6eff!important;
  }
 
}
