css:
@-moz-document url-prefix("https://moodle.cis.fiu.edu/") {


html,
body {
    background-color: #002d61;
}
/* HEADER */

.navbar-inner,
#page-footer {
    background-color: #252526;
    color: #7F8C8D;
    text-shadow: none;
    text-transform: capitalize;
}
.navbar .nav > li > a {
    color: #7F8C8D;
}
.usermenu .moodle-actionmenu .toggle-display .userbutton .usertext {
    color: #7F8C8D;
}
/* PAGE */

p,
a {
    color: #E0DDF0;
}
.pagelayout-mydashboard #region-main,
.block .header .title h2,
.coursebox,
.coursebox a,
div.content,
.input-block-level,
.course-content,
.course-content p,
.course-content h6,
div.summary ul#tetras li,
h1,
select,
.pagelayout-incourse #region-main,
.generaltable tbody tr > th,
.generaltable tbody tr > td,
.generaltable tbody > tr:nth-child(2n+1) > td,
.path-mod-assign td.submissionnotgraded,
.path-mod-assign td.earlysubmission,
.jstree-default .jstree-hovered,
ul.jstree-children a,
div#fitem_id_categorylist,
.userprofile .profile_tree section,
table.flexible tbody > tr:nth-child(2n+1) > td,
.generaltable tbody > tr:nth-child(2n+1) > td,
.que .formulation,
.que .info,
pre {
    background-color: #2D2D30;
    color: #E0DDF0;
    text-shadow: none;
}
/* Mods */

#content {
    background-color: #2d2d30;
    border-radius: 0px;
}
.loginbox .loginsub {
    border: 2px solid #d1a502;
    background-color: #2d2d30;
}
#content h1 {
    text-align: left;
}
header {
    background-color: #2d2d30;
    margin-bottom: 15px;
}

#page-footer {
    border-top: 2px solid #d1a502;
}

.page-context-header {
    border-top: 2px solid #d1a502;
    border-bottom: 2px solid #d1a502;
}
.page-context-header .page-header-headings h1 {
    margin-top: 1px;
    padding: 0px 0px 17px 0px;
    background-color: transparent;
    color: #e0ddf0;
    text-transform: capitalize;
}
a {
    color: #e0ddf0;
}
label {
    color: #e0ddf0;
}
a:hover {
    color: #e0ddf0;
}
li a:hover {
    color: #e0ddf0;
}
h2,
div.summary #tetras a {
    color: white;
}
.block {
    background-color: #d1a502;
    border-color: #d1a502;
    border-radius: 0px;
    border: 0px;
}
.breadcrumb {
    background-color: #d1a502;
    border: 1px solid #2d2d30;
    border-radius: 0px;
    border-left: 0px;
}

tr {
    color: gold;
}

.generaltable tbody>tr:nth-child(odd)>th {
    background-color: #2d2d30;
}

h2 {
 	text-transform: capitalize;  
   
}

.path-grade-report-user .user-grade.generaltable .levelodd {
    background-color: #2d2d30;
}

.breadcrumb-nav {
    float:left;
    margin-bottom: 0px;
}
.coursebox {
    background-color: #2D2D30;
    border-color: #d1a502;
    border-radius: 0px;
    border: 1px solid #d1a502;
}
.courses .coursebox.even {
    background-color: #2d2d30;
}
.courses .coursebox:hover {
    background-color: #2d2d30;
}
.course-content .current {
    background-color: #2d2d30;
}
.filemanager a,
.file-picker a,
.file-manager a:hover,
.file-picker a:hover {
    color: #e0ddf0;
}

.course-content ul.weeks {
    margin: 0;
    border-left: 2px solid #d1a502;
    border-right: 2px solid #d1a502;
}
div#jstree_categorylist div.coursebox {
    background-color: #3F3F46;
}
h1 {
    text-align: center;
}
.path-mod-assign div.submissionstatustable,
.path-mod-assign div.submissionfull,
.path-mod-assign div.submissionlinks,
.path-mod-assign div.usersummary,
.path-mod-assign div.feedback,
.path-mod-assign div.gradingsummary {
    margin-bottom: 5em;
    padding: 20px;
}
#coc-courselist .coursebox {
    border-bottom: 2px dashed #1C1C1C;
}
select {
    border: 1px solid #1C1C1C;
}
.pagelayout-mydashboard #region-main,
.course-content .section-summary,
div.summary ul#tetras li,
h6 {
    border: 0;
}
input[type="submit"],
input#searchform_search,
input#searchform_button,
input#id_submitbutton,
div.loginpanel div:first-child a,
button {
    background-color: #3F3F46;
}
.course-content ul li.section.main {
    border-bottom: 2px solid #252526;
}
h2,
div.summary #tetras a {
    color: #007ACC;
}
.breadcrumb > li {
    text-shadow: 0 1px 0 #3F3F46;
}
.alert-danger,
.alert-error {
    color: #E0DDF0;
    background-color: #812B28;
    text-shadow: 0 0px 0 rgba(255, 255, 255, 0.5);
}
  
  .forumpost {
	background-color: #2d2d2d;
}

}
