css:
@-moz-document domain("smartschool.be") {
/*variables*/
/*colors: https://material.google.com/style/color.html#color-color-palette*/
:root {
    /*50*/
    --color-main-50: #E3F2FD;
    --color-main-100: #BBDEFB;
    --color-main-200: #90CAF9;
    --color-main-300: #64B5F6;
    --color-main-400: #42A5F5;
    --color-main-500: #2196F3;
    --color-main-600: #1E88E5;
    --color-main-700: #1976D2;
    --color-main-800: #1565C0;
    --color-main-900: #0D47A1;
    
    --color-main-A100: #82B1FF;
    --color-main-A200: #448AFF;
    --color-main-A400: #2979FF;
    --color-main-A700: #2962FF;
}

/* --- SITE-WIDE ---*/
/*top logo*/
.smscTopNav {
    background-image: none;
    background-color: var(--color-main-100);
    box-shadow: 0 5px 3px #888888;
    margin-bottom: 10px;
}
.smscTopLogo {
    display: none !important;
}

/* --- HOMEPAGE ---*/
/*groupbox - remove (sometimes hardcoded) borders*/
.smscborder.bright.topr, .smscborder.bleft.topl, .smscborder.bleft.bottoml, .smscborder.bright.bottomr {
    display: none!important;
}
.smscborder.centerxy, .smscborder.centery.bottomy {
    border: none !important;
}

/*groupbox - header*/
.smscborder.centery.topy.smallboxct, .smscborder.centery.topy {
    background: var(--color-main-500) !important;
    color: #eee !important;
    width: 100%;
}
.smscborder.centerxy.smallboxc {
    background-color: var(--color-main-100);
    width: 100%!important;
}
.title.tcenter span {
    color: #EEE!important;
}
.title.tleft, .title.tright, .title.tcenter, .smscborder.centerxy, .titleIdk {
    background-image: none;
}

/*middle part*/
div[id*="news_in_de_kijker_"], div[id*="Nieuwsblok_"] {
    width: 100% !important;
}
.smscborder.centery.bottomy {
    background-color: transparent!important;
}
#centercontainer {
    padding-left: 10px;
    padding-right: 10px;
}

/*fix for courses*/
.courseButtonLeft {
    background-color: transparent!important;
}
.courseButtonLeftSelected, .courseButtonLeftSelected {
    background-color: var(--color-main-300)!important;
}

/*news & RSS icon*/
.pns_tab {
    margin: 0 !important;
    padding-left: 5px;
    padding-right: 5px;
}
.pns_tab.bgleft, .pns_tab.bgright, .pns_tab_spacer, .pns_shadow {
    background-image: none!important;
    display: none;
    
}
.pns_tab.bgcenter_sel {
    background-color: var(--color-main-A200);
}

/*custom icons*/
.smsc-column-nav__button--mydoc {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_white_24px.svg);
}
.smsc-column-nav__button--portfolio {
	background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_open_white_24px.svg);
}
.smsc-column-nav__button--timetable {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_access_time_white_24px.svg);
}
.smsc-column-nav__button--enquete {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_pie_chart_outlined_white_24px.svg);
}
.smsc-column-nav__button--studentcard {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_account_circle_white_24px.svg);
}
.smsc-column-nav__button--lvs {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_supervisor_account_white_24px.svg);
}
.smsc-column-nav__button--wiki {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_mode_edit_white_24px.svg);
}
.smsc-column-nav__button--schoolagenda {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_book_white_24px.svg);
}
.smsc-column-nav__button--publishers {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_card_travel_white_24px.svg);
}
div[style="background-image: url('/smsc/img/lightbulb_on/lightbulb_on_16x16.png'); background-repeat: no-repeat; background-position: right top;"] {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_lightbulb_outline_black_18px.svg)!important;
}
div[style*="/smsc/img/lightbulb_on/lightbulb_on_16x16.png"] {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_lightbulb_outline_black_18px.svg)!important;
}
div[style*="/smsc/img/clock/clock_16x16.png"] {
	background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_access_time_black_18px.svg) !important;
}
.courseImage1 {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_open_white_24px.svg);
}
.courseButtonLeft, .courseButtonLeft#button1, .courseButtonLeftSelected, .courseButtonLeftSelected#button1 {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_open_white_18px.svg);
}
.courseImage4 {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_white_24px.svg);
}
.courseButtonLeft#button3, .courseButtonLeftSelected#button3 {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_white_18px.svg);
}
#smscNotify .smscNotify__bubble {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_message_black_18px.svg);
}
#smscSearch {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_search_black_18px.svg);
}

/* --- PAGE-SPECIFIC --- */
/*portfolio blocks*/
.portfolioBlock {
    margin: 5px!important;
}
.portfolioBlock.selected {
    box-shadow: 0px 0px 5px 2px var(--color-main-800);
}
.portfolioBlock .blockLeft, .portfolioBlock .blockRight {
    width: 0 !important;
}
.portfolioBlock .blockMiddle {
    width: 100% !important;
    background: var(--color-main-200) !important;
}
.portfolioBlock.selected .blockMiddle {
    background: var(--color-main-300) !important;
}
.portfolioBlock .blockImage {
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_folder_open_white_48px.svg)!important;
    background-color: transparent !important;
    background-size: auto !important;
    background-position: center !important;
    height: 100% !important; 
}
.portfolioBlock .blockGoTo {
    height: 96px !important;
    color: transparent!important; /*hiding text*/
    width: 100px;
    margin: 0px !important;
    top: -65px;
    position: relative;
    z-index: 9999 !important;
    background-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_arrow_forward_white_48px.svg)!important;
    background-position: center !important;
}

/*agenda*/
.gridBlock {
    border-radius: 0!important;
}
}
