css:
@-moz-document domain(9gag.com) {

*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #212121 ! important;
    border: 1px solid #252525 ! important;
}
*::-webkit-scrollbar-button {
    display: none ! important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #454545 ! important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #1e1e1e ! important;
}

.horizontal-vote.down a.down, .vertical-vote.down a.down {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat -42px -32px;
}

.horizontal-vote.up a.up, .vertical-vote.up a.up {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat -42px 0;
}

.horizontal-vote a.down, .vertical-vote a.down {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat 0 -32px;
}

.horizontal-vote a.up, .vertical-vote a.up {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat 0 0;
}

.horizontal-vote.up a.up span  {
    opacity: 0;
}

.btn-vote .down.active {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat -42px -32px;
}

.btn-vote .up.active {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat -42px 0;
}

.btn-vote .down {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat 0 -32px;
}

.btn-vote .up {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/sticky-vote.png) no-repeat 0 0;
}

.horizontal-vote.up a.up span  {
    opacity: 0;
}

a.back-to-top {
    background-color: black;
}

/*hover*/

a {
    -webkit-transition: 0.3s !important;
}

a:hover {
    -webkit-transition: 0.3s !important;
}
.popup-menu a {
    -webkit-transition: 0.3s;
}
.popup-menu a:hover {
    background-color: #212121 !important;
    color: white !important;
    -webkit-transition: 0.3s;
}

.post-afterbar-a .btn-share.facebook, .post-afterbar-a.in-post-bot .btn-share.facebook {
    background-color: #212121;
    border: 1px solid #424242;
    -webkit-transition: 0.3s;
}
.post-afterbar-a .btn-share.facebook:hover, .post-afterbar-a.in-post-bot .btn-share.facebook:hover {
    -webkit-transition: 0.3s;
}

.post-afterbar-a .btn-share.twitter, .post-afterbar-a.in-post-bot .btn-share.twitter {
    background-color: #212121;
    border: 1px solid #424242;
    -webkit-transition: 0.3s;
}
.post-afterbar-a .btn-share.twitter:hover, .post-afterbar-a.in-post-bot .btn-share.twitter:hover {
    -webkit-transition: 0.3s;
}

.post-afterbar-a .others a.more {
    background-color: #212121;
    border: 1px solid #424242;
    -webkit-transition: 0.3s;
}
.post-afterbar-a .others a.more:hover {
    background-color: #454545 !important;
    border: 1px solid #424242;
    -webkit-transition: 0.3s;
}

.horizontal-vote a, .btn-vote a {
    -webkit-transition: 0.3s;
}
.horizontal-vote a, .btn-vote a:hover {
    -webkit-transition: 0.3s;
}

.horizontal-vote.up a.up span {
    -webkit-transition: 0.6s;
}

.post-container:hover .post-read-more {
    background-color: black;
    border-left-color: black;
    border-right-color: black;
    border-bottom-color: black;
    color: #09f;
    -webkit-transition: 0.3s;
}

.section-nav a:hover {
    color: white !important;
    border-bottom: 3px solid #fff;
}

.post-afterbar-meta a:hover {
    color: red;
}

section#list-view-2 h2 a:hover {
    color: #09f;
}

section.section-header .section-nav a:hover, .CS3 .tab-bar ul.tab a:hover {
    color: white;
}

/**/

.notification-text-list a {
    color: #09f;
}

section.section-header .section-nav a {
    color: #a1a1a1;
}

.post-afterbar-a.in-post-bot .btn-share.twitter, .post-afterbar-a.in-post-bot .btn-share.facebook {
    width: 356px;
}

textarea, .CS3 .comment-box .payload input[type=url].focus, input[type=email], input[type=password], input[type=text], input[type=url], .static-block, .CS3 .notice-message {
    background-color: #212121;
    color: white;
}
textarea:active {
    background-color: white;
    color: black;
}

.background-white {
    background-color: #212121;
    color: white;
}

.section-nav {
    background-color: #212121;
}

.section-nav a {
    color: #a1a1a1;
}

section#list-view-2 h2 a, section#contact-form a.upload-selected {
    color: white;
}

section#list-view-2 .activity-text, section#list-view-2 .activity-text a {
    color: #a1a1a1;
}



section.block-feature-cover .info-container a, .CS3 .tab-bar ul.tab li.active a, .CS3 .comment-entry .payload .username, .CS3 .comment-pagination a.more span.label, .section-nav a.selected, section.section-header .section-nav, .popup-menu a.selected {
    color: white;
}

.popup-menu ul, .nav-menu ul, .popup-menu a, .nav-menu .flexMenu-popup li a {
    background-color: black;
    border-color: black;
}

.popup-menu ul, .nav-menu ul, .popup-menu a, .nav-menu .flexMenu-popup li a {
    background-color: black;
    border-color: black;
}


section.modal, section.modal.upload .source-file, .horizontal-vote a.up span, .CS3 {
    background-color: #212121;
}

section.footer, .post-afterbar-a.in-post-top {
    background-color: #212121;
}

section#list-view-2 article {
    border-top-color: #424242;
}

a.post-read-more {
    background-color: black;
    border-left-color: black;
    border-right-color: black;
    border-bottom-color: black;
    color: white;
    -webkit-transition: 0.3s;
}

section#idle p.lead, section#idle .ticker>h3, section#idle .ticker .info h3 {
    color: white;
}

.ticker {
    background-color: rgba(0,0,0,0.7);
}

.post-read-more .shadow {
    background: url(http://test.itcpp.com.ua/wp-content/uploads/2016/11/expand-crook.png) !important;
}

.loading a.btn.blue {
    background-image: url(https://pp.vk.me/c637822/v637822351/1b9f9/SJsVcys9MuU.jpg);
    border-color: brown;
}
    
.modal.idle, .CS3 a.cmnt-btn.grey {
    background-image: url(https://pp.vk.me/c637822/v637822351/1b9f9/SJsVcys9MuU.jpg);
    color: white;
}

.badge-sticky-button {
    background-image: url(https://pp.vk.me/c637822/v637822351/1b9f9/SJsVcys9MuU.jpg);
}

.btn-vote a {
    border-color: #424242;
}
  
}
