additional_info:
To make the scrollbars fit in better with this theme, add this code via Tampermonkey or another userscript manager.
<code style="margin-left: 5px;display: block;background:#DDDDDD;">
// ==UserScript==
// @name         App Inventor 2 Scrollbars
// @namespace    https://google.com
// @version      0.1
// @description  Edit scrollbars in App Inventor, so that they are rectangular, and look normal.
// @author       akainth015
// @match        http://ai2.appinventor.mit.edu/*
// @grant        none
// ==/UserScript==

(function() {
    var Scrollbars = document.getElementsByClassName('blocklyScrollbarKnob');
    Scrollbars[2].style.width = "10px";
    Scrollbars[0].setAttributeNS(null, "rx", "0");
    Scrollbars[1].setAttributeNS(null, "rx", "0");
    Scrollbars[2].setAttributeNS(null, "rx", "0");
    Scrollbars[3].setAttributeNS(null, "rx", "0");
    Scrollbars[0].setAttributeNS(null, "x", "10");
    Scrollbars[1].setAttributeNS(null, "y", "10");
    Scrollbars[2].setAttributeNS(null, "x", "10");
    Scrollbars[3].setAttributeNS(null, "x", "0");
})();
</code>

css:
@-moz-document domain("ai2.appinventor.mit.edu") {
/*
 * MIT App Inventor 2 Material Design: Edited by Aanand Kainth
 */

body {
    background: #FFFFFF;
}
#page, #aiac .main-container, .container, .DialogBox-message > div {
    width: 100%;
    font-family: Roboto, 'Roboto', Helvetica, Arial, sans-serif !important;
}
/*
 * Button Redesigning
 */
div.ode-TopPanelButton, .ActionDropDown-Default, .ActionDropDown-Options, #menu-bar li {
    height: 40px;
    border-radius: 3px;
    margin-right: 1px;
    text-transform: uppercase;
}
div.ode-TopPanelButton:hover, .ActionDropDown-Default:hover .ActionDropDown-Options:hover, #menu-bar li:hover {
    background-color: #388E3C;
    color: #FFFFFF;
}
td.ode-ContextMenuItem {
    text-align: left;
    padding-left: 9px; 
}
td.ode-ContextMenuItem-selected {
    background: rgba(0, 0, 0, 0.035);
    color: #4CAF50;
}
div.ode-PropertiesComponentName {
    background-color: #4CAF50;
    color: #FFFFFF;
}
.ode-TextButton, .gwt-Button {
    background: #FFFFFF;
    color: #4CAF50;
    text-shadow: 0 0 0;
    text-transform: uppercase;
    border-color: transparent;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.23);
    border-radius: 2px;
}
.ode-TextButton:hover, .gwt-Button:hover {
    box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.23);
}
#menu-bar li a {
    color: #FFFFFF;
    text-transform: uppercase;
}
#menu-bar li a:hover {
    color: #FFFFFF;
    text-transform: uppercase;
}
#themeplacehold_daylo, .ode-TextButton:hover, .gwt-Button:hover {
    background: #4CAF50;
    color: #FFFFFF;
    text-shadow: 0 0 0;
    text-transform: uppercase;
    border-color: transparent;
}
/*
 * Dropdown Designing
 */
div.gwt-PopupPanel {
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.24);
    border: 0;
    width: auto;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
    margin-right: 10px;
    padding: 1px;
}
/*
 * Input Designing
 */
.ode-SimpleMockButton {
	box-shadow: none;
	text-transform: none;
	text-decoration: none;
}
.ode-SimpleMockButton:hover {
     box-shadow: none;
 	text-transform: none;
    	text-decoration: none;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input, select, textarea {
    border: 0;
    box-shadow: 0 0 0;
    background: rgba(255, 255, 255, 0.6);
}
/*
 * Header Designing
 */

table.ode-TopPanel, #top_bar, #top_bar_background {
    background: #4CAF50;
    border: 0;
}
table.ode-TopPanel > tbody > tr > td[width="50px"], a[title="Home page"] {
    background-image: url(data:image/png;base64,);
    background-size: 40px;
    background-repeat: no-repeat;
    margin-top: 5px;
}
a[title="Home page"] {
    height: 40px;
    width: 40px;
    display: block;
}
a[title="Home page"] > img {
    display: none !important;
}
div.ode-LogoText, .ode-TopPanelButton, .ActionDropDown-Default, .ActionDropDown-Options {
    color: #FFFFFF;
}
img[onload='this.__gwtLastUnhandledEvent="load";'] {
 	   background: none;
}
table.ode-TopPanelAccount > tbody > tr > td:nth-child(2) > div {
    font-size: 0;
    width: 24px;
    height: 24px;
    background-image: url(data:image/png;base64,);
    background-repeat: no-repeat;
    padding-right: 0;
}
table.ode-SimplePaletteItem > tbody > tr > td:nth-child(3) {
    /**background: url(data:image/png;base64,) no-repeat;**/
    width: 20px;
    height: 20px;
    background-size: 100%;
    z-index: 100000;
}
table.ode-SimplePaletteItem > tbody > tr > td:nth-child(3) > img {
    opacity: 0.01;
    height: 20px;
    width: 20px;
}
img.gwt-Image:not([src="http://ai2.appinventor.mit.edu/ode/clear.cache.gif"]), .ode-Logo, .ode-StatusPanel, .ode-LogoVersion, .ode-Box-header, .gwt-CheckBox, .ode-SimpleMockFormPhoneBar > tbody {
    display: none
}
/*
 * Update Notifier Design
*/
div.ode-DialogBox {
    border: 0;
    font-family: Roboto, "Roboto", Helvetica, sans-serif;
}
div.ode-DialogBox > div > table {
    max-height: 500px !important;
}
tr.dialogTop, .ode-DialogBox .Caption {
    background-color: #4CAF50 !important;
    box-shadow: 0 0 0;
}
/*
 * Project Name Design
 */
table.ya-Toolbar {
    background: #FFFFFF;
}

div.ya-ProjectName {
    background-color: #4CAF50;
    border-radius: 2px;
    padding-bottom: 5px;
    width: 230px;
}
table.ode-Box-header, .ode-Box-content, .ode-ProjectHeaderRow {
    border: 0;
    background: #ffffff;
}
div.ode-ProjectHeaderLabel, .ode-ProjectNameLabel, .gwt-Label {
    font-size: 12px;
}
.ode-SimpleMockFormTitleBar {
    display: none;
}
td.gwt-StackPanelItem :first-child {
    background-color: #4CAF50;
    color: white;
}
div.ode-Box-content {
 	margin-left: 0px;   
}
div.ode-ComponentHelpPopup-TitleBar {
    padding: 5px;
    background: #4CAF50;
    height: 20px;
}
div.ode-ComponentHelpPopup {
    border: 0;
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.24);
    text-align: justify;
}
#themeplacehold_daylo, .ode-ComponentHelpPopup-Link a {
    color: #4CAF50;
    text-decoration: none;
}
#themeplacehold_daylo, .ode-ComponentHelpPopup-Link a:hover {
    color: #388E3C;
    text-decoration: none;
}

/*
 * Preview Window
 */
div.ode-SimpleMockForm table:first-child {
    background: url(data:image/png;base64,);
}
div.dialogContent :first-child :first-child :last-child :first-child :first-child {
	text-align:center;    
}
div.ode-SimpleMockForm table:last-child {
    background-image: url("http://www.androidpolice.com/wp-content/uploads/2015/01/nexus2cee_Nexus.jpg");
    background-size: 100% 100%;
}
div.ode-SimpleMockFormTitleBar {
    background: #ffffff;
    box-shadow: 0px 1px 3px 0px rgba(20, 20, 20, 0.75);
    font-size: 130%;
}
div.ode-SimpleMockFormTitle {
    color: #1f1f1f;
    font-size: 95%;
}
/* = phone size etc = */

div.ode-SimpleMockForm, div.ode-SimpleMockForm-selected, .ode-SimpleMockContainer {
    border: 1px solid rgba(0, 0, 0, .25);
    background: #ffffff;
}
/* ====
		blocks page
   ====
*/

path {
    font-family: Roboto, 'Roboto', Helvetica, Arial, sans-serif;
}
/* == colour blocks == */
/* = control block = */

path[fill="#b18e35"], path[stroke="#c8b072"], path[fill="#6a5520"] {
    /*fill: #FDD835;*/
    fill: #fd35d4;
    stroke: #fdd31c;
    stroke-width: 0.5px;
}
/* = logic block = */

path[fill="#77ab41"], path[stroke="#a0c47a"], path[fill="#476727"] {
    fill: #43A047;
    stroke: #3b8e3f;
    stroke-width: 0.5px;
}
/* = maths block = */
/* maths, not math!! */

path[fill="#3f71b5"], path[stroke="#799ccb"], path[fill="#26446d"] {
    fill: #01579B;
    stroke: #014982;
    stroke-width: 0.5px;
}
/* = text block = */

path[fill="#b32d5e"], path[stroke="#ca6c8e"], path[fill="#6b1b38"] {
    fill: #F44336;
    stroke: #f32c1e;
    stroke-width: 0.5px;
}
/* = lists block = */

path[fill="#49a6d4"], path[stroke="#80c1e1"], path[fill="#2c647f"] {
    fill: #448AFF;
    stroke: #2b7aff;
    stroke-width: 0.5px;
}
/* = colours block = */
/* british spelling */

path[fill="#7d7d7d"], path[stroke="#a4a4a4"], path[fill="#4b4b4b"] {
    fill: #757575;
    stroke: #686868;
    stroke-width: 0.5px;
}
/* = variables block = */

path[fill="#d05f2d"], path[stroke="#de8f6c"], path[fill="#7d391b"] {
    fill: #E64A19;
    stroke: #cf4317;
    stroke-width: 0.5px;
}
/* = procedures block = */

path[fill="#7c5385"], path[stroke="#a387aa"], path[fill="#4a3250"] {
    fill: #673AB7;
    stroke: #5c34a4;
    stroke-width: 0.5px;
}
/* = attributes block = */

path[fill="#266643"], path[stroke="#67947b"], path[fill="#173d28"] {
    fill: #2E7D32;
    stroke: #276a2b;
    stroke-width: 0.5px;
}
/* = propgrab block = */

path[fill="#439970"], path[stroke="#7bb89b"], path[fill="#285c43"] {
    fill: #4CAF50;
    stroke: #449d48;
    stroke-width: 0.5px;
}
/*
 * Math Operation
 */
#themeplacehold-daylo, .blocklyNonEditableText>rect, .blocklyEditableText>rect {
    fill-opacity: 0.7;
}
/*
 * Parameters
 */
#themeplacehold-daylo, .blocklyFieldParameter>rect {
    fill: rgba(255, 255, 255, 0.7);
    stroke: rgba(255, 255, 255, 1);
}
rect.blocklyIconShield {
    stroke-width: 0;
}
#themeplacehold-daylo, .blocklyFieldParameter:hover>rect {
    fill: rgba(255, 255, 255, 0.9);
}
/*
 * Comments
 */
rect[fill="#b18e35"] {
    fill: rgba(255, 255, 255, 0.7);
    stroke: rgba(0, 0, 0, 0.2);
    stroke-width: 1px;
}
textarea.blocklyCommentTextarea {
    background: rgba(255, 255, 255, 0.5);
}
/*
 * Scrollbars
 */
rect.blocklyScrollbarBackground {
    display:none;
    padding: 0px;
    margin-right: 0px;
}
rect.blocklyScrollbarKnob[x='3'] {
 	width: 10px;
    border-radius: 0px;
    position: relative;
}
rect.blocklyScrollbarKnob[y='3'] {
	height: 10px;
	border-radius: 0px;
	position: relative;
    
}
.ode-PropertyEditor {
 	width: 97%;
}
.popupContent {
     border: none;
     width: 100%;
     background-color: #EEEEEE;
}
.ode-MultipleChoicePropertyEditor {
     border: none;
     width: 15%;
}
.gwt-ListBox {
	width: 100%;
}
.gwt-HTML {
     text-transform: none;
}
.gwt-Tree .gwt-TreeItem-selected span {
     background: #DDDDDD;
     cursor: hand;
}
.gwt-Tree:hover .gwt-TreeItem-selected:hover span:hover {
     background: #D3D3DD;
}
.gwt-TreeItem span {
     display:block !important;
     width: 150px;
}
}
