css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("^.*4-ch.net/([^i].*|i[^m].*|im[^g].*)") {

  
  .mainpage, .threadpage{
    background: #FFF url(https://files.catbox.moe/0gr8mp.jpg) bottom right no-repeat !important;
    background-size: 21.5% !important ;
    background-attachment: fixed !important;
    font-family: Mona;
  }
  
  /* HEADER */
  #stylebox:nth-child(1n+5), #stylebox strong, #stylebox a, div[id^="snFlkDiv"], #titlebox .innerbox, .threadlink:nth-child(1n+10){
    display: none;
  }
  #stylebox:nth-child(4){
    margin: 0 2.5% 0;
    position: absolute;
    background: #D8E7CE;
    border: none;
    padding: 0;
    color: #8BA14E;
  }
  #stylebox:nth-child(4):hover{
    color: #000;
  }
  #stylebox .innerbox{
    border: none;
  }
  #stylebox .innerbox:before{
    content: "Does the style look incorrect?";
  }
  #stylebox a:nth-child(7){
    display: initial;
    font-size: 0px;
    color: #8BA14E !important;
  }
  #stylebox a:nth-child(7):before{
    content: "Click Here";
    font-size: 12pt;
  }
  #titlebox{
    background: url(https://files.catbox.moe/18lufr.png), url(https://files.catbox.moe/sx66tl.png);
    background-position: calc(40% - 236px) 0, calc(50% + 20px);
    background-attachment:  fixed, local;
    background-color: #0000;
    background-repeat: no-repeat;
    border: none;
    height: 120px;
    margin: -27px 21.5% 0 2.5%;
    padding: 42px 7px 23px;
  }
  #threadbox{
    border: none;
    margin: 0 21.5% 16px 2.5%;
    padding: 0;
  }
  #threadbox .innerbox{
    background: url(https://files.catbox.moe/7ez7re.png) left repeat;
    border: 58px solid transparent;
    border-image: url(https://files.catbox.moe/1i0dvg.png) 58 round;
  }
  
  .threadlink{
    display: block;
  }
  .threadlink a[href^="/dqn"]{
    display: inline-block;
    padding: 0 0 0 5px;
    color: #FFF !important;
    background: #8BA14E;
    font-family: sans;
    font-weight: 700;
    width: 12px;
    height: 17px;
    border: 1px solid #8BA14E;
    border-radius: 50%;
    text-decoration: none;
  }
  .threadlink a[href^="#"]{
    color: #8BA14E !important;
    font-size: 13pt;
    text-decoration: none;
  }
  #threadlinks a{
    color: #8BA14E !important;
  }
  .threadlink a[href^="#"]:hover, #threadlinks a:hover{
    color: #69D3D3 !important;
  }
  
  
  /*THREADS*/
  #posts h2, #posts .replies, #posts form{
    border: none !important;
  }
  .mainpage #posts .thread, .threadpage #posts .thread{
    background: #FFF;
    border: 4px solid #444;
    border-radius: 3px;
    margin: 0 21.5% 2em 2.5%;
  }
  #posts form{
    background: #FFF;
  }
  #posts h3 .replynum a, #posts h2 a, #posts h2, a, .reply .postername{
    color: #444 !important;
  }
  #posts h2 a:hover, a:hover{
    color: red !important;
  }
  #posts h2 a:visited, a:visited{
    color: gray;
  }
  
  .reply .replytext{
    border: 30px solid transparent;
    max-width: calc(100% - 150px);
    overflow-x: auto;
  }
  .reply .replytext p{
    margin-bottom: 0;
    color: #444;
  }
  .reply:nth-child(2n+1) .replytext{
    border-image: url(https://files.catbox.moe/4syykq.png) 30 stretch;
    margin-left: 78px !important;
    float: left;
  }
  .reply:nth-child(2n+0) .replytext{
    border-image: url(https://files.catbox.moe/3aactm.png) 30 stretch;
    margin-right: 90px !important;
    margin-left: 0 !important;
    float: right;
  }
  .reply{
    width: 100%;
    padding-bottom: 40px;
    background-repeat: no-repeat !important;
    float: left;
  }
  .reply:nth-child(4n+0){
    background: url(https://files.catbox.moe/hlf7mt.png) bottom right;
  }
  .reply:nth-child(4n+1){
    background: url(https://files.catbox.moe/qc836j.png) bottom left;
  }
  .reply:nth-child(4n+2){
    background: url(https://files.catbox.moe/rq5sjy.png) bottom right;
  }
  .reply:nth-child(4n+3){
    background: url(https://files.catbox.moe/lb5076.png) bottom left;
  }
  .firstreply .reply{
    background: url(https://files.catbox.moe/ix9prw.png) bottom left;
  }
  .reply h3{
    margin: 0 120px 0 110px !important;
    border-bottom: 2px solid #444;
    font-weight: 700 !important;
    color: #444;
  }
  
  textarea{
    width: 100%;
  }
  .threadcaptcha, .postcaptcha{
    opacity: 0.735;
  }
  
  /*FOOTER*/
  #createbox{
    background: #FFF url(https://files.catbox.moe/59a6yg.png) bottom right no-repeat;
    margin-right: 21.5%;
    border: solid #444 4px;
  }
  #createbox .innerbox{
    border: none;
  }
  #createbox h2{
    color: #444;
  }
}
