css:
@-moz-document domain("board.rt.mipt.ru"), domain("zlo.rt.mipt.ru") {
a {color: #ffcc66;}
a:visited {color: #ffff99;}

BIG {font-size: 120%;font-weight: bold;}

DD DL{margin-left:-15px;}

.cl,  .cs {font-family: Verdana, Arial; font-size: 70%}



body {  
  	background-color: #666666; 
  	color: #ccffff;}
	img, table, body, form {margin: 0;padding:0;border: 0;
}


div.menu
{
	padding: 2px;
	background-color: #414141;
	text-align:center;
	font-size: 80%;
	font-family: Verdana, Arial;
}

div.menu a, div.menu a:visited
{
	text-decoration: underline;
	margin:0 4px;
	color: #996;
	font-size: 100%;

}	

/* boardtags [url][c][/c][/url] */ 
a span.btc
{
	text-decoration: underline;
}

/* info */
div.info 
{
	font-family: Verdana, Arial;
	font-size: 70%;
	padding:10px;  
	margin:0px;
	background-color: #555;
}

div.info input, div.info select
{
	font-family: Verdana, Arial;
	font-size: 100%;
  	background-color: #999;
}

div.left_info
{	
	float: left;
	width: 60%;
}

div.right_info
{
	float: right;
	width: 30%;
	text-align:right;
} 


/* index  */

div.g div, div.w div
{
	padding: 1px 0 1px 25px;
}


div.g, div.w
{
	font-size: 75%;
	padding: 4px 0 2px 10px;
}

div.g
{
	border-top: 1px solid #666;
	border-bottom: 1px solid #292929;
	background-color:#555555;
}

div.g a, div.w a
{
	font-size: 133%;
}


div.an
{
	font-family: Verdana, Arial;
	padding: 3px;
	margin: 5px 3%;
	font-size: 70%;
	border: 1px solid #aaa;
	background-color:#6b6161;
}

span.e, span.e a, span.e a:visited
{
	color: #46C400;
	font-weight: bold;
	font-size: 120%;
	
}

span.roll2, span.roll2 a
{
	font-size: 100%;
}

span.roll1
{
	color: #909000;
}

span.close
{
	color: #909000;
}

.strike
{
	text-decoration: line-through;
}


div.ibody
{
	border-top: 1px solid #292929;
	border-bottom: 1px solid #666;
	font-size: 115%;
	background-color:#eee;
}

/* message */
.quote {background-color: #555; border: 1px solid #0f0d0d;}
.inquote {color: #6b6161;}

div.body, div.sign {padding: 25px;}

select {
	color: #f2eded;
	background-color: #0f0d0d;
}

input {
  	color: #0f0d0d;
	background-color: #f2eded;
}
  
textarea {
	color: #0f0d0d;
  	background-color: #f2eded;
}

/* nickname */
a.nn {
	color: #C7E2C7;
	font-weight: bold;
}
span.reg {
	color: #ccffff;
	font-size: 120%;
	font-weight: bold;
}
span.unr {
	color: #207025;
	font-size: 125%;
	font-weight: bold;
}

span.own {
	color: red;
	font-size: 133%;
	font-weight: bold;
}

span.sel {
	color: #FF9900;
	font-size: 133%;
	font-weight: bold;
}


/* privates*/
div.pr_to{
	padding: 3px;
	margin:  1px 10px 20px 10px;
  	color: #D3D3D3;
	background-color: #383847;
	border: 1px solid #555;
}

div.pr_from{
	padding: 3px;
	margin: 1px 10px 20px 10px;
  	color: #D3D3D3;
	background-color: #423434;
	border: 1px solid #555;
}

div.pr_info{
	margin-left: 20px;
}

/* new msg */
div.smilestable {
	display: none;
	position: absolute;
	right: 10%;
	padding: 3px;
}
		
table.smilestable td {
	padding:2px;
}

input.qe_button {
	border:0;
	background-color: #b58d8d;
	color: #0f0d0d;
	font-size: 80%;
	font-weight: bold;
}

/* reg */

input#email {
	display: none;
}
}
