css:
@-moz-document domain("feedly.com") {

#feedlyPageHolderFX,
#searchBarFX,
#headerBarFX,
#herculeFrame,
#searchBarFX,
#feedlyPageHolderFX .entryholder,
#feedlyPageHolderFX .headerInfo,
#headerBarFX .header,
#searchBarFX .header {
    background-color: #2A2B2F;
    color: rgba( 255, 255, 255, 0.9);
}

#headerBarFX h1,
#feedlyPageHolderFX h1,
#feedlyPageHolderFX .unread,
#feedlyPageHolderFX .entry.unread .title,
#feedlyPageHolderFX .entryHeader .title,
#feedlyPageHolderFX .content #feedlyPageHolderFX .entryHeader .title,
#feedlyPageHolderFX .item-header,
#feedlyPageHolderFX .entryBody,
#feedlyPageHolderFX .entryBody p {
    color: rgba( 255, 255, 255, 0.9);
}

#feedlyPageHolderFX .content a {
    color: rgba( 200, 200, 255, 0.9) !important;
}

#feedlyPageHolderFX .content a:visited {
    color: rgba( 200, 200, 255, 0.6) !important;
}

#feedlyPageHolderFX .entry.u0:hover {
    background-color: #4F5259;
}

#feedlyPageHolderFX .entry.u0 .summary {
    color: rgba( 255, 255, 255, 0.7);
}

#feedlyPageHolderFX .entry {
    border-left-color: #555
}

#headerBarFX button,
#feedlyPageHolderFX .save-for-later,
#feedlyPageHolderFX .headerInfo-links,
#herculeFrame .hercule-search,
#feedlyPageHolderFX button {
    filter: invert(100%);
}

#feedlyPageHolderFX .entry.u0 {
    border-top-color: #888;
}

#herculeHint {
    border-color: #888;
}

#searchBarFX button.pro {
    opacity: 0.3;
}

#searchBarFX input {
    background-color: #777;
}

*::-webkit-scrollbar {
    background-color: transparent;
    width: 8px;
}

*::-webkit-scrollbar-thumb {
	border-radius: 8px;
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #777;
}

}
