css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fontanka.ru"), domain("doctorpiter.ru") {
  .body, .page, .sidebar, .wrapper, .sb-content_newsline, .clearfix, .col-c, *
  {
    background: black !important;
    color: #cbcdcb !important;
    background-color: black !important;
  }

  .sidebar__content, .sb-content, .sb-content__item, .news-of-hour, .m-art__comments--wrapper, .comments, .article-header
  {
    background: #3e4042 !important;
  }

  html:not(#⁠)
  {
    background: black !important;
    color: #cbcdcb !important;
  }

  :not(#⁠) :link
  {
    color: #599CFF!important;
  }

  .h-widgets__item--rubrics
  {
    color: #599CFF!important;
  }

  :not(#⁠) :visited
  {
    color: #A0A0A0!important;
  }

  .article_title, .article_desc
  {
    color: #cbcdcb !important;
  }

  .article_fulltext
  {
    color: gray !important;
  }

  .widget_content, .fll, .comment__content, .comments-toolbar, *
  {
    background: #1a1a1a !important;
  }

}
