css:
@-moz-document domain("linuxsurvival.com") {
html, body {
    background-image: url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==) !important;
    background-repeat: repeat !important;
    background-repeat-x: repeat;
    background-repeat-y: repeat;
    background-color: #181818 !important;
}

#main {
    box-shadow: 0px 0px 4px 0px #000 !important;
    background: #181818 !important;
    color: #fff !important;
    text-shadow: 0 0 4px #000, 0 0 2px #666 !important;
}

textarea#textpane {
    background: #202020;
    color: #fff !important;
    text-shadow: 0 0 4px #000, 0 0 2px #666 !important;
}


#site-header {
    background-color: #181818 !important;
    height: 108px;
    background-image: url('http://i.imgur.com/V4NXZYY.png'),url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==) !important;
    background-repeat: no-repeat, repeat;
    background-attachment: fixed;
	background-position: top;
}

#site-header img {
    display: none;
}

.site-header {
    box-shadow: 0px 0px 4px 0px #000 !important;
    background-color: #181818 !important;
}

#secondary {
    box-shadow: 0px 0px 4px 0px #000 !important;
    background-color: #202020;
}

.secondary-navigation li:hover > a,
	.secondary-navigation li.focus > a {
		background-color: #5A6164;
		color: #fff;
	}
}
