css:
@-moz-document domain("trello.com") {

.list-card-labels .card-label {
    float: none;
    display: block;
    width: auto !important;;
    height:initial !important;
    padding: 0px 4px !important;
    margin-right: 0px !important;
    margin-left: 0;
    border-radius: 0;
    
    line-height: initial !important;
    font-size: 12px !important;
    text-transform: uppercase;
    text-shadow: none;
    text-shadow: 1px 1px 0px rgba(0,0,0,.10);
    text-align: center;
  }





/* Create Black Parts */
.list {
	background: rgba(0, 0, 0, 1) !important;
}
.list-card {
    background-color: #080808;
    border-bottom-color: #000;
}

.list-card.active-card {
    background-color: #282828;
    border-bottom-color: #242424;
}

.list-card a {
    color: #CCC;
}

.list-card:hover a,
.list-card.active-card a {
    color: #eee;
}

.list-card-operation {
    background-color: #000 !important;
    color: #FFF !important;
}

.list-card.is-stickered .list-card-details {
    background-color: rgba(25, 25, 25, 0.7);
}

.list-card.active-card.is-stickered .list-card-details {
    background-color: rgba(40, 40, 40, 0.7);
}

body {
    background-color: #000000 !important;
}
.board-drawer-content {
    background-color: transparent !important;
}
.board-drawer-content .quiet,
.board-drawer-content .quiet a,
.boards-page-board-section-header-name,
.agile_arrow_title {
    color: #EDEDED;
}
.boards-drawer {
    background-color: #000000 !important;
}
.boards-drawer input {
    background-color: white;
}
.boards-drawer-header {
    background-color: #272727 !important;
    color: #f3f3f3 !important;
}

::-webkit-scrollbar {
	background: transparent !important;
	height: 12px !important;
	width: 12px !important;
}
::-webkit-scrollbar-button {
	display: none !important;
}
::-webkit-scrollbar-track {
	background: transparent !important;
}
::-webkit-scrollbar-track:horizontal {
	margin-left: 4px !important;
	margin-right: 4px !important;
}
::-webkit-scrollbar-track:vertical {
	margin-top: 4px !important;
	margin-bottom: 4px !important;
}
::-webkit-scrollbar-track-piece {
	background: #050505 !important;
	border-radius: 0px !important;
}
::-webkit-scrollbar-thumb {
	background: #222222 !important;
	border-radius: 16px;
}
::-webkit-scrollbar-thumb:hover {
	box-shadow: inset 0 0 6px #777;
}



/* Custom Mouse */ 
body, a:hover {cursor: url(http://cur.cursors-4u.net/cursors/cur-5/cur425.cur), progress !important;}



/* Remove Logo at the Top */
span.header-logo-default {
	display: none;
    background: none;
}



/* Make Cards Black */
.list,
div.window {
    background: #292929;
    color: white;
}



/* Change Label Colors */
.card-label-red {
	background-color: rgba(255, 0, 0, 1) !important;
}
.card-label-blue {
	background-color:  rgba(0, 79, 97, 1) !important;
}
.card-label-orange {
	background-color: rgba(190, 190, 0, 1) !important;
}
.card-label-yellow {
	background-color: rgba(255, 255, 255, .25) !important;
}
.card-label-lime {
	background-color: rgba(255, 255, 255, .25) !important;
    border-bottom: 1px solid #ffffff;
}
.card-label-green {
	background-color:  rgba(0, 0, 0, 1) !important;
}
.card-label-black {
	background-color:  rgba(0, 0, 0, 0) !important;
    border-bottom: 1px solid #ffffff;
}
.card-label-pink {
	background-color:  rgba(157, 68, 142, 1) !important;
}








}
