css:
@-moz-document domain("lapresse.ca"), domain("www.ledevoir.com") {
body, div, li, ul, p, span,
#footer .boxCopyright li a{
    background-color: #1c1c1c !important;
    color: #acacac !important;
}

header{
    z-index: 100 !important;
}

.shader, .degrade {
    z-index: -100 !important;
}

#entete .boxChannels ul li a,
#wrapper .filariane ul li a, 
#footer .liens li a,
#footer .liensTop ul li a,
a {
    background-color: #1c1c1c !important;
    color: #6c6cff !important;
}

h1 {
    background-color: #1c1c1c !important;
    color: #acacac !important;
    border-left: 5px solid #2c2c2c !important;
    border-bottom: 4px solid #4c4c4c !important;
    z-index: 10 !important;
}

h2, h3, h4, h6 a {
    background-color: #1c1c1c !important;
    color: #acacac !important;
    z-index: 10 !important;
}



.nouvellesTrio .infoArticle {
    position: absolute;
    bottom: 10000px;
    z-index: 10;
    overflow: hidden;
}

#entete .boxLogo a
{
    display: block;
    height: 0;
    padding: 0;
    overflow: hidden;
}
}
