css:
@-moz-document domain("reddit.com") {
.res-nightmode div#sr-header-area {
	color: #606060 !important;
	background: #2d2d2d !important;
    border-bottom: 1px solid #555 !important;
}

.res-nightmode div#header {
    background: #3d3d3d !important;
}

.res-nightmode .sr-bar a {
    color: #979797 !important;
}

#RESShortcutsSort, #RESShortcutsRight, #RESShortcutsLeft, #RESShortcutsAdd, #RESShortcutsTrash, #RESShortcutsEditContainer {
    display: none !important;
}

.res-nightmode div#header-bottom-left {
    background: #3d3d3d !important;
}

.res-nightmode body.with-listing-chooser div#header {
    background: #3d3d3d !important;
}

::-webkit-scrollbar {
    height: 12px !important;
    width: 12px !important;
}

::-webkit-scrollbar-track {
    background: transparent !important;
    -webkit-border-radius: 6px !important;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.4); 

}

::-webkit-scrollbar-thumb {
    background: #bbb !important;
    -webkit-border-radius: 6px !important;
    border: 1px solid !important;
    border-color: #999 !important;
    cursor: pointer !important; /*Currently not working (as of 15h of Nov 2016)*/

}

.res-nightmode ::-webkit-scrollbar-thumb {
    background: #666 !important;
    -webkit-border-radius: 6px !important;
    border: 1px solid !important;
    border-color: #333 !important;
    cursor: pointer !important; /*Currently not working (as of 15h of Nov 2016)*/

}


/*Prevents white corners (on just some browsers?)*/
::-webkit-scrollbar-corner {
    background: rgba(0,0,0,0.5);
}


::-webkit-scrollbar-button {
    display: none !important;
}
}
