css:
@-moz-document domain(eztv.ag) {
	* {
		font-family: "Open Sans", Verdana;
	}
	
	body {
		background-color: #666;
		text-align: center;
		color: #fff;
		padding-bottom: 30px;
	}
	
	#header_holder {
		width: 950px;
		margin: 0 auto;
	}
	
	
	/* hide warning message */
	
	table:nth-of-type(2) {
		display: none;
	}
	
	
	/* main menu */
	
	#site_menu {
		position: absolute;
		top: 0; left: 0;
		width: 100%;
		height: 40px;
		background-color: #333;
		border: none;
		z-index: 999;
	}
	
	#site_menu > * {
		padding: 10px;
	}
	
	
	/* website title */
	
	#header_holder > div:first-child {
		max-width: 250px !important;
		margin-top: 70px;
		float: left;
		position: absolute !important;
		top: -1px;
	}
	
	#header_logo {
		background: none;
		width: auto;
		height: 60px;
	}
	
	#header_link::before {
		content: "EZTV";
	}
	
	#header_link {
		display: block;
		width: 100%;
		text-align: left;
		float: left;
		font-size: 100px;
		font-weight: bold;
		color: #fff;
		
	}
	
	#header_link:hover {
		text-decoration: none;
		color: #000;
	}
	
	#header_link_holder {
		display: none;
	}
	
	
	#header_holder > div + div,
	#header_holder > div + div + span,
	#header_holder > div + div + span +br {
		display: none;
	}
		
	
	/* tables */
	
	.forum_header_border {
		border: none;
	}
	
	.forum_header {
		background: #333;
		border: none;
	}
	
	.forum_header_border_normal tr {
		background: #aaa;
	}
	
	.forum_header_noborder tr:nth-child(odd),
	.forum_header_border tr:nth-child(odd) {
		background-color: #a0a0a0;
	}
	.forum_header_noborder tr:nth-child(even),
	.forum_header_border tr:nth-child(even) {
		background-color: #aaaaaa;
	}
	
	.forum_thread_post,
	.forum_thread_post_end {
		border: none;
	}
	
	.section_header_column {
		background: none;
	}
	
	.forum_thread_header,
	.forum_thread_header_end,
	.section_post_header {
		background-image: none;
		background-color: #333;
		border: none;
		color: #ccc;
	}
	
	.forum_header {
		background: #333;
		border: none;
	}
	
	.forum_header_border_normal tr {
		background: #aaa;
	}
	
	
	/* a links */
	
	a.epinfo, span.epinfo {
		color: #3053bb;
	}
	
	
	/* search bar */
	
	.forum_header_border:first-of-type {
		width: 950px;
		border: none;
		margin-top: 40px;
		top: -1px !important;
		height: 75px;
		position: relative !important;
		background: none;
	}
	
		.forum_header_border:first-of-type tr,
		.forum_header_border:first-of-type td {
			background: none;
		}
		
		.tv-show-search-select, .select2, #select2-q2-ba-container {
			width: 200px !important;
		}
		
		.select2-results { 
			text-align: left;
			color: #000;
		}
		
		form#searchsearch_submit {
			float: right;
		}
}
