css:
@-moz-document domain(wikipedia.org) {


    body, #content {
        background: url(http://i.imgur.com/anSVcTO.png);
    }
    
    
    #content {
        width:45em !important; /* 45*width of W. hopefully around width of 80 regular random chars */
    }
    
    html, body, #bodyContent {
        font-family: ubuntu, "Ubuntu Light", Tahoma, Open Sans, sans-serif !important;
        font-weight: 300 !important;
    }
    #bodyContent {
        font-size:14.5pt !important;
    }
    b {
        font-weight: 300 !important;
        color: black !important;
    }

    .firstHeading {
        font: 30pt "Palatino URW", palatino !important;;
    }


    p {
        margin: 0 0 1em 0;
        line-height: 1.55em !important;;
        color: #444 !important;
    }
    
    a, p sup a {
        /*color: cadetblue !important;*/
        text-decoration: none !important;
    }
    p a {
        color: #336 !important;
        text-decoration: underline dotted !important; 
        text-decoration-color: cornflowerblue !important;
    }
    a:visited {
        color: #336 !important;
        text-decoration-color: mediumorchid !important;
    }
    a:hover {
        color: cornflowerblue !important;
        text-decoration-color: #333 !important;
    }
    a:visited:hover {
        color: mediumorchid !important;
        text-decoration-color: #333 !important;
    }
    
    
        
    
    .MathJax {
        color: #666;
    }

    #siteSub {
        color: beige;
    }

    /* prevents sub/superscripts from throwing off line spacing */
    sub, sup {
        font-size: 75%;
        line-height: 0;
        position: relative;
        vertical-align: baseline;
    }
        sup {top: -0.33em;}
        sub {bottom: -0.33em;}

     h1, h2, h3, h4, h5, h6 {
        border: none !important;
        font-family: Open Sans, Droid Serif, PT Serif, Georgia, Helvetica, sans-serif !important;
        margin: 0.5em 0;
        color: #444;
    }
    h2 {
        font-size: 18pt;
    }
    h3 {
        font-size: 17pt;
        font-weight: 400;
    }
    h4 {
        font-size: 14px;
        font-weight: 400;
    }
    h5 {
        font-size: 1em;
        font-weight: 400;
    } 



}
