css:
@-moz-document domain("st.x.sankuai.com") {
/*一级导航_展开_文字*/
.cust-navbar.extend .app-name {
    margin-left: 48px;
    margin-right: 35px;
    line-height: 50px;
    font-size: 16px;
}

/*一级导航_展开_主图标*/
.cust-navbar.extend .app-icon .icon-app {
    width: 16px;
    font-size: 18px;
}

/*一级导航_展开_副图标*/
.cust-navbar.extend .app-icon .icon-app {
    width: 18px;
    font-size: 18px;
}

/*一级导航_行高*/
.cust-app-item {
    position: relative;
    height: 50px;
    line-height: 50px;
    text-align: center;
    cursor: pointer;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    color: hsla(0,0%,100%,.5);
}

/*一级导航_展开_主图标行高*/
.cust-navbar.extend .cust-app-item .dxicon {
    vertical-align: initial;
    line-height: 50px;
}

/*一级导航_展开_副图标行高*/
.cust-navbar.extend .myapp-container .cust-app-item .app-icon {
    padding-top: 16px;
}

/*一级导航_展开_应用滚动区域高度*/
.cust-navbar.extend .myapp-container {
    top: 447px;
}

/*一级导航_展开_红点提醒*/
.cust-navbar.extend .cust-app-item .comp-badge {
    position: absolute;
    left: auto;
    right: 17px;
    top: 16px;
}

/*一级导航_收起_应用滚动区域高度*/
.myapp-container {
    position: absolute;
    bottom: 111px;
    top: 381px;
    overflow: hidden;
    width: 100%;
}

/*我的文件_一级导航_行高*/
.yp-menu-item {
    height: 46px;
}

/*我的文件_一级导航_图标*/
.yp-menu-item .menu-item-icon {
    display: inline-block;
    width: 16px;
    height: 16px;
    vertical-align: middle;
    margin-left: 20px;
}

/*我的文件_一级导航_文字*/
.yp-menu-item .menu-item-title {
    margin-left: 10px;
    font-size: 14px;
    line-height: 20px;
    display: inline-block;
    vertical-align: middle;
}

/*代办_一级导航_行高*/
.nav-item {
    width: 100%;
    height: 46px;
}

/*代办_一级导航_图标*/
.nav-item .nav-item-icon i {
    font-size: 16px;
}

/*代办_一级导航_文字*/
.nav-item .nav-item-count, .nav-item .nav-item-icon, .nav-item .nav-item-title {
    display: inline-block;
    height: inherit;
    line-height: 46px;
    text-align: center;
    position: relative;
    right: 16px;
}
.nav-item .nav-item-title {
    font-size: 14px;
    letter-spacing: 0;
    width: 20%;
    color: #333;
}
}
