css:
@-moz-document domain("myscore.com.ua") {
#header{
    margin-top: -145px;
	transition: 1s;
}
.dw-icon, span.racard, .rhcard, #box-over-content-a, #box-over-content-b, #box-over-content-c, div#lsadvert-zid-1855, #header h1, #lang-box-wrapper, li.banner.last-item-banner, .scrolling-banner-top-stop, .scrolling-banner-wrap, .adsenvelope, #bottom-box-fb-mobile-apps, #e-content, .scrolling-mobile-version-wrap, .partner, .mbox, #timezone, .ban_envelope, ul.menu.country-list.my-teams, ul.menu.country-list.my-leagues, .mbox0px.l-brd, div#lsid-content, .tournament-name, div#social-button {
    display: none !important;
}
div#control-panel-right {
    float: right;
    right: 15px;
    position: fixed;
    top: 0px;
}
div#control-panel-bg {
    margin-top: -40px;
}
div.team-header, div.tournament-header {
    height: 7px;
	border-radius: 10px 10px 0px 0px;
}
div#mt {
    border: 0 !important;
    position: relative;
    margin-top: -30px;
}
ul.menu.selected-country-list {
    position: absolute;
    left: 820px;
    top: 132px;
    width: 155px;
    border: 1px solid #777;
    box-shadow: 4px 4px 0px #CCC;
}
.tournament-logo {
    position: absolute;
    left: 705px;
    top: 5px;
    box-shadow: 0px 0px 7px #ddd;
}
h2.tournament {
    position: absolute;
    z-index: 999999;
	width: 650px;
    text-align: center;
}
ul.menu.country-list {
    box-shadow: -4px 5px 0px #CCC;
	border: 1px solid #777;
	margin-left: 4px;
}



