@-moz-document url("http://www.myscore.com.ua/") {
ul.menu.country-list {
    margin-top: -20px;
}
#rc-top:before {
    color: #A00;
    content: "MyScore Style v1.0";
	position:absolute;
	margin-top: 22px;
	margin-left: 22px;
	font-weight: 700;
	opacity: 0.2;
}
#rc-top:after {
    content: url(http://ipic.su/img/img7/fs/150.1483879989.png);
    opacity: 0.1;
}
}
