css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.arretsurimages.net")
{

  /*
    THEME NOIR
  */

  body
  {
    background-image: url(data:image/png;base64,) !important;
  }

  #col_main > *,
  .contenu-html p,
  #phorum p,
  #phorum div.generic,
  #phorum .titre a,
  #phorum .information,
  #phorum #description,
  #phorum div.message, #phorum div.message h2,
  #phorum #post textarea,
  h1,  h1 *,  h1 a,
  h2,  h2 *,  h2 a,
  h3,  h3 *,  h3 a,
  h4,  h4 *,  h4 a,
  h5,  h5 *,  h5 a,
  .typo-droite-titre,
  .contenu-dup-3 h3 a,
  .ui-widget-content a,
  .ui-widget-content,
  .text-XL-abo, .text-XL-abo-blue, .text-M-abo, .text-S-abo
  {
    color: #aaa;
  }

  #headercontainer,
  #pagecontainer,
  #footercontainer,
  #phorum table tr,
  #phorum > div.nav
  {
    border: 0 !important;
  }

  #headercontainer,
  #pagecontainer,
  #footercontainer,
  #phorum table tr,
  #phorum .index-tool,
  #phorum #post-form,
  #phorum > div.message,
  #phorum #description
  {
    background-color: rgba(0, 0, 0, 0.3);
  }

  #phorum div.message div.header-first-post,
  #phorum div.message div.header-first-elus,
  #footercontainer
  {
    border: 0 !important;
  }

  #col_right,
  #col_main > *, #col_main > p > a,
  #col_right > *,
  #col_left > *,
  #col_center > *,
  #bloc_une > *,
  #mycarousel > *,
  #mycarousel ul.es-slides *,
  #header > *,
  #nav,
  #nuage-tags,
  .filet-blanc,
  .bloc-contenu-chro-7,
  .text-abo,
  .bloc-chroniques > div,
  .bloc-chro-float > div,
  div.ui-accordion > div, div.ui-accordion > h3,
  #phorum thead.entete-list a,
  #phorum div.header,
  #phorum div.colleft,
  #phorum div.message-body-elu,
  #phorum div.pas-elu,
  #phorum div.message-body
  {
    background: transparent !important;
  }

  #cboxOverlay
  {
    background-color: black;
  }

  .bloc-connexion
  {
    background-color: darkgrey;
  }

  #mycarousel,
  #phorum div.message,
  #phorum #post h1,
  #phorum #post-form,
  .emission-home,
  .redaction-mea,
  .chronique-mea,
  .vitedit-mea,
  .bloc-contenu-12, .bloc-contenu-12-chro,
  .bloc-vite-contenu-3,
  .bloc-contenu-14,
  .bloc-dossier-2,
  .lnk-tout-content,
  .lnk-tout-forums,
  .lnk-tout-dossiers,
  .bloc-dup-1,
  .lnk-tout-forums-droite,
  .bloc-contenu-8,
  .bloc-dossier-6,
  .bloc-bande-contenu,
  .bloc-contenu-5, .bloc-contenu-5-chro,
  .bloc-contenu-9,
  .bloc-contenu-10,
  .bloc-chroniqueur-2,
  .bloc-contenu-6,
  .lnk-toutes-emissions-home,
  .lnk-tout-chro,
  .lnk-tous-vite,
  .lnk-plus-de-vitedit,
  .lnk-tous-gratuits,
  .bloc-contenu-chro-7,
  .bloc-chroniqueur-1,
  .bloc-contenu-4,
  .bloc-vite-gratuit-1,
  .bloc-chroniques > div,
  div.ui-accordion > div, div.ui-accordion > h3
  {
    border-top-color: #333 !important;
    border-bottom-color: #333 !important;
    border-right-color: #333 !important;
  }

  #col_main #slider_essentiel,
  #col_right form,
  #vite-titre,
  #dossier-bandeau,
  #nuage-tags,
  #texte-presentation,
  #phorum #top,
  #phorum table td,
  #phorum .index-tool,
  .bloc-contenu-3,
  .bloc-dossier-4,
  .bloc-emi,
  .rech-filtres-doss,
  .bloc-divers,
  .bloc-vite-contenu-1,
  .text-abo
  {
    border-color: #333 !important;
  }

  #nav-wrap
  {
    border-top-color: #888;
  }

  #headercontainer
  {
    box-shadow: 0 2px 2px #777;
  }

  a:hover,
  h2 a:hover,
  .boutons-sidebar a:hover .txt-sidebar,
  .boutons-sidebar a:hover .fa:before,
  .contenu-titre-home-alaune h2 a:hover,
  .lnk-deplier:hover, .lnk-replier:hover,
  #slider_essentiel li.es-after-slide:hover a,
  #phorum h1 a:hover,
  #phorum .sous-titre-menu-compte:hover
  {
    color: #ccc;
  }

  #slider_essentiel li.es-after-slide:hover img
  {
    border-color: #ccc;
  }

  .menu-emissions  > ul,
  .menu-chroniques > ul
  {
    background-color: rgba(0, 0, 0, 0.8) !important;
  }

  .zoom_legende
  {
    color: #0ae;
  }

  #phorum thead.entete-list,
  #phorum div.colright,
  #phorum div.message div.header-first-post,
  #phorum div.message div.header-first-elus,
  #phorum div.elu,
  #phorum #post h1,
  #phorum #post-body
  {
    background-color: rgba(60, 90, 0, .3);
  }

  #phorum #post textarea
  {
    background-color: #333;
  }

  #phorum #editor-tools
  {
    border-bottom-color: #4d894d;
  }

}
