css:
@-moz-document domain("www.youtube.com") {
/*==========================================

YouTube Watched Badge 0.9.3
BangDroid 2016

==========================================*/
.resume-playback-progress-bar {
 width: 100% !important;
 height: 100% !important;
 opacity: 1;
 background: no-repeat url('data:image/png;base64,');
background-size: 100% 100%;
transition: height 0.1s ease;
}
.resume-playback-background {
 display: none;   
}
.watched-badge {
 display: none;
}
/*hide on hover*/
.yt-lockup:hover .resume-playback-progress-bar,.yt-shelf-grid-item:hover .resume-playback-progress-bar,.pl-video:hover .resume-playback-progress-bar,.video-list-item:hover .resume-playback-progress-bar {
    height: 0px !important;
}
}
