.s_ipt_wr  {border: 0px solid #b6b6b6; height: 40px;}
.s_btn_wr:hover { border-bottom: 0px solid #2771d9; border-bottom-width: 0px; border-bottom-style: solid; border-bottom-color: rgb(39, 113, 217);}
