css:
@-moz-document url-prefix("https://www.connexus.com/content/chrome/online/lessonViewer.aspx"), url-prefix("https://www.connexus.com/content/render.aspx") {
disabled#ctl00_completionStatusIcon {
	background-image: none;
	background-color: whitesmoke;
	height: 28px;
	border-radius: 50%;
	border-color: #5C6BC0;
	border-width: 2px;
}

#resizeBar {
	background-color: #7E57C2;
	background-image: none;
}

#resizeBar span {
	visibility: hidden;
}

#bodyWrapper img {
	background-color: lightgray !important;
	border-radius: 10px !important;
}

#chromeLessonTree a:link {
	color: #212121 !important;
	text-decoration: none !important;
}

#lessonContentIFrame {
	border: 0px !important;
}

#lessonDocumentType {
	margin: 0px !important;
	size: 0px !important;
	visibility: hidden !important;
}

#lmuBorderBottomLeft {
	opacity: 0 !important;
}

#lmuBorderBottomRight {
	opacity: 0 !important;
}

#lmuBorderLeft {
	opacity: 0 !important;
}

#lmuBorderRight {
	opacity: 0 !important;
}

#lmuBorderTop, #lmuBorderBottom {
	background-image: none !important;
	height: 3px !important;
	border-radius: 3px !important;
	width: 60% !important;
	margin: 0 auto !important;
}

#lmuBorderBottom {
	margin-bottom: 3px !important;
}

#lmuBorderTop.docTypeGettingStarted, #lmuBorderBottom.docTypeGettingStarted {
	background-color: #9CCC65 !important;
}

#lmuBorderTop.docTypeInstruction, #lmuBorderBottom.docTypeInstruction {
	background-color: #EC407A !important;
}

#lmuBorderTop.docTypeActivity {
	background-color: #FFCA28 !important;
}

#lmuBorderTop.docTypeReview {
	background-color: #29B6F6 !important;
}

#lmuBorderTop.docTypeAssesment {
	background-color: #7E57C2 !important;
}

#lmuBorderTopLeft {
	opacity: 0 !important;
}

#lmuBorderTopRight {
	opacity: 0 !important;
}

#loadImageDetails {
	text-shadow: 0px !important;
}

.assessmentHeader {
	color: #42A5F5 !important;
}

.assessmentQuestion em {
	color: #66BB6A !important;
}

.assessmentQuestionNumber {
	color: #4CAF50 !important;
}

.assessmentStartMessageWrapper {
	color: #CCCCCC !important;
}

.lessonTree div .rtMinus, .lessonTree div .rtPlus {
	background-color: #E0E0E0;
}

.lessonTree div .rtMinus {
	content: '-';
	background-color: red;
}

.lessonTreeNode {
	color: #3949AB !important;
}

.lmuPractice {
	background-color: #FFF59D !important;
	color: #212121 !important;
}

.lmuCaModification {
	background-color: #66BB6A !important;
	color: #212121 !important;
}

.lmuCaModification a:link {
	color: #1565C0 !important;
}

.lmuCourseTitle {
	color: #9C27B0 !important;
	left: 50% !important;
	transform: translate(-50%, 0) !important;
}

.lmuCourseTitle img {
	visibility: hidden !important;
}

.lmuExtension {
	background-color: #CE93D8 !important;
	color: #212121 !important;
}

.lmuExtension a:link {
	color: #1565C0 !important;
}

.lmuExtension img {
	visibility: hidden !important;
}

.lmuImagePadding {
	visibility: hidden !important;
}

.lmuPortfolioItem {
	background-color: #D4E157 !important;
	color: #212121 !important;
}

.lmuTip {
	background-color: #00838F !important;
}

.lmuTipText {
	color: #212121 !important;
}

.loadingContents {
	visibility: hidden !important;
}

.questionText {
	color: #CCCCCC !important;
}

.rtBot {
	background-color: #E0E0E0 !important;
}

.rtIn {
	background-color: #E0E0E0 !important;
	border: 0px !important;
}

.rtMid {
	background-color: #E0E0E0 !important;
}

.rtTemplate {
	background-color: #E0E0E0 !important;
	border: 0px !important;
}

.rtTop {
	background-color: #E0E0E0 !important;
}

.rtUL {
	background-color: #E0E0E0 !important;
}

.rwDialogText {
	color: #CCCCCC !important;
}

.rwWindowContent {
	background: #212121 !important;
}

.subheader {
	color: #9C27B0 !important;
	text-align: center !important;
	font-weight: 500;
}

.header {
	text-align: center !important;
	font-family: Calibri;
	font-weight: 800;
}

.wysiwygBody .contentText {
	color: #CCCCCC !important;
	font-family: Calibri !important;
	padding: 0 !important;
}

::-webkit-scrollbar {
	width: 15px !important;
}

::-webkit-scrollbar-button {
	background-color: #616161 !important;
}

::-webkit-scrollbar-corner {
	background-color: #424242 !important;
}

::-webkit-scrollbar-thumb {
	background-color: rgba(0, 0, 0, 0.2) !important;
}

::-webkit-scrollbar-track {
	background-color: #9E9E9E !important;
}

body a:link {
	color: #42A5F5 !important;
}

.lessonViewerContentContainer {
	top: 51px !important;
	bottom: 70px !important;
}

.lmuRightWindow, .lmuContent, p, .lesson, .lessonViewerParent {
	/*[[brightness]]*/
}

.lmuRightWindow, .wysiwygBody, .wysiwygBody P, .body, .lmuContent, .lessonViewerContentContainer, .lesson {
	font-family: Calibri !important;
	transition: background-color 300ms ease-in-out !important;
	transition: opacity 300ms ease-in-out !important;
}
}

@-moz-document url-prefix("https://www.connexus.com/extra/ThirdPartyProviders/Pearson/") {
/* Pearson HTML Textbooks */

:root {
	--mainColor: #D9D7D3;
	--secondaryColor: #374395;
	--lightTextColor: whitesmoke;
	--darkTextColor: #252525;
}

html, body, #page {
	background: var(--mainColor) !important;
	overflow-x: hidden;
}

#page {
	width: 100%;
	border: none;
}

#top_area {
	background: var(--secondaryColor);
	padding: 10px;
	height: 50px
}

#top_area::after {
	display: inline-block;
	position: relative;
	content: 'Pearson';
	vertical-align: middle;
	margin: auto;
	left: 30px;
	font-size: 35px;
	top: 50%;
	transform: translateY(-53%);
	font-weight: bold;
	font-family: Times, Garamond;
	color: whitesmoke;
}

#anchor_area {
width: auto;
}

#anchor_links {
	position: relative;
	top: 50%;
	transform: translateY(-38%);
	font-size: 12px;
	width: 400px;
}

#top_nav, #bottom_nav {
	background: #eeeeee;
	border: none;
}

#breadcrumb_bar, #breadcrumb_left, #breadcrumb_right {
	background: var(--backgroundColor);
}

#breadcrumb_bar_text {
	text-align: center;
}

#wrap {
	width: 100%;
}

#content_wrap {
	float: none;
	width: 80%;
	margin: 40px 10% 0 10%;
	margin-top: 40px;
}

#page_content {
	font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif !important;
	font-size: 19px !important;
	line-height: 26px !important;
	letter-spacing: -0.3px !important;
	text-align: center;
	color: var(--darkTextColor);
}

#toc {
	transition: all 300ms ease-in-out;
	position: absolute;
	left: 0;
	transform: translateX(-200px);
	height: 78%;
	overflow-y: hidden;
	top: 135px;
}

#toc:hover {
	transform: translateX(0);
}

#toc_top_bar {
	background: var(--secondaryColor)
}

#toc_top_bar h1 {
	color: var(--lightTextColor);
}

#toc_content {
	position: relative;
	overflow-y: auto;
	height: 100%;
}

#copyright, #copyright a {
	color: var(--darkTextColor) !important;
	border: none;
	font-size: 11px;
}

::-webkit-scrollbar {
	width: 10px !important;
	height: 10px !important;
}

::-webkit-scrollbar-button {
	background-color: #616161 !important;
}

::-webkit-scrollbar-corner {
	background-color: #424242 !important;
}

::-webkit-scrollbar-thumb {
	background-color: rgba(0, 0, 0, 0.2) !important;
}

::-webkit-scrollbar-track {
	background-color: var(--backgroundColor) !important;
}
}
