css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ipon.hu") {
  #ipon{
    background-color: #8c8c8c !important; 
  }
  
  #header {
    background-color: #333333;
    
  }
  
  #start div.newsItem i {
    color:black;
  }
  
  #header .icons ul li a {
    color: #81ba04;
  }
  
  .commentBox .welcome {
    color: #66cc00;
  }
  
  .item_view0 {
    background-color: #bfbfbf;
  }
  
  #left div.box ul {
    background-color: #8c8c8c;
  }
  
  #start div.newsItem .newsImage {
    border-radius: 3px;
  }
  
  h2.title {
    color:#333333;
  }
  
  #mainmenu li a i {
    color: #81ba04;
  }
  
  #start div.newsItem span {
    color: #00cc44;
  }
  
  #start #top-buttons {
    background-color: #8c8c8c;
  }
  
  #start #top-buttons a {
    background-color: lightgrey;
    border-color: #737373;
    border-radius: 20px;
  }
  
  #start #top-buttons a:hover {
    background-color: #b3b3b3;
  }
  
  #groups-main-container {
    background-color: lightgrey;
    border-radius: 25px;
  }
  
  #groups li .submenu-container {
    background-color: lightgrey;
  }
  
  #groups .submenu .col1 li .subsub {
    background-color: lightgrey;
  }
  
  #groups .submenu .col1 > li > a {
    background-color: lightgrey;
  }
  
  #groups .submenu .col1 > li {
    background-color: lightgrey;
  }
  
  #comments a, #reviews a {
    color: #81ba04;
  }
  
  #article {
    border-radius:5px;
  }
  
  #footer {
    border-top:0px;
    background-color: #8c8c8c;
  }
  
  .comment-container .bottom .button-green {
    border: 1px solid grey;
    background: linear-gradient(to bottom, rgb(98, 131, 26) 0%,rgba(145,196,32,1) 100%);
  }
  
  .forumBox .new_topic .button-green, .comments-footer .new_topic .button-green {
    border: 1px solid grey;
    background: linear-gradient(to bottom, rgb(98, 131, 26) 0%,rgba(145,196,32,1) 100%);
  }
  
  .comment-container .left .rank-container .rank-title {
    color: #81ba04;
  }
  
  .comment-container .left .message a {
    color: #81ba04;    
  }
  
  .item_view0 .middle .to-product {
    color: #81ba04;
  }
  
  div#articlePreview img.noborder {
    border-radius: 3px;
  }   
  
  #comments li .clearer, #reviews li .clearfix{
    border-bottom:none;
  }
  
  .social-container{
    border-bottom: none;
  }
  
  #recent-links div.recent-box{
    border: none;
  }
  
  #recent-links div.recent-line{
    border-bottom: none;
  }
  
 .forumTable tr td .descr
  {
    color: #DDDDDD;
  }
  
  .forumTable tr td .last-comment
  {
    color: #DDDDDD;
  }
  
  .comments-footer .bottom
  {
    border-bottom: none;
  }
  
  #footer-logo
  {
    border-top-left-radius:25px;
    border-top-right-radius:25px;
  }
}
