      }

      article .image_part .addon-element {
          display: block;
          -webkit-box-sizing: content-box;
          -moz-box-sizing: content-box;
          box-sizing: content-box;
          float: none;
          z-index: auto;
          width: auto !important;
          height: auto;
          position: static;
          cursor: default;
          opacity: 1;
          margin: 0;
          padding: 0;
          overflow: hidden;

          -webkit-border-radius: 0 8px 8px 0 !important;
          border-radius: 0 8px 8px 0 !important;

          text-align: center;
          -o-text-overflow: ellipsis;
          text-overflow: ellipsis;
          border-left: 0px; 
          
          -webkit-box-shadow: 1px 1px 2px 0 #03252B !important;
          -moz-box-shadow: 1px 1px 2px 0 #03252B !important;
          box-shadow: 1px 1px 2px 0 #03252B !important;
          

