css:
@-moz-document url-prefix("https://volafile.org/")
{

@font-face {
    font-family: 'Merriweather';
    font-style: normal;
    font-weight: 400;
    src: local('Merriweather'), local('Merriweather-Regular'), url(https://fonts.gstatic.com/s/merriweather/v13/RFda8w1V0eDZheqfcyQ4EOgdm0LZdjqr5-oayXSOefg.woff2) format('woff2');
}
@font-face {
    font-family: 'Merriweather Italic';
    font-style: italic;
    font-weight: 400;
    src: local('Merriweather Italic'), local('Merriweather-Italic'), url(https://fonts.gstatic.com/s/merriweather/v13/So5lHxHT37p2SS4-t60SlBMBlNeli-0RcAdOFWWLdxQ.woff2) format('woff2');
}
.username {
    font-family: 'Merriweather', sans-serif !important;
}
.chat_message {
    margin-left: .4em;
    word-wrap: break-word;
    overflow: hidden;
    padding: 2px 1em 2px .5em;
    /*padding: 2px 1em 2px .5em;*/
}
.chat_message .username {
    float: none;
}
.chat_message.highlight {
    font-family: 'Merriweather Italic', sans-serif !important;
}
#chat_messages_frame {
    font-family: 'Merriweather', sans-serif !important;
}
#chat_name_container {
    background-color: #2d2e2b;
}
#files_header {
    background-color: transparent;
}
#chat_messages,
.header_row,
#files_frame,
html {
    background-color: #272822 !important;
}
.ui_frame_body_bar,
.dropdown_list {
    border-color: #2d2e2b !important;
}
input[type="text"],
input[type="password"],
textarea {
    background-color: #484848;
}
input[type="submit"] {
    background-color: rgba(0, 255, 71, 0.88) !important;
}
input[type="submit"]:hover {
    background-color: rgba(0, 255, 102, 1) !important;
    color: #282828 !important;
    text-shadow: 0em 0em 0.3em rgba(255, 255, 255, 0.85);
}
.file_tag {
border-radius:2px;
}
.file_tag.tag_key_user {
    background-color: rgba(53, 137, 86, 0.8) !important;
    border-radius:2px;
}
#chat_messages_frame {
    border: none !important;
}
#chat_input {
    background-color: #272822 !important;
    border-color: #2d2e2b !important;
    color: #7e7e7e !important;
}
#chat_notifier {
    margin-top: -.1em;
}
.icon-arrow-down:before {
    content: "\2B9F";
}
.icon-star::before {
    content: "\2721";
    font-size: 1.2em;
    line-height: .5em;
}
#chat_input:focus {
    color: #c7c7c7 !important;
}
#chat_hbar_container {
    border-left-color: #2d2e2b !important;
    border-top-color: #2d2e2b !important;
    box-shadow: 0 -3px 5px 0 #272822;
}
#chat_input_container {
    border-left-color: #272822 !important;
}
#room_name_container {
    box-shadow: 0 3px 5px 0 #2d2e2b;
}
#room_search {
    background-color: #2d2e2b;
}
#file_notifier {
    background-color: #414141;
    border-bottom: #414141;
}
#file_notifier:hover {
    background-color: #484848
}
.chat_message.highlight {
    background-color: rgb(74, 74, 74) !important;
}
.file_control_icon.filetype {
    padding-right: .25em;
    padding-left: .35em;
}
#toggles .toggle:hover {
    background-color: #414141;
}
.icon-image::before {
    color: #57b1f4;
}
.icon-film::before {
    color: #ff688e;
}
.icon-music::before {
    color: #21ec57;
}
.icon-file::before {
    color: #FFFFFF;
}
.icon-archive::before {
    color: #f1f24e;
}
.icon-book::before {
    color: #ffe05f;
}
#chat_hbar {
    background-color: #2d2e2b !important;
    border-color: #2d2e2b !important;
}
#chat_name {
    background-color: #2d2e2b !important;
    border-color: #2d2e2b !important;
}
#room_name_container {
    background-color: #2d2e2b !important;
    border: none !important;
}
#chat_frame,
.header_row,
.header_row_element,
#files_frame {
    border: none !important;
}
#search_input,
input[type="number"],
#filter_input {
    background-color: #2d2e2b !important;
    color: #C7C7C7 !important;
}
*,
*:after {
    border-color: #272822 !important;
}

/* Top bar filters enabled */
#toggles .enabled .toggle_icon,
#radio_playback_controls .enabled .radio_icon,
.music_playing {
    color: #008aff !important;
}

/* Top bar filters disabled */
#toggles .toggle_icon,
#radio_playback_controls .radio_icon {
    color: #888888 !important;
}
#upload_container > .button {
    background-color: #00FF47;
}
#upload_container > .button:hover {
    background-color: #00FF66;
}
#gallery_close,
#gallery_download {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
}
#filter_reminder {
    background-color: #484848 !important;
    border-bottom: #484848;
}
#filter_reminder:hover {
    background-color: #545454 !important;
}
.file_tag {
    background-color: #3b3a32 !important;
}
.chat_room_url,
.slider,
#rename_input {
    background-color: #484848 !important;
}
#rename_input {
    color: #dfdfdf !important;
}
td textarea,
td input {
    color: #dfdfdf !important;
}
.ui_frame {
    background-color: #272822 !important;
    border-color: #6e6e6e !important;
}
.ui_frame_table td {
    border-color: #484848 !important;
}
.ui_frame_table input,
.ui_frame_table textarea {
    background-color: #484848 !important;
}
#header_row1 .header_row_element:hover {
    background-color: #484848 !important;
}
.header_row_element:after {
    border-color: #2d2e2b !important;
}
#toggles .toggle:hover {
    border-color: #484848 !important;
}
#toggles .toggle:active {
    background: #605f5f;
}
.dropdown_item {
    background-color: #272822 !important;
}
.dropdown_item:hover {
    background-color: #484848 !important;
}
.file_uploading {
    background-color: #3a4040 !important;
}

/*
  .file_uploading {
    background-color: pink !important;
  }
  */

/*
  #files_frame {
    border-color: #2d2e2b !important;
  }
  */
.scroller-slider {
    background: #525354 !important;
}
.header_row_element_disabled {
    background-color: #2d2e2b !important;
    color: #484848 !important;
}
#show_search_ui:hover {
    background-color: #484848 !important;
}
#radio_control_bar {
    background-color: #272822 !important;
}
#radio_container {
    border-color: pink !important;
    background-color: #272822 !important;
}
#radio_current {
    background-color: #2d2e2b !important;
    /*border-color: #484848 !important;*/
    border: none !important;
}
#radio_current_time {
    border-color: #484848 !important;
}
#radio_progress {
    background-color: #272822 !important;
}
#radio_load_progress {
    background-color: #2d2e2b !important;
}
#radio_play_progress {
    background-color: #484848 !important;
}
.ui_frame_table select {
    background-color: #484848 !important;
}
.ui_frame_tooltip {
    background-color: rgba(39, 40, 34, 0.95);
    border-color: rgba(39, 40, 34, 0.95);
}
.ui_frame_tooltip.has_thumb .ui_frame_content {
    border-color: rgba(39, 40, 34, 0.95);
    border-width: 2px;
}
.chat_file {
    background-color: #2d2e2b !important;
    border-color: #484848 !important;
}
.chat_file:hover {
    background-color: #484848 !important;
}
.filelist_file {
    border-style: solid;
    border-color: rgba(0, 0, 0, 0);
}
.filelist_file:hover {
    border-left-color: #78797b;
    border-width: 2px;
    border-left-width: 2px;
    border-right-width: 2px;
    text-shadow: 0em 0em 0.3em rgba(255, 255, 255, 0.85);
}
.file_selected {
    background-color: #484848 !important;
}

/*.file_name:hover {
      color: #ffffff !important;
      text-shadow: 0em 0em 0.3em rgba(255, 255, 255, 0.85);
    } */
#chat_messages .chat_message .username {
    /* Unregistered user */
    color: rgb(72, 132, 236);
}
#chat_messages .chat_message.user .username {
    /* Registered user */
    color: rgb(0, 241, 96);
}
#chat_messages .chat_message.donator .username {
    /* Donators */
    color: rgb(0, 174, 255);
}
#chat_messages .chat_message.admin .username {
    /* admin members */
    color: rgb(249, 0, 60);
}
#chat_messages .chat_message.staff .username {
    /* Staff members */
    color: rgb(249, 0, 60);
}
#overlay {
    background-color: #2d2e2b !important;
}
#file_list.uneven .filelist_file:nth-child(2n+1) {
    /* First color. */
    background-color: #272822;
}
#file_list.even .filelist_file:nth-child(2n) {
    /* Second color. */
    background-color: #2d2e2b;
}
#file_list.even .filelist_file:nth-child(2n-1) {
    /* First color. */
    background-color: #272822;
}
#file_list.uneven .filelist_file:nth-child(2n+2) {
    /* Second color */
    background-color: #2d2e2b;
}
.highlight {
    background-color: #2d2e2b !important;
    /* border-left: 0.4em solid #fd971f !important; */
    border-left: 0.4em solid #75715e !important;
}
#radio_volume_bar {
    background-color: #2d2e2b !important;
}
#radio_volume_slider {
    background-color: #484848 !important;
}

/* MAIN MENU */
#main_learn_more,
.light.button {
    background-color: #2d2e2b !important;
    color: #fff !important;
}
#main_learn_more,
.light.button:hover {
    background-color: #414141 !important;
}
#top_navigation,
.top_nav_item {
    background-color: #2d2e2b !important;
    border-color: #484848 !important;
}
.main hr {
    border-color: #484848 !important;
}
#main_small_links {
    background-color: #2d2e2b !important;
}

/* USER TOP LIST */
th {
    background-color: #2d2e2b !important;
    border-color: #484848 !important;
}
tbody td {
    border-color: #484848 !important;
}
.file_uploading {
    background-color: transparent!important
}
.file_uploading:after,
.file_uploading:before {
    position: absolute;
    background-position: top left;
    background-attachment: fixed;
    background-size: 2em 2em;
    content: '';
    top: 0;
    left: -.35em;
    bottom: 0;
    z-index: -10;
    width: 100%
}
.file_queued {
    background-color: #484848 !important;
}
.file_uploading::before {
    padding-left: .35em;
    background-color: #484848;
    /*background-image: repeating-linear-gradient(-45deg, #484848, #484848 25%, #484848 25%, #484848 50%);*/
    background-image: repeating-linear-gradient(-45deg, #272822, #272822 25%, #272822 25%, #272822 50%);
}
.file_uploading:after {
    width: 0;
    background-color: #484848;
    background-image: repeating-linear-gradient(-45deg, #404040, #404040 25%, #6c6c6c 25%, #6c6c6c 50%);
    transition: 2s
}
.file_status::before {
    border: none !important;
    margin: 0 !important;
}

/* Shows the ETA for uploads all the time. */
.file_right_part .hover_show {
    display: inline !important;
}
.file_right_part .hover_show:after {
    content: ' - ';
}
.file_right_part:hover .hover_hide {
    display: inline !important;
}
.gallery_area_right,
.gallery_area_left {
    border: none!important;
}
#chat_messages a:link {
    color: white;
}
#chat_messages a:link:hover {
    color: #dddddd;
}
#chat_messages a:visited {
    color: #b6b6b6;
}
#chat_messages a:visited:hover {
    color: #dddddd;
}
#mobile_toggle_chat > .button {
    background-color: #505050;
}
}
