css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("knightmachine.pl") {


#header{
		padding:20px;
	}

table.tablesorter{
		font-size: 15px;
	}
	
table, th, td, #comments .avatar, #comments input, #comments textarea{
	border-color:#8D825E;
	}
 
	
tr, #comments li, #comments input[type="submit"], #comments input[type="reset"]{
	color:inherit; 
	background-color:#707570;
	}
tr:nth-child(even), #comments li:nth-child(even){
	color:inherit; 
	background-color:#A19C9C;
	}
	
table a, #comments a{
	background-color:inherit;
	}
	

#nick, #coins, #attack_time, #security, #ko, #acid, #defeat, #notMe{
	padding: 1px;
	margin: 1px 0px -2px;
	}
	
#security, #ko, #acid, #defeat, #notMe{
	margin-top: 7px;
	}
	
#ko {
	margin-left: 6px;
	}
	
#acid {
	margin-left: 15px;
	}
	
#defeat {
	margin-left: 25px;
	}
	
#notMe {
	margin-left: 29px;
	}
	
	
.busy{
	  background-color: rgba(225,5,5,.80);
	}
	
.free {
  background-color: rgba(6,155,6,.80);
  }
	
a{
	color:#483877;
	font-weight: bold;
	}

nav ul, nav ol{
	margin:0px;
	padding:0;
	list-style:none;
	}	

#mainav, #breadcrumb, .sidebar nav{
	line-height:15px;
	}

#mainav .drop::after{
	top:17px; 
	left:5px;
	}
	
#mainav li li a, #mainav li li .drop{
	display:block; 
	margin:0; 
	padding:10px 5px;
	}

#mainav li li .drop::after{
	top:15px;
	left:52px;
	}
	
#mainav li a{
	display:block; 
	padding:15px 0;
	}
	
input {
	margin-top: -8px;
	}
	
	tbody > tr > td > input {
		margin-top: 0px;
		padding-bottom: -15px;
	}
	
.cke_editable, .cke_editable_themed, .cke_contents_ltr, .cke_show_borders {
	background-color: #DFDFDF;
  }
}
