css:
@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("bbs.io-tech.fi") {

*
{
	transition:all .2s ease-in-out
}

.message .messageInfo
{
	min-height:145px!important
}

.Responsive .message .messageInfo
{
	border-left:1px solid #3d3d3d
}

@media (min-width: 480px) {
	.messageUserInfo
	{
		border-right:1px solid #3d3d3d
	}
}

.button
{
	background-color:#393939;
	color:#CCC;
	border-color:#444
}

.PageNav a:hover
{
	color:#FFF
}

a.PreviewTooltip:HOVER,a.crumb:HOVER
{
	color:#557cb9!important
}

.PageNav a:hover,.button:hover,.messageMeta a.item.control:HOVER,a.callToAction:HOVER,a.callToAction:HOVER *,span.itemPageNav a:HOVER
{
	background-color:#557cb9!important;
	color:#FFF
}

.PageNav a:FOCUS,.button:FOCUS,a.OverlayTrigger:FOCUS
{
	background-color:#264565!important;
	color:#FFF
}

.messageHeading,.PageNav a.currentPage,.XenBase .userBanner.bannerStaff,.button.primary,a.callToAction
{
	background-color:#46596F
}

.XenBase .userBanner.bannerStaff.wrapped span
{
	background-color:#41546A
}

html .messageHeading span a
{
	color:#FFF
}

.messageUserBlock a.username
{
	color:#557cb9
}

.avatar img
{
	filter:grayscale(25%)!important
}

.avatar img[src*="styles/zipped/xenforo/avatars/"]
{
	filter:opacity(25%)!important;
	border-radius:49px
}

.nodeList.sectionMain
{
	margin-left:0;
	padding:0;
	-moz-columns:2;
	-moz-column-gap:10px
}

ol#forums li.level_1:nth-child(4)
{
	margin-bottom:3000px!important
}

.messageText img
{
	transform:scale(1);
	transition:all .2s ease-in-out;
	max-height:250px
}

.messageText img:HOVER
{
	transform:scale(2);
	position:absolute;
	z-index:999
}

.sidebar .section:NTH-CHILD(2n+2),.xbScrollButtons,footer,.sidebar .section:NTH-child(5),#copyright,i.fa-rss
{
	display:none!important
}

.messageUserBlock div.avatarHolder
{
	padding:0!important
}

.messageUserBlock .avatar img
{
	float:left!important
}

.messageUserBlock h3.userText
{
	padding:0!important;
	width:100%!important
}

.messageUserBlock .extraUserInfo
{
	position:absolute!important;
	top:100px;
	width:153px!important
}

.XenBase .messageList .userBanner
{
	position:relative;
	top:46px;
	z-index:1
}

.userBanner.bannerStaff.wrapped strong
{
	position:relative;
	left:-50px
}

li.message.threadStarter .messageHeading
{
	background:#557cb9;
}
}
