css:
@-moz-document domain("insin.github.io"), domain("react-hn.appspot.com") {
body {
  background-color: #222;
}

.App__wrap {
  color: #fdfdfd;
  background-color: #333;
  font-family: Roboto, sans-serif;
  font-size: 18px;
  max-width: 900px;
}
.App__header {
  color: #dedede;
  background-color: #252525;
}

.App__homelink {
  color: #f2f2f2 !important;
}

.App__footer {
  margin-top: 1em;
  border-top: 1px solid #454545;
  color: #808080;
}

.Paginator a {
  color: dodgerblue;
}


.Item__title {
  color: #cecece;
}
.Item__title a {
  color: #fefefe;
    
  font-size: 1.25em;
}


.Item__title a:visited {
  color: #aeaeae;
}
.Item__meta {
  color: #808080;
}


.Item__meta em {
  background-color: #ffffde;
  color: #000;
}

.PollOption__score {
  color: #666;
}

.Comment--new > .Comment__content {
  background-color: #505060;
}

.Comment__text a {
  color: lightblue;
}
.Comment__meta {
	color: #a0a0a0;
}
.Comment__meta a {
  color: #b0b0b0;
}
}
