css:
@-moz-document domain("planetsuzy.org") {

    :root {
        --ff: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
        --bg: #222;
        --bg--lighter: #333;
        --border: 1px solid #666;
        --c: #fafafa;
        --c--link: dodgerblue;
    }

    body {
        background: var(--bg);
        font-family: var(--ff) !important;
    }

    * {
        font-family: inherit !important;
        /*     font-size: inherit !important; */
    }

    /* remove banner */
    body > div:first-of-type {
        display: none;
    }


    .page {
        background: var(--bg--lighter);
        color: var(--c);
    }

    .alt1, .alt1Active {
        background: var(--bg--lighter);
        color: var(--c);
    }

    .alt2, .alt2Active {
        background: var(--bg--lighter);
        color: var(--c);
    }

    .tborder {
        background: var(--bg--lighter);
        border: none;
    }

    a:hover, a:active, a:hover {
        color: var(--c-link);
    }

    a:visited {
        color: cadetblue;
    }
    a:link {
        color: cornflowerblue;
    }


    .vbmenu_control,
    .thead,
    .tfoot,
    .panel,
    .panelsurround {
        background: #444;
        color: #cecece;
        border: 1px solid #666;
        font-family: roboto, sans-serif;
    }

    fieldset {
        border: 1px solid #666;
    }

    .tcat {
        background: #444;
        color: lightskyblue;
        font-family: Roboto, sans-serif;
    }


    /* remove twitter follow link */
    a[href*="twitter.com"] {
        display: none;
    }


    [id*="edit"] thead,
    [id*="edit"] td,
    [id*="post"] thead,
    [id*="post"] td {
        border: var(--border) !important;
    }

    font[size] {
        font-size: inherit;
    }

    font[color] {
        color: var(--c);
    }


}
