css:
@-moz-document domain('qbt.localhost') {
  * {
      background-color: #2F2F2F;
      color: white;
  }
  th, li > a, li > a:hover {
      background-color: #2F2F2F !important;
      color: white !important;
  }
  div:not(.progressbar_dark) {
      background: #2f2f2f !important;
  }
  .tab-menu li,
  .tab-menu a {
      background-image: none !important;
  }
  .tab-menu li.selected a {
      color: #cfe !important;
  }
  th {
      background-color: #5F5F9F !important;
  }
}
